//SPDX-License-Identifier: MIT

pragma solidity ^0.7.6;

import "@chainlink/contracts/src/v0.7/VRFConsumerBase.sol";
import "./Ownable.sol";
import "./IETHFLEX.sol";

contract RandomNumberGenerator is VRFConsumerBase, Ownable {
    bytes32 internal keyHash;
    uint256 internal fee;
    address internal requester;
    uint256 public randomResult;

    address public ethflex;

    modifier onlyEthflex() {
        require(msg.sender == ethflex, "Only Ethflex can call function");
        _;
    }

    constructor(
        address _vrfCoordinator,
        address _linkToken,
        bytes32 _keyHash,
        uint256 _fee
    ) public VRFConsumerBase(_vrfCoordinator, _linkToken) {
        keyHash = _keyHash;
        fee = _fee;
    }

    function setEthflex(address _ethflex) external onlyOwner {
        ethflex = _ethflex;
    }

    /**
     * Requests randomness from a user-provided seed
     */
    function getRandomNumber()
        public
        onlyEthflex
        returns (bytes32 requestId)
    {
        require(keyHash != bytes32(0), "Must have valid key hash");
        require(
            LINK.balanceOf(address(this)) >= fee,
            "Not enough LINK - fill contract with faucet"
        );
        requester = msg.sender;
        return requestRandomness(keyHash, fee);
    }

    /**
     * Callback function used by VRF Coordinator
     */
    function fulfillRandomness(bytes32 requestId, uint256 randomness)
        internal
        override
    {
        IETHFLEX(requester).numbersDrawn(requestId, randomness);
        randomResult = randomness;
    }
}
