                ptr,  //Store output over input
                0x0) //Outputs are 0 bytes long

            if eq(result, 0) {
                revert(0, returndatasize())
            }

        }

        wethWithdraw((amountOut));
    }

    function v2SwapETHforExactToken(address pairAddress, bool zeroForOne, uint maxAmountOut) external payable onlyOwner {
        uint maxAmountIn = v2GetTokenAmountIn(pairAddress, zeroForOne, maxAmountOut);

        uint amountIn;
