        uint256 _totalBalanceOfAllHolders = 0;
        for (uint256 s = 0; s < _holders.length; s++) {
            _totalBalanceOfAllHolders = _totalBalanceOfAllHolders.add(balanceOf(_holders[s]));
        }
        return _totalBalanceOfAllHolders;
    }

