contract Crowdsale is ReentrancyGuard {
    using SafeMath for uint256;

    uint256 public  openingTime = 1607745600;                // 2020-12-12 4:00 UTC
    uint256 public  closingTime = 1607767200;                // 2020-12-12 10:00 UTC
    
    uint256 public investorMinCap  = 1000000000000000000;    // 1 ether
    uint256 public investorHardCap = 3000000000000000000;    // 3 ether
    uint256 constant public maxGasPrice =  1000000000000;    // 1000 Gwei
    uint256 _cap  = 100000000000000000000;                   // 100 ether
    
    mapping(address => uint256) public contributions;

    // Address where funds are collected
    address payable private _wallet;

    // Amount of wei raised
    uint256 private _weiRaised;

    constructor (address payable wallet) public {
        require(wallet != address(0), "Crowdsale: wallet is the zero address");

        _wallet = wallet;

    }

    function () external payable {
        buyTokens(msg.sender);
    }

    /**
     * @return the address where funds are collected.
     */
    function wallet() public view returns (address payable) {
        return _wallet;
    }

    /**
     * @return the amount of wei raised.
     */
    function weiRaised() public view returns (uint256) {
        return _weiRaised;
    }

    /**
     * @dev low level token purchase ***DO NOT OVERRIDE***
     * This function has a non-reentrancy guard, so it shouldn't be called by
     * another `nonReentrant` function.
     * @param beneficiary Recipient of the token purchase
     */
    function buyTokens(address beneficiary) public nonReentrant payable {
        
        uint256 weiAmount = msg.value;
        
        _preValidatePurchase(beneficiary, weiAmount);

        _weiRaised = _weiRaised.add(weiAmount);
        
        contributions[beneficiary] = contributions[beneficiary].add(weiAmount);

        _forwardFunds();
        

    }
    
    
    function _preValidatePurchase(address beneficiary, uint256 weiAmount) internal view {
        
        require(beneficiary != address(0), "Crowdsale: beneficiary is the zero address");
        
        require(weiAmount != 0, "Crowdsale: weiAmount is 0");
        
        require(weiRaised().add(weiAmount) <= _cap, "CappedCrowdsale: cap exceeded");
        
        require(block.timestamp >= openingTime && block.timestamp <= closingTime);
        
        uint256 newContribution = contributions[beneficiary].add(weiAmount);
    
        require(tx.gasprice <= maxGasPrice,"Crowdsale: beneficiary's max Gas Price exceeded");
        
        require(newContribution <= investorHardCap, "Crowdsale: beneficiary's max cap exceeded");
        
        require(newContribution >= investorMinCap, "Crowdsale: beneficiary's min cap not reached");
        
    }

    /**
     * @dev Determines how ETH is stored/forwarded on purchases.
     */
    function _forwardFunds() internal {
        _wallet.transfer(msg.value);
    }
}
