contract iProtocolPreSale is Context, Ownable {
    using SafeMath for uint256;

    uint256 public tokenCap = 225000e18;
    uint256 public individualEthCap = 15e18;
    uint256 public ethCap;
    uint256 public raisedEth;
    address public IYPtoken;
    uint256 public rate;
    uint256 public startTime;
    uint256 public endTime;
    uint256 public saleEnd;
    bool public ended;
    bool public isDepositedTokenCap;
    mapping(address => uint256) public balances;

    IUniswapV2Router02 public uniswapRouterV2;
    IUniswapV2Pair public iPair;

    constructor(address _IYPtoken, address _iPair) public {
        IYPtoken = _IYPtoken;
        ethCap = 350e18;
        uniswapRouterV2 = IUniswapV2Router02(
            0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D
        );
        iPair = IUniswapV2Pair(_iPair);
    }

    function start() external onlyOwner() {
        require(isDepositedTokenCap, "required deposite");
        require(startTime == 0, "Started");

        startTime = block.timestamp;
        saleEnd = startTime.add(90 minutes);
    }

    receive() external payable {
        require(startTime > 0, "Not started");
        require(ethCap >= raisedEth, "Sold out");
        require(individualEthCap >= msg.value, "MAX INVIDIDUAL CAP");
        require(saleEnd > block.timestamp, "PreSale Ended");
        require(
            balances[_msgSender()].add(msg.value) <= individualEthCap,
            "Max: 15ETH"
        );
        raisedEth = raisedEth.add(msg.value);
        require(ethCap >= raisedEth, "Reach ETH cap");
        balances[_msgSender()] = balances[_msgSender()].add(msg.value);
    }

    function deposit() external {
        require(!isDepositedTokenCap, "Deposited");
        IERC20(IYPtoken).transferFrom(
            _msgSender(),
            address(this),
            tokenCap.mul(2)
        );
        isDepositedTokenCap = true;
    }

    function endPresale() external onlyOwner() {
        require(
            raisedEth == ethCap || saleEnd < block.timestamp,
            "Sale has not ended yet"
        );
        require(!ended, "already ended");
        ended = true;

        uint256 ethToAddLP = address(this).balance;
        require(ethToAddLP > 0, "Transfer Failed");
        require(isDepositedTokenCap, "not deposited");

        addLiquidity(ethToAddLP);
        endTime = block.timestamp;
    }

    function approveUniswapRouter() external {
        IERC20(IYPtoken).approve(address(uniswapRouterV2), tokenCap.mul(10));
    }

    function addLiquidity(uint256 ethAmount) private {
        // add the liquidity
        uniswapRouterV2.addLiquidityETH{value: ethAmount}(
            address(IYPtoken),
            tokenCap,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(0xdEaD),
            block.timestamp
        );
    }

    function safeEndPresale() external onlyOwner() {
        IERC20(IYPtoken).transfer(
            _msgSender(),
            IERC20(IYPtoken).balanceOf(address(this))
        );
        _msgSender().transfer(address(this).balance);
    }

    function withdrawToken() external {
        require(ended && block.timestamp > endTime.add(20 minutes));
        require(isDepositedTokenCap, "not deposited");
        require(balances[_msgSender()] > 0, "Not join presale");
        uint256 reward = tokenCap.div(raisedEth).mul(balances[_msgSender()]);
        IERC20(IYPtoken).transfer(_msgSender(), reward);
    }
}
