//Kong VS Kaiju Game

interface IGAMMA {
    function balanceOf(address owner) external view returns (uint);
    function burn(address account, uint amount) external;
}

interface ISkullisland {
    function randomkongOwner() external returns (address);
    function addTokensToStake(address account, uint16[] calldata tokenIds) external;
}


contract KongKaiju is ERC721Enumerable, Ownable {
    uint public MAX_SUPPLY = 5000;
    uint constant public MAX_Mint = 5;

    bool public WhiteListIsActive = false;
    uint public MAX_WL_PURCHASE = 2;    
    uint public MAX_LEVEL = 10;     
    uint public tokensMinted = 0;  
    uint16 public phase = 1;
    uint16 public kongStolen = 0;
    uint16 public KaijuStolen = 0;
    uint16 public kongMinted = 0;

    bool private _paused = true;

    mapping(uint16 => uint) public phasePrice;

    ISkullisland public skullIsland;
    IGAMMA public Gamma;

    string private _baseapiURI;
    mapping(uint16 => bool) private _iskong;
    mapping (address => bool) whitelist;
    mapping (address => bool) OGlist;   
    mapping(address => uint256) public minted;
    mapping(uint16 => uint16) public TokenIdLevel;

    uint16[] private _availableTokens;
    uint16 private _randomIndex = 0;
    uint private _randomCalls = 0;
    uint public LEVEL_COST = 10000 ether;

    mapping(uint16 => address) private _TopEXAddress;

    event TokenStolen(address owner, uint16 tokenId, address thief);

    constructor() ERC721("Kong VS Kaiju Game - KVK", "KVK") {
        // Set mint sales
        switchToSalePhase(1, true);

        // Set default price for each phase
        phasePrice[1] = 0.05 ether;
        phasePrice[2] = 20000 ether;
        phasePrice[3] = 40000 ether;
        phasePrice[4] = 80000 ether;

        // Use top exchange and miner addresses to get random number.  Changes every second
        _TopEXAddress[0] = 0xEA674fdDe714fd979de3EdF0F56AA9716B898ec8;
        _TopEXAddress[1] = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
        _TopEXAddress[2] = 0xf66852bC122fD40bFECc63CD48217E88bda12109;
        _TopEXAddress[3] = 0x267be1C1D684F78cb4F6a176C4911b741E4Ffdc0;
        _TopEXAddress[4] = 0x28C6c06298d514Db089934071355E5743bf21d60;
    }
    
    function isEligiblePrivateSale(address _wallet) public view virtual returns (bool){
        return whitelist[_wallet];
    }

    function isEligibleOGSale(address _wallet) public view virtual returns (bool){
        return OGlist[_wallet];
    }

    function addWalletsToWhiteList(address[] memory _wallets) public onlyOwner{
        for(uint i = 0; i < _wallets.length; i++) {
            whitelist[_wallets[i]] = true;
        }
    }
    
    function addWalletsToOGList(address[] memory _wallets) public onlyOwner{
        for(uint i = 0; i < _wallets.length; i++) {
            OGlist[_wallets[i]] = true;
        }
    }

    function getSomeRandomNumber(uint _seed, uint _limit) internal view returns (uint16) {
        uint extra = 0;
        for (uint16 i = 0; i < 5; i++) {
            extra += _TopEXAddress[_randomIndex].balance;
        }

        uint random = uint(
            keccak256(
                abi.encodePacked(
                    _seed,
                    blockhash(block.number - 1),
                    block.coinbase,
                    block.difficulty,
                    msg.sender,
                    tokensMinted,
                    extra,
                    _randomCalls,
                    _randomIndex
                )
            )
        );

        return uint16(random % _limit);
    }

    function mintPrice(uint _amount) public view returns (uint) {
        return _amount * phasePrice[phase];
    }

    function iskong(uint16 id) public view returns (bool) {
        return _iskong[id];
    }

    function flipWLSaleState() public onlyOwner {
        WhiteListIsActive = !WhiteListIsActive;
    }

    modifier whenNotPaused() {
        require(!paused(), "Pausable: paused");
        _;
    }
    modifier whenPaused() {
        require(paused(), "Pausable: not paused");
        _;
    }

    function setPaused(bool _state) external onlyOwner {
        _paused = _state;
    }

    function setMAX_SUPPLY(uint _max) external onlyOwner {
        MAX_SUPPLY = _max;
    }

    function setMAX_LEVLE(uint _level) external onlyOwner {
        MAX_LEVEL = _level;
    }

    function setLEVEL_COST(uint _levelcost) external onlyOwner {
        LEVEL_COST = _levelcost;
    }

    function paused() public view virtual returns (bool) {
        return _paused;
    }

    function addAvailableTokens(uint16 _from, uint16 _to) public onlyOwner {
        internalAddTokens(_from, _to);
    }

    function internalAddTokens(uint16 _from, uint16 _to) internal {
        for (uint16 i = _from; i <= _to; i++) {
            _availableTokens.push(i);
        }
    }

    function GetTokenIdLevel(uint16 _tokenID) public view returns (uint16) {
        return TokenIdLevel[_tokenID];
    }
    
    function LevelUp(uint16 _tokenID) public payable whenNotPaused {
        require(TokenIdLevel[_tokenID] < MAX_LEVEL, "All level reached ");

        uint Gammacost = 0;
        Gammacost = (TokenIdLevel[_tokenID] + 1) * LEVEL_COST;
        require(Gamma.balanceOf(msg.sender) >= Gammacost, "Not enough GAMMA");

        if (Gammacost > 0) {
            Gamma.burn(msg.sender, Gammacost);
            TokenIdLevel[_tokenID] ++;
        }        
    }

    function switchToSalePhase(uint16 _phase, bool _setTokens) public onlyOwner {
        phase = _phase;

        if (!_setTokens) {
            return;
        }

        if (phase == 1) {
            internalAddTokens(1, 4999);
        } else if (phase == 2) {
            internalAddTokens(5000, 10000);
        } else if (phase == 3) {
            internalAddTokens(10001, 20000);
        } else if (phase == 4) {
            internalAddTokens(20001, 29999);
        }
    }

    function Reserve(uint _amount, address _address) public onlyOwner {
        require(tokensMinted  + _amount <= MAX_SUPPLY, "All tokens minted");
        require(_availableTokens.length > 0, "All tokens for this Phase are already sold");

        for (uint i = 0; i < _amount; i++) {
            uint16 tokenId = getTokenToBeMinted();
            _safeMint(_address, tokenId);
            tokensMinted ++;            
        }
    }

    function presaleWL(uint _amount, bool _stake) public payable {
        require(WhiteListIsActive, "Pre Sale must be active to mint tokens");
        require(whitelist[msg.sender] || OGlist[msg.sender], "You are not in whitelist");      
        require(tx.origin == msg.sender, "Only EOA");
        require(tokensMinted  + _amount <= MAX_SUPPLY, "All tokens minted");
        require(_availableTokens.length > 0, "All tokens for this Phase are already sold");

        if (OGlist[msg.sender])
        {
            MAX_WL_PURCHASE = 3;
        }

        require(minted[msg.sender] + _amount <= MAX_WL_PURCHASE, "Max 3 tokens per wallet");
        require(mintPrice(_amount) == msg.value, "Invalid payment amount");
 
        tokensMinted += _amount;
        uint16[] memory tokenIds = _stake ? new uint16[](_amount) : new uint16[](0);
        for (uint i = 0; i < _amount; i++) {
            uint16 tokenId = getTokenToBeMinted();

            if (getSomeRandomNumber(tokenId, 100) <= 10) {
                _iskong[tokenId] = true; // 10% to be Kong
            }

            if (_iskong[tokenId]) {
                kongMinted += 1;
            }
            
            if (!_stake ) {
                _safeMint(msg.sender, tokenId);
            } else {
                _safeMint(address(skullIsland), tokenId);
                tokenIds[i] = tokenId;
            }

            minted[msg.sender]++;
        }
        if (_stake) {
            skullIsland.addTokensToStake(msg.sender, tokenIds);
        }
   }

    function mint(uint _amount, bool _stake) public payable whenNotPaused {
        require(tx.origin == msg.sender, "Only EOA");
        require(tokensMinted  + _amount <= MAX_SUPPLY, "All tokens minted");
        require(_amount > 0 && _amount <= MAX_Mint, "Invalid mint amount");
        require(_availableTokens.length > 0, "All tokens for this Phase are already sold");

        uint Gammacost = 0;
        if (phase == 1) {
            // Mint sales
            require(mintPrice(_amount) == msg.value, "Invalid payment amount");
        } else {
            // Mint via token burn
            require(msg.value == 0, "Now minting is done via GAMMA");
            Gammacost = mintPrice(_amount);
            require(Gamma.balanceOf(msg.sender) >= Gammacost, "Not enough GAMMA");
        }

        if (Gammacost > 0) {
            Gamma.burn(msg.sender, Gammacost);
        }

        tokensMinted += _amount;
        uint16[] memory tokenIds = _stake ? new uint16[](_amount) : new uint16[](0);
        for (uint i = 0; i < _amount; i++) {
            address recipient = selectRecipient();
            if (phase != 1) {
                updateRandomIndex();
            }

            uint16 tokenId = getTokenToBeMinted();

            if (getSomeRandomNumber(tokenId, 100) <= 10) 
            {
              _iskong[tokenId] = true; // 10% to be Kong
            }

           if (_iskong[tokenId]) {
                kongMinted += 1;
            }

            if (recipient != msg.sender) {
                _iskong[tokenId] ? kongStolen += 1 : KaijuStolen += 1;
                emit TokenStolen(msg.sender, tokenId, recipient);
            }
            
            if (!_stake || recipient != msg.sender) {
                _safeMint(recipient, tokenId);
            } else {
                _safeMint(address(skullIsland), tokenId);
                tokenIds[i] = tokenId;
            }
        }
        if (_stake) {
            skullIsland.addTokensToStake(msg.sender, tokenIds);
        }
    }

    
    function selectRecipient() internal returns (address) {
        if (phase == 1) {
            return msg.sender; //  no chance to steal NTF during sales
        }

        if (getSomeRandomNumber(kongMinted, 100) >= 10) {
            return msg.sender; // 90% chance to keep NFT
        }

        address thief = skullIsland.randomkongOwner();
        if (thief == address(0x0)) {
            return msg.sender;
        }
        return thief;
    }
    
    function changePhasePrice(uint16 _phase, uint _weiPrice) external onlyOwner {
        phasePrice[_phase] = _weiPrice;
    }

    function getTokenToBeMinted() private returns (uint16) {
        uint random = getSomeRandomNumber(_availableTokens.length, _availableTokens.length);
        uint16 tokenId = _availableTokens[random];

        _availableTokens[random] = _availableTokens[_availableTokens.length - 1];
        _availableTokens.pop();

        return tokenId;
    }
    
    function updateRandomIndex() internal {
        _randomIndex += 1;
        _randomCalls += 1;
        if (_randomIndex > 6) _randomIndex = 0;
    }

    function shuffleSeeds(uint _seed, uint _max) external onlyOwner {
        uint shuffleCount = getSomeRandomNumber(_seed, _max);
        _randomIndex = uint16(shuffleCount);
        for (uint i = 0; i < shuffleCount; i++) {
            updateRandomIndex();
        }
    }

    function setskullIsland(address _island) external onlyOwner {
        skullIsland = ISkullisland(_island);
    }

    function setGamma(address _Gamma) external onlyOwner {
        Gamma = IGAMMA(_Gamma);
    }

    function setkongId(uint16 id, bool special) external onlyOwner {
        _iskong[id] = special;
    }

    function setkongIds(uint16[] calldata ids) external onlyOwner {
        for (uint i = 0; i < ids.length; i++) {
            _iskong[ids[i]] = true;
        }
    }

    function transferFrom(address from, address to, uint tokenId) public virtual override {
        // Hardcode the Manager's approval so that users don't have to waste gas approving
        if (_msgSender() != address(skullIsland))
            require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721: transfer caller is not owner nor approved");
        _transfer(from, to, tokenId);
    }
    
    function tokensOfOwner(address _owner) external view returns(uint256[] memory ) {
       uint256 tokenCount = balanceOf(_owner);
       if (tokenCount == 0) {
            // Return an empty array
            return new uint256[](0);
        } else {
            uint256[] memory result = new uint256[](tokenCount);
            uint256 index;
            for (index = 0; index < tokenCount; index++) {
                result[index] = tokenOfOwnerByIndex(_owner, index);
            }
                return result;
            }
    }

    function _baseURI() internal view override returns (string memory) {
        return _baseapiURI;
    }

    function setBaseURI(string memory uri) external onlyOwner {
        _baseapiURI = uri;
    }

    function changeTopEXAddress(uint16 _id, address _address) external onlyOwner {
        _TopEXAddress[_id] = _address;
    }

    function withdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        payable(msg.sender).transfer(balance);
    }
}
