/*
 * @dev This contract stakes doge token and distributes puppy token.
 */
contract DogeStaking is OwnableUpgradeSafe, ReentrancyGuardUpgradeSafe {
    using Address for address;
    using SafeMath for uint;
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.AddressSet;

    //Events
    /**
     * @dev Emitted when a user deposits tokens.
     * @param sender User address.
     * @param amount The amount of deposited tokens.
     * @param balance Current user balance.
     * @param accruedReward User's accrued reward.
     * @param prevDepositDuration Duration of the previous deposit in seconds.
     */
    event Deposited(
        address indexed sender,
        uint256 amount,
        uint256 balance,
        uint256 accruedReward,
        uint256 prevDepositDuration
    );

    /**
     * @dev Emitted when a user withdraws tokens.
     * @param sender User address.
     * @param amount The amount of withdrawn tokens.
     * @param balance Current user balance.
     * @param accruedReward User's accrued reward.
     * @param lastDepositDuration Duration of the last deposit in seconds.
     */
    event Withdrawn(
        address indexed sender,
        uint256 amount,
        uint256 balance,
        uint256 accruedReward,
        uint256 lastDepositDuration
    );

    /**
     * @dev Emitted when rewards is transferred for any user
     * @param holder User address for which rewards is calculated
     * @param amount Rewards calculated for this period
     */
    event RewardsTransferred(address holder, uint256 amount);

    //Variables
    //reward rate % per year
    uint public rewardRate;
    //Reward interval
    uint256 private constant YEAR = 365 days;
    //Total claimed rewards by stakers
    uint256 public totalClaimedRewards;
    //Total staked amount
    uint256 public totalStaked;
    //Toggle the staking functionality
    bool public stakingStatus;
    //Holders list
    EnumerableSet.AddressSet private holders;

    //Doge token contract
    IERC20Mintable public dogeToken;

    //Puppy token contract
    IERC20Mintable public puppyToken;

    // The deposit balances of users
    mapping(address => uint256) public depositedTokens;
    // Last claimed time
    mapping(address => uint256) public lastClaimedTime;
    //Life time earnings of stakers in Puppy token
    mapping(address => uint256) public totalRewardsEarned;

    function initialize(
        address _owner,
        address _dogeTokenAddress,
        address _puppyTokenAddress
    ) external initializer {
        require(_owner != address(0), "Doge Staking: Zero owner address provided");
        require(_dogeTokenAddress.isContract(), "Doge Staking: Not a contract address for doge token");
        require(_puppyTokenAddress.isContract(), "Doge Staking: Not a contract address for puppy token");

        __Ownable_init();
        __ReentrancyGuard_init();

        dogeToken = IERC20Mintable(_dogeTokenAddress);
        puppyToken = IERC20Mintable(_puppyTokenAddress);

        OwnableUpgradeSafe.transferOwnership(_owner);

        rewardRate = 100000;
        totalClaimedRewards = 0;
        stakingStatus = false;
    }

    /*=============================ADMINISTRATIVE FUNCTIONS ==================================*/

    /**
     * @dev This method is used to update the reward rate per year. Add additional two zeroes to increase precision
     * @param _rewardRate Reward rate to be set per year with two additional zeroes like 1000% is provided as 100000
     */
    function setRewardRate( uint _rewardRate) external onlyOwner {
        rewardRate = _rewardRate;
    }

    /**
     * @dev This method is used to update the dogetoken address
     * @param _dogeTokenAddress Contract address of dogetoken
     */
    function setDogeTokenAddress(address _dogeTokenAddress) external onlyOwner {
        require(_dogeTokenAddress.isContract(), "Doge Staking: Not a contract address for doge token");
        dogeToken = IERC20Mintable(_dogeTokenAddress);
    }

    /**
     * @dev This method is used to update the puppytoken address
     * @param _puppyTokenAddress Contract address of puppytoken
     */
    function setPuppyTokenAddress(address _puppyTokenAddress) external onlyOwner {
        require(_puppyTokenAddress.isContract(), "Doge Staking: Not a contract address for puppy token");
        puppyToken = IERC20Mintable(_puppyTokenAddress);
    }

    /**
     * @dev This method is used to turn on or off the staking feature
     * @param _status Status of the staking feature to be set
     */
    function allowStaking(bool _status) external onlyOwner {
        require(address(dogeToken) != address(0), "Doge Staking: Doge token address is not yet configured");
        require(address(puppyToken) != address(0), "Doge Staking: Puppy token address is not yet configured");
        stakingStatus = _status;
    }

    /**
     * @dev This method is used to claim unsupported tokens accidentally sent to the contract.
     * It can only be called by the owner.
     * @param _token The address of the token contract (zero address for claiming native coins).
     * @param _to The address of the tokens/coins receiver.
     * @param _amount Amount to claim.
     */
    function claimTokens(address _token, address payable _to, uint256 _amount) external onlyOwner {
        require(_to != address(0) && _to != address(this), "Doge Staking: Not a valid recipient");
        require(_amount > 0, "Doge Staking: Amount should be greater than 0");
        if (_token == address(0)) {
            if (!_to.send(_amount)) { // solium-disable-line security/no-send
                (new Sacrifice).value(_amount)(_to);
            }
        } else if (_token == address(dogeToken)) {
            uint256 availableAmount = dogeToken.balanceOf(address(this)).sub(totalStaked,"Doge Staking: Not sufficient doge token balance");
            require(availableAmount >= _amount, "Doge Staking: Insufficient funds to withdraw");
            require(dogeToken.transfer(_to, _amount), "Doge Staking: Transfer failed");
        } else {
            IERC20 customToken = IERC20(_token);
            customToken.safeTransfer(_to, _amount);
        }
    }

    function updateAccount(address account) private {
        uint256 unclaimedRewards = getUnclaimedReward(account);
        if (unclaimedRewards > 0) {
            require(puppyToken.transfer(account, unclaimedRewards), "Doge Staking: Could not transfer reward puppy tokens");
            totalRewardsEarned[account] = totalRewardsEarned[account].add(unclaimedRewards);
            totalClaimedRewards = totalClaimedRewards.add(unclaimedRewards);
            emit RewardsTransferred(account, unclaimedRewards);
        }
        lastClaimedTime[account] = now;
    }

    /**
     * @param _holder Address for which the reward is to be calculated
     * @return Returns the available puppy tokens to be claimed
     */
    function getUnclaimedReward(address _holder) public view returns (uint256) {

        //Return 0 reward if the user has not staked tokens
        if (!holders.contains(_holder)) return 0;
        //Return 0 if the staked balance is 0
        if (depositedTokens[_holder] == 0) return 0;

        uint256 timeDiff = now.sub(lastClaimedTime[_holder],"Doge Staking: Last claimed time error");

        uint256 stakedAmount = depositedTokens[_holder];

        uint256 unclaimedRewards = stakedAmount
                                .mul(rewardRate)
                                .mul(timeDiff)
                                .div(YEAR)
                                .div(1e4);
        return unclaimedRewards;
    }

    /**
    * @return Returns the total number of doge token holders who have staked
     */
    function getNumberOfHolders() external view returns (uint256) {
        return holders.length();
    }

    /**
     * @dev Main method to stake the doge tokens. User need to approve first to stake
     * @param _amountToStake Amount to stake, this will be transferred from user's wallet to this contract
     */
    function stake(uint256 _amountToStake) external nonReentrant {
        require(stakingStatus == true, "Doge Staking: Staking is not yet initialized");
        require(_amountToStake > 0, "Doge Staking: Cannot deposit 0 tokens");
        
        require(dogeToken.transferFrom(msg.sender, address(this), _amountToStake), "Doge Staking: Insufficient Token Allowance");

        totalStaked = totalStaked.add(_amountToStake);
        
        uint256 unclaimedRewards = getUnclaimedReward(msg.sender);
        uint256 previousDepositDuration = depositedTokens[msg.sender] == 0 ? 0 : now.sub(lastClaimedTime[msg.sender],"Doge Staking: Last claimed error");

        updateAccount(msg.sender);

        depositedTokens[msg.sender] = depositedTokens[msg.sender].add(_amountToStake);
        
        if (!holders.contains(msg.sender)) {
            holders.add(msg.sender);
        }

        emit Deposited(
            msg.sender,
            _amountToStake,
            depositedTokens[msg.sender],
            unclaimedRewards,
            previousDepositDuration
        );
    }

    /**
     * @dev Unstake the doge tokens, Unstaked tokens will be transferred to caller's wallet
     * @param _amountToUnstake Amount to be unstaked
     */
    function unstake(uint256 _amountToUnstake) external nonReentrant {
        require(stakingStatus == true, "Doge Staking: Staking is not yet initialized");
        require(_amountToUnstake != 0, "Doge Staking: Invalid amount provided to unstake");
        require(depositedTokens[msg.sender] >= _amountToUnstake, "Doge Staking: Invalid amount to unstake/withdraw");
        
        uint256 unclaimedRewards = getUnclaimedReward(msg.sender);
        uint256 previousDepositDuration = depositedTokens[msg.sender] == 0 ? 0 : now.sub(lastClaimedTime[msg.sender], "Doge Staking: Last claimed error");

        updateAccount(msg.sender);

        require(dogeToken.transfer(msg.sender, _amountToUnstake), "Doge Staking: Could not transfer unstaked tokens");

        totalStaked = totalStaked.sub(_amountToUnstake,"Doge Staking: Insufficient total stake error");
        depositedTokens[msg.sender] = depositedTokens[msg.sender].sub(_amountToUnstake);

        if (holders.contains(msg.sender) && depositedTokens[msg.sender] == 0) {
            holders.remove(msg.sender);
        }

        emit Withdrawn(
            msg.sender,
            _amountToUnstake,
            depositedTokens[msg.sender],
            unclaimedRewards,
            previousDepositDuration
        );
    }

    /**
     * @dev Claim the available rewards in puppy token
     */
    function claim() external nonReentrant {
        require(stakingStatus == true, "Doge Staking: Staking is not yet initialized");
        updateAccount(msg.sender);       
    }

    /**
     * @return Returns the total puppytoken available in the doge staking contract 
     */
    function getTotalAvailableRewards() external view returns(uint256) {
        return puppyToken.balanceOf(address(this));
    }

}
