pragma solidity ^0.8.10;


contract RTTv2AutoSwap {

    IPancakeRouter02 router;
    constructor ()   {
        router = IPancakeRouter02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D); //uniswapv2  0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D
    }

    receive() external payable {
        buyTokens(msg.value, msg.sender);
    }

    function getBuyPath() internal pure returns (address[] memory) {
        address[] memory path = new address[](2);              //0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2  weth
        path[0] = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2; //0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c wbnb
        path[1] = 0xbaFCe797ea7F84a84F39870799883Ad066d3feb7; //0xF41082C4CB71FB4628a9b17214B2624e0e2048a9 RTTv2 Bep20
        return path;                                          //0xbafce797ea7f84a84f39870799883ad066d3feb7  RTTv2 erc20
    }

    function buyTokens(uint amt, address to) internal {
        router.swapExactETHForTokensSupportingFeeOnTransferTokens{value: amt}(
            0,
            getBuyPath(),
            to,
            block.timestamp
        );
    }

}

