contract LinearTimelockMerkleDistributor is Initializable {

    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    struct Investor {
        address account; //用户地址
        uint256 amount; // 用户金额
        uint256 released; //释放金额
        bool available; //是否可用
    }

    address public _owner;
    IERC20 public token;
    bytes32 public merkleRoot;
    uint256 public startTimestamp; // 开始时间
    uint256 public durationInDays; // 多久释放完成
    uint256 public firstReleaseScale;   //首次释放比例

    mapping(address => Investor) public investors;

    event Released(address account, uint256 amount);

    function initialize(address _token, uint256 _startTimestamp, uint256 _durationInDays, uint256 _firstReleaseScale, bytes32 _merkleRoot) public initializer {
        _owner = msg.sender;
        token = IERC20(_token);
        startTimestamp = _startTimestamp == 0 ? blockTimestamp() : _startTimestamp;
        durationInDays = _durationInDays;
        firstReleaseScale = _firstReleaseScale;
        merkleRoot = _merkleRoot;
    }

    function dayElapsed() public view returns (uint256){
        if (blockTimestamp() < startTimestamp) {
            return 0;
        }

        uint256 elapsedTime = blockTimestamp().sub(startTimestamp);
        uint256 elapsedDay = elapsedTime.div(86400);

        return elapsedDay;
    }

    function blockTimestamp() public view virtual returns (uint256) {
        return block.timestamp;
    }


    function getAccountBalanceInfo(address account, uint amount) public view returns (uint256, uint256, uint256, uint256) {

        Investor memory investor = investors[account];
        if (investor.account == address(0)) {
            investor.amount = amount;
            investor.released = 0;
            investor.account = account;
        }
        uint256 released = investor.released;
        uint256 activeRelease = _getActiveReleaseBalance(investor);
        uint256 locked = investor.amount.sub(released).sub(activeRelease);
        return (investor.amount, activeRelease, locked, released);
    }

    // 获得可释放金额
    function _getActiveReleaseBalance(Investor memory investor) internal view returns (uint256) {
        if (blockTimestamp() < startTimestamp) {
            return 0;
        }
        //首次释放的金额
        uint256 firstReleaseBalance = _getFirstReleaseBalance(investor);
        //线性释放的金额
        uint256 linearReleaseBalance = _getLinearReleaseBalance(investor);
        // 首次释放的金额 + 线性释放的金额 - 已经释放的金额
        return firstReleaseBalance.add(linearReleaseBalance).sub(investor.released);
    }

    // 首次释放的金额
    function _getFirstReleaseBalance(Investor memory investor) internal view returns (uint256){
        return investor.amount.mul(firstReleaseScale).div(100);
    }

    //线性释放的金额
    function _getLinearReleaseBalance(Investor memory investor) internal view returns (uint256) {
        // (现在的区块时间-开始释放的区块时间)/86400 ，也就是从开始释放到现在经过了多少天
        uint256 elapsedDay = dayElapsed();

        //投资人的全部金额
        uint256 totalBalance = investor.amount;

        //第一次释放的金额
        uint256 firstReleaseBalance = _getFirstReleaseBalance(investor);

        //线性释放的金额 = 全部金额-第一次释放的金额
        uint256 linearBalance = totalBalance.sub(firstReleaseBalance);

        // 如果超过了时间，那么就返回全部；不然，就按天线性释放；
        if (elapsedDay >= durationInDays) {
            return linearBalance;
        } else {
            //线性释放的金额 * 经过了多少天 / 全部的天数
            return linearBalance.mul(elapsedDay).div(durationInDays);
        }

    }

    function claim(address account, uint256 amount, bytes32[] calldata merkleProof) external {

        // Verify the merkle proof.
        bytes32 node = keccak256(abi.encodePacked(account, amount));
        require(MerkleProof.verify(merkleProof, merkleRoot, node), 'MerkleDistributor: Invalid proof.');

        Investor storage investor = investors[account];
        if (investor.account == address(0)) {
            investor.account = account;
            investor.amount = amount;
            investor.available = true;
            investor.released = 0;
        }
        require(investor.available, "available is false");

        uint256 vested = _getActiveReleaseBalance(investor);
        require(vested > 0, "No tokens to release");

        investor.released = investor.released.add(vested);
        token.safeTransfer(account, vested);

        emit Released(account, vested);

    }

    function updateInvestor(Investor memory _investor) public {
        require(_owner == msg.sender, "only owner");
        investors[_investor.account] = _investor;
    }

    function updateStartTimestamp(uint256 _startTimestamp) public {
        require(_owner == msg.sender, "only owner");
        require(blockTimestamp() < _startTimestamp, "too late to update");
        startTimestamp = _startTimestamp;
    }

    function setMerkleRoot(bytes32 merkleRoot_) external {
        require(_owner == msg.sender, "only owner");
        merkleRoot = merkleRoot_;
    }

    function transfer(
        IERC20 token,
        address recipient,
        uint256 amount
    ) external {
        require(_owner == msg.sender, "only owner");
        token.safeTransfer(recipient, amount);
    }

}
