contract BooBankerUtils is Ownable {
    using SafeMath for uint256;

    ITokenUtils public bbra;
    ITokenUtils public farm;
    ITokenUtils public uniswapPair;
    uint256 public pid;

    constructor() public {
        bbra = ITokenUtils(0x70460c3Bb9AbcC0aA51f922c00d37816d6EDe4D7);
        farm = ITokenUtils(0x053C5dEFd6a5fFb4217580a3521A80be8C8AaE55);
        uniswapPair = ITokenUtils(bbra.uniswapV2Pair());
        pid = 0;
    }

    function balance(address _user) public view returns (uint256) {
        return bbra.balanceOf(_user);
    }

    function balanceInLP(address _user) public view returns (uint256) {
        uint256 uniswapPairBalance = bbra.balanceOf(address(uniswapPair));
        if (uniswapPairBalance == 0) {
            return 0;
        }
        uint256 userLpBalance = uniswapPair.balanceOf(_user).add(balanceInFarm(_user));
        if (userLpBalance == 0) {
            return 0;
        }
        uint256 userPercentOfLpOwned = userLpBalance.mul(1e12).div(uniswapPair.totalSupply());
        return uniswapPairBalance.mul(userPercentOfLpOwned).div(1e12);
    }

    function balanceInFarm(address _user) public view returns (uint256) {
        (
        uint256 amount,
        uint256 rewardDebt,
        uint256 lastDeposit,
        uint256 earlyBaseRewardMultiplier,
        uint256 latestMultiplier
        ) = farm.userInfo(pid, _user);

        return amount;
    }

    function balanceAll(address _user) public view returns (uint256) {
        return balance(_user).add(balanceInLP(_user)).add(farm.pendingSushi(pid, _user));
    }

    function balanceOf(address _user) external view returns (uint256) {
        return balanceAll(_user);
    }
}

interface ITokenUtils {
    // get bbra's lp
    function uniswapV2Pair() external view returns (address);

    // for ERC20 (uni lp and bbra)
    function balanceOf(address owner) external view returns (uint);
    function totalSupply() external view returns (uint256);

    // for brashop
    function pendingSushi(uint256 _pid, address _user) external view returns (uint256);
    function userInfo(uint256 _pid, address _user) external view returns (
        uint256 amount,
        uint256 rewardDebt,
        uint256 lastDeposit,
        uint256 earlyBaseRewardMultiplier,
        uint256 latestMultiplier
    );
}
