    //address private _creator;
    uint256 private _minted;

    //event ApprovalCheck(address from, address to, uint256 tokenId);

    constructor(address creator_) ERC721SingleCreatorExtension(creator_)  { _minted = 0; }

    function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
        return interfaceId == 
