        require(msg.sender == _creator, "Invalid caller");

        // can't mint or transfer if this contract is paused (unless minting or sending to admin)
        require(!paused() || isAdmin(to) || isAdmin(from), "Tokens aren't transferable now");

        //emit ApprovalCheck(from, to, tokenId);

        return true;
    }

    function setBaseTokenURI(string calldata uri, bool identical) external adminRequired {
        IERC721CreatorCore(_creator).setBaseTokenURIExtension(uri, identical);
    }

