pragma solidity ^0.5.5;

contract MythicCreatures is ERC721 {
    string public constant name = "Mythic Creeps";
    string public constant symbol = "CREEPS";


    struct Monster {
        string name;
        uint level;
        uint attackPower;
        uint defensePower;
        uint magicPower;
        uint magicResist;
    }

    Monster[] public monsters;
    address public gameOwner;
    uint public luckModifier;
    uint public losingBoost;
    uint public fairLevelBoost;
    uint private winner;
    uint private loser;
    uint private attackBoost;
    uint private defenseBoost;
    uint private magicBoost;
    uint private resistBoost;
    uint public mintCost;
    uint public levelCost;
    uint public statLuck;

    

    // EVENTS
    event Battle(uint winner, uint loser, uint attackAmount, uint defenseAmount, uint lucky);
    
    // MAPS
    mapping (address => bool) public allowed;
    
    
    constructor() public ERC721() {
        gameOwner = msg.sender;
        luckModifier = 30;
        losingBoost = 2;
        fairLevelBoost = 3;
        mintCost = 20;
        levelCost = 10;
        statLuck = 7;
        
    }

    modifier onlyOwnerOf(uint _monsterId) {
        require(ownerOf(_monsterId) == msg.sender, "Must be owner of monster to battle");
        _;
    }
    
    
    function random(uint restriction) private view returns (uint) {
        return ((uint256(keccak256(abi.encodePacked(block.timestamp*block.difficulty*block.number*restriction)))%statLuck+1));
    }  
    
    function lucky(uint restriction) private view returns (uint) {
        return ((uint256(keccak256(abi.encodePacked(block.timestamp*block.difficulty*block.number*restriction)))%luckModifier+1));
    }  
    
    function findBattle(uint restriction) private view returns (uint) {
        return ((uint256(keccak256(abi.encodePacked(block.timestamp*block.difficulty*block.number*restriction)))%monsters.length));
    }       
    
    function battle(uint _attackingMonster) external payable onlyOwnerOf(_attackingMonster) {
        require(msg.value >= 0, "Must Bet More than 0");
        require(msg.value <= (address(this).balance) / 1000 * 50, "Bet must be less than or equal to 0.5% of pool");
        ////////////////////////////////////////////////
        //find total monsters and draw random number to battle
        ////////////////////////////////////////////////
        uint luck = lucky(11);
        uint _defendingMonster = findBattle(luck);
        
        if (_attackingMonster == _defendingMonster) {
            _defendingMonster += 1;
        }
        
        Monster storage attacker = monsters[_attackingMonster];
        Monster storage defender = monsters[_defendingMonster];

        //Normal Stats
        uint attackAmount = (attacker.attackPower + attacker.magicPower);
        uint defenseAmount = (defender.defensePower + defender.magicResist);
        ////////////////////////////////////////////////
        //Level Cap Stats
        // Add Luck MODIFIERS to give players a lucky chance
        // You only get lucky if you are a lower level monster by 3
        ////////////////////////////////////////////////
        uint fairLevel = (defender.level + fairLevelBoost);
        uint fairDefense = (defender.level - fairLevelBoost);
        ////////////////////////////////////////////////
        // Create Random Winning StatBoost
        ////////////////////////////////////////////////
        attackBoost = random(3);
        defenseBoost = random(6);
        magicBoost = random(21);
        resistBoost = random(47);
        ////////////////////////////////////////////////
        //Calculate adjusted Offense & Defense
        ////////////////////////////////////////////////
        if (attacker.level >= fairLevel) {
            defenseAmount = defenseAmount.mul(luck);
        }
        if (attacker.level < fairDefense) {
            
            if (defender.level > 3) {
                attackAmount = attackAmount.mul(luck);  
            }
            else {
                attackAmount = attackAmount;
            }
        }
        else {
            //filler
            attackAmount = attackAmount;
        }
        ////////////////////////////////////////////////
        // Battle Logic
        ////////////////////////////////////////////////
        if (attackAmount > defenseAmount) {
            //Atacker Won
            winner = _attackingMonster;
            loser = _defendingMonster;
            attacker.level += 1;
            attacker.attackPower += attackBoost;
            attacker.defensePower += defenseBoost;
            attacker.magicPower += magicBoost;
            attacker.magicResist += resistBoost;
            //Defender Stat Boost - This is to keep pool level small stat boost for battleworn with no level.
            defender.attackPower += losingBoost;
            defender.defensePower += losingBoost;
            defender.magicPower += losingBoost;
            defender.magicResist += losingBoost;
            msg.sender.transfer(msg.value * 2);
            emit Battle(winner, loser, attackAmount, defenseAmount, luck);
            }
        else{
            //Defender Won
            attacker.level += 1;
            winner = _defendingMonster;
            loser = _attackingMonster;
            defender.attackPower += attackBoost;
            defender.defensePower += defenseBoost;
            defender.magicPower += magicBoost;
            defender.magicResist += resistBoost;
            //Attacker Stat Boost - This is to keep pool level small stat boost for battleworn with no level.
            attacker.attackPower += losingBoost;
            attacker.defensePower += losingBoost;
            attacker.magicPower += losingBoost;
            attacker.magicResist += losingBoost;
            address payable defenderWin = address(uint160(_defendingMonster));
            //Denfense Winners get 3% of bet the rest goes to the pool for winnings
            defenderWin.transfer(msg.value / 1000 * 300);

            
            emit Battle(winner, loser, attackAmount, defenseAmount, luck);
            }
            
    }
    
    ////////////////////////////////////////////////
    //Owner only Mint function for Airdrops
    ////////////////////////////////////////////////
    function mintMonster(string memory _name, address _to) public {
        require(msg.sender == gameOwner, "Only game owner can create new monsters");
        uint id = monsters.length;
        monsters.push(Monster(_name, 1, 1, 1, 1, 1));
        _safeMint(_to, id);

    }
    
    function createMonster(string memory _name, address _to, address _token, uint256 amount) public {
	    require(allowed[_token], "Token must be allowed to be deposited"); // mapping (address => bool), set in constructor or make an add function
	    require(amount >= (mintCost * (10 ** 18)), "Must deposit token minimums"); // mapping (address => uint256), set in same place as above, or make a mapping (address => TokenStruct) to contain all info
	    uint id = monsters.length;
	    monsters.push(Monster(_name, 1, 1, 1, 1, 1));
	    _safeMint(_to, id);
	    IERC20(_token).transferFrom(msg.sender, address(this), amount); // msg.sender must call IERC20(_token).approve(x), where x >= amount, most use 2**256-1 cause thats max uint256

	}

    function levelMonster(uint _monsterId, address _token, uint256 amount) public {
	    require(allowed[_token], "Token must be allowed to be deposited"); // mapping (address => bool), set in constructor or make an add function
	    require(amount >= (levelCost * (10 ** 18)), "Must deposit token minimums"); // mapping (address => uint256), set in same place as above, or make a mapping (address => TokenStruct) to contain all info
	    Monster storage _levelMonster = monsters[_monsterId];
	    uint Boost = random(7);
        _levelMonster.level += 1;		
        _levelMonster.attackPower += Boost;
        _levelMonster.defensePower += Boost;
        _levelMonster.magicPower += Boost;
        _levelMonster.magicResist += Boost;
	    IERC20(_token).transferFrom(msg.sender, address(this), amount); // msg.sender must call IERC20(_token).approve(x), where x >= amount, most use 2**256-1 cause thats max uint256

	}
	////////////////////////////////////////////////
	// GAME MODIFERS for Balancing & Mint Cost
	////////////////////////////////////////////////
	function setAllowed(address token, bool allow) public {
        require(msg.sender == gameOwner, "Only game owner can set allowed tokens");
        allowed[token] = allow; // default entry is always falsy, so this case allowed[token] default is false
    }

    function setMintCost(uint _mintCost) public {
        require(msg.sender == gameOwner, "Only game owner can set allowed tokens");
        mintCost = _mintCost;
    }
    
    function setLevelCost(uint _levelCost) public {
        require(msg.sender == gameOwner, "Only game owner can set allowed tokens");
        levelCost = _levelCost;
    }

    function setLuckModifier(uint _luckMod) public {
        require(msg.sender == gameOwner, "Only game owner can set allowed tokens");
        luckModifier = _luckMod;
    }    

    function setLosingBoost(uint _losingBoost) public {
        require(msg.sender == gameOwner, "Only game owner can set allowed tokens");
        losingBoost = _losingBoost;
    }      
    
    function setLevelBoost(uint _levelBoost) public {
        require(msg.sender == gameOwner, "Only game owner can set allowed tokens");
        fairLevelBoost = _levelBoost;
    }      
    
    function setStatLuck(uint _statLuck) public {
        require(msg.sender == gameOwner, "Only game owner can set allowed tokens");
        statLuck = _statLuck;
    }  
    
    ////////////////////////////////////////////////
    //external Functions
    ////////////////////////////////////////////////
    function totalSupply() public view returns (uint) {
        return monsters.length;
    }
    
    function maxBet() public view returns (uint) {
        return (address(this).balance) / 1000 * 50;
    }
    
    function deposit() public payable{
    }

    ////////////////////////////////////////////////
    //Functions for Contract Migration//Upgrade if needed
    ////////////////////////////////////////////////
    function payout () public {
    	require(msg.sender == gameOwner, "Only game owner can move tokens");
    	msg.sender.transfer(address(this).balance);
	}				
    
    
}
