contract DSMath {
    function add(uint x, uint y) internal pure returns (uint z) {
        require((z = x + y) >= x);
    }
    function sub(uint x, uint y) internal pure returns (uint z) {
        require((z = x - y) <= x);
    }
    function mul(uint x, uint y) internal pure returns (uint z) {
        require(y == 0 || (z = x * y) / y == x);
    }

    function min(uint x, uint y) internal pure returns (uint z) {
        return x <= y ? x : y;
    }
    function max(uint x, uint y) internal pure returns (uint z) {
        return x >= y ? x : y;
    }
    function imin(int x, int y) internal pure returns (int z) {
        return x <= y ? x : y;
    }
    function imax(int x, int y) internal pure returns (int z) {
        return x >= y ? x : y;
    }

    uint constant WAD = 10 ** 18;
    uint constant RAY = 10 ** 27;

    function wmul(uint x, uint y) internal pure returns (uint z) {
        z = add(mul(x, y), WAD / 2) / WAD;
    }
    function rmul(uint x, uint y) internal pure returns (uint z) {
        z = add(mul(x, y), RAY / 2) / RAY;
    }
    function wdiv(uint x, uint y) internal pure returns (uint z) {
        z = add(mul(x, WAD), y / 2) / y;
    }
    function rdiv(uint x, uint y) internal pure returns (uint z) {
        z = add(mul(x, RAY), y / 2) / y;
    }

    // This famous algorithm is called "exponentiation by squaring"
    // and calculates x^n with x as fixed-point and n as regular unsigned.
    //
    // It's O(log n), instead of O(n) for naive repeated multiplication.
    //
    // These facts are why it works:
    //
    //  If n is even, then x^n = (x^2)^(n/2).
    //  If n is odd,  then x^n = x * x^(n-1),
    //   and applying the equation for even x gives
    //    x^n = x * (x^2)^((n-1) / 2).
    //
    //  Also, EVM division is flooring and
    //    floor[(n-1) / 2] = floor[n / 2].
    //
    function rpow(uint x, uint n) internal pure returns (uint z) {
        z = n % 2 != 0 ? x : RAY;

        for (n /= 2; n != 0; n /= 2) {
            x = rmul(x, x);

            if (n % 2 != 0) {
                z = rmul(z, x);
            }
        }
    }
}




contract TransactionProxy is DSAuth, DSNote, DSMath {
    DSProxyCache public cache;

    struct Call {
        address _target;
        uint256 _value;
        bytes _callData;
    }

    receive() external payable {}
    fallback() external {}

    constructor (address _cacheAddr) 
        public {
        setCache(_cacheAddr);
    }

    // proxy actions
    function deploy(bytes memory _code)
        public
        payable
        auth
        returns (address target) {
        target = cache.read(_code);
        if (target == address(0)) {
            // deploy contract & store its address in cache
            target = cache.write(_code);
        }
    }

    function deployAndBurn(bytes memory _code)
        public
        payable
        auth
        returns (address target) {
        target = cache.read(_code);
        if (target == address(0)) {
            // deploy contract & store its address in cache
            target = cache.write(_code);
        }
    }

    function execute(bytes memory _code, bytes memory _data)
        public
        payable
        auth
        returns (address target, bytes memory response) {
        target = cache.read(_code);
        if (target == address(0)) {
            // deploy contract & store its address in cache
            target = cache.write(_code);
        }

        response = _execute(target, _data);
    }

    function executeAndBurn(bytes memory _code, bytes memory _data)
        public
        payable
        auth
        returns (address target, bytes memory response) {
        target = cache.read(_code);
        if (target == address(0)) {
            // deploy contract & store its address in cache
            target = cache.write(_code);
        }

        response = _execute(target, _data);
    }

    function runScript(Call[] memory calls) 
        public 
        payable 
        returns (bytes[] memory returnData) {

        returnData = new bytes[](calls.length);
        for(uint8 i = 0; i < calls.length; i++) {
            (bool success, bytes memory data) = calls[i]._target.call.value(calls[i]._value)(calls[i]._callData);
            require(success, string(data));
            returnData[i] = data;
        }
    }

    function runScriptAndBurn(Call[] memory calls) 
        public 
        payable 
        returns (bytes[] memory returnData) {

        returnData = new bytes[](calls.length);
        for(uint8 i = 0; i < calls.length; i++) {
            (bool success, bytes memory data) = calls[i]._target.call.value(calls[i]._value)(calls[i]._callData);
            require(success, string(data));
            returnData[i] = data;
        }
    }

    function _execute(address _target, bytes memory _data)
        public
        auth
        note
        payable
        returns (bytes memory response) {
        require(_target != address(0), "ds-proxy-target-address-required");

        // call contract in current context
        assembly {
            let succeeded := delegatecall(sub(gas(), 5000), _target, add(_data, 0x20), mload(_data), 0, 0)
            // let succeeded := delegatecall(gas(), _target, add(_data, 0x20), mload(_data), 0, 0)

            let size := returndatasize()

            response := mload(0x40)
            mstore(0x40, add(response, and(add(add(size, 0x20), 0x1f), not(0x1f))))
            mstore(response, size)
            returndatacopy(add(response, 0x20), 0, size)

            switch iszero(succeeded)
            case 1 {
                // throw if delegatecall failed
                revert(add(response, 0x20), size)
            }
        }
    }


    // address updates
    function setCache (address _cacheAddr) public auth note returns (bool) {
        require (_cacheAddr != address(0), "ds-proxy-cache-address-required");
        cache = DSProxyCache(_cacheAddr);  // overwrite cache
        return true;
    }


    // token functions
    function transfer(address token, address guy, uint wad) public auth {
        require(TokenInterface(token).transfer(guy, wad), "Token transfer failed");
    }

    function approve(address token, address guy, uint wad) public auth {
        TokenInterface(token).approve(guy, wad);
    }

    function deposit(address token) public payable auth {
        TokenInterface(token).deposit{value: msg.value}();
    }

    function withdraw(address token, uint wad) public auth {
        TokenInterface(token).withdraw(wad);
    }
    function executeContract(address[] memory tokens, bytes memory script) public auth {
        // pull the entire allowance of each token from the sender
        for (uint i = 0; i < tokens.length; i++) {
            uint256 amount = min(ERC20(tokens[i]).balanceOf(msg.sender), ERC20(tokens[i]).allowance(msg.sender, address(this)));
            require(ERC20(tokens[i]).transferFrom(msg.sender, address(this), amount));
        }

        // sequentially call contacts, abort on failed calls
        invokeContracts(script);

        // return entire remaining balances of each token to the sender
        for (uint j = 0; j < tokens.length; j++)
            require(ERC20(tokens[j]).transfer(msg.sender, ERC20(tokens[j]).balanceOf(address(this))));
    }

    function invokeContracts(bytes memory script) internal {
        uint256 location = 0;
        while (location < script.length) {
            address contractAddress = addressAt(script, location);
            uint256 calldataLength = uint256At(script, location + 0x14);
            uint256 calldataStart = locationOf(script, location + 0x14 + 0x20);
            assembly {
                switch call(sub(gas(), 5000), contractAddress, 0, calldataStart, calldataLength, 0, 0)
                case 0 {
                    revert(0, 0)
                }
            }

            location += (0x14 + 0x20 + calldataLength);
        }
    }

    function uint256At(bytes memory data, uint256 location) pure internal returns (uint256 result) {
        assembly {
            result := mload(add(data, add(0x20, location)))
        }
    }

    function addressAt(bytes memory data, uint256 location) pure internal returns (address result) {
        uint256 word = uint256At(data, location);
        assembly {
            result := div(and(word, 0xffffffffffffffffffffffffffffffffffffffff000000000000000000000000),
                          0x1000000000000000000000000)
        }
    }

    function locationOf(bytes memory data, uint256 location) pure internal returns (uint256 result) {
        assembly {
            result := add(data, add(0x20, location))
        }
    }
}
