     * @param duration duration in seconds of the period in which the tokens will vest
     */
    function __SingleTokenVestingNonRevocable_init(
        IERC20Upgradeable token,
        address beneficiary,
        address guardian,
        uint256 start,
        uint256 cliffDuration,
        uint256 duration
    ) public initializer {
        require(address(token) != address(0), "TokenVesting: token is the zero address");
        require(beneficiary != address(0), "TokenVesting: beneficiary is the zero address");
        // solhint-disable-next-line max-line-length
        require(cliffDuration <= duration, "TokenVesting: cliff is longer than duration");
        require(duration > 0, "TokenVesting: duration is 0");
        // solhint-disable-next-line max-line-length
        require(start.add(duration) > block.timestamp, "TokenVesting: final time is before current time");

        _token = token;
        _beneficiary = beneficiary;
        _guardian = guardian;
        _duration = duration;
        _cliff = start.add(cliffDuration);
        _start = start;
    }

    /**
     * @return the token being held.
     */
    function token() public view returns (IERC20Upgradeable) {
        return _token;
    }

    /**
     * @return the beneficiary of the tokens.
     */
    function beneficiary() public view returns (address) {
        return _beneficiary;
    }

    function guardian() public view returns (address) {
        return _guardian;
    }

    /**
     * @return the cliff time of the token vesting.
     */
    function cliff() public view returns (uint256) {
        return _cliff;
    }

    /**
     * @return the start time of the token vesting.
     */
    function start() public view returns (uint256) {
        return _start;
    }

    /**
     * @return the duration of the token vesting.
     */
    function duration() public view returns (uint256) {
        return _duration;
    }

    /**
     * @return the amount of the token released.
     */
    function released() public view returns (uint256) {
        return _released;
    }

    /**
     * @notice Transfers vested tokens to beneficiary.
     */
    function release(uint256 amount) public virtual whenNotPaused() {
        require (msg.sender == beneficiary(), "onlyBeneficiary");

        uint256 unreleased = _releasableAmount();
        require(unreleased > 0, "TokenVesting: no tokens are due");
        require(amount <= unreleased, "Excessive release");

        _released = _released.add(amount);
        _token.safeTransfer(_beneficiary, amount);

        emit TokensReleased(address(_token), amount);
    }

    function releasableAmount() public view returns (uint256) {
        return _releasableAmount();
    }

    /**
     * @dev Calculates the amount that has already vested but hasn't been released yet.
     */
    function _releasableAmount() internal view returns (uint256) {
        return _vestedAmount().sub(_released);
    }

    /**
     * @dev Calculates the amount that has already vested.
     */
    function _vestedAmount() internal view returns (uint256) {
        uint256 currentBalance = _token.balanceOf(address(this));
        uint256 totalBalance = currentBalance.add(_released);

        if (block.timestamp < _cliff) {
            return 0;
        } else if (block.timestamp >= _start.add(_duration)) {
            return totalBalance;
        } else {
            return totalBalance.mul(block.timestamp.sub(_start)).div(_duration);
        }
    }

    function claimToken(IERC20Upgradeable tokenToClaim) external whenNotPaused() {
        require (msg.sender == beneficiary(), "onlyBeneficiary");

        uint256 claimableTokenAmount = tokenToClaim.balanceOf(address(this));
        require(claimableTokenAmount > 0, "smart-timelock/no-token-balance-to-claim");

        require(address(tokenToClaim) != address(token()), "smart-timelock/no-locked-token-claim");
        tokenToClaim.transfer(beneficiary(), claimableTokenAmount);
    }

    function claimEther() external whenNotPaused() {
        require (msg.sender == beneficiary(), "onlyBeneficiary");

        uint256 etherToTransfer = address(this).balance;
        require(etherToTransfer > 0, "smart-timelock/no-ether-balance-to-claim");

        payable(beneficiary()).transfer(etherToTransfer);
    }

    function migrateBeneficiary(address newBeneficiary) external {
        require (msg.sender == beneficiary(), "onlyBeneficiary");
        _beneficiary = newBeneficiary;
    }

    function pause() external {
        require(msg.sender == _guardian, "onlyGuardian");
        _pause();
    }

    function unpause() external {
        require(msg.sender == _guardian, "onlyGuardian");
        _unpause();
    }
}
