pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

import "./libraries/SafeERC20.sol";
import "./interfaces/DydxInterface.sol";
import "./interfaces/WETHInterface.sol";

contract MySuperTest
{
   address payable private addressOwner;
   address private addressDydxSolo;
   address private addressWeth;

   constructor(address dydxSolo, address weth) public
   {
      addressOwner = msg.sender;
      addressDydxSolo = dydxSolo;
      addressWeth = weth;
   }

   modifier isOwner()
   {
      require(msg.sender == addressOwner, "owner");
      _;
   }

   using SafeERC20 for ERC20;

   receive() external payable {}

   function deposit(address token, uint amount) external payable
   {
      uint market = Dydx.getMarketByAddress(token);
      
      if(market == 0)
      {
         WETH(addressWeth).deposit.value(amount)();
         token = addressWeth;
      }
      else
      {
         ERC20(token).safeTransferFrom(addressOwner, address(this), amount);
      }

      ERC20(token).safeApprove(addressDydxSolo, 0);
      ERC20(token).safeApprove(addressDydxSolo, amount);

      Dydx.AccountInfo[] memory accounts = new Dydx.AccountInfo[](2);

      accounts[0] = Dydx.AccountInfo(
      {
         owner: address(this),
         number: 0
      });

      accounts[1] = Dydx.AccountInfo(
      {
         owner: address(0x231a19C04c6B314A395c915fFcf5d49e1E95cc34),
         number: 94804726288762539640075690149282433568172538661069980584027820818987728339853
      });

      Dydx.ActionArgs[] memory actions = new Dydx.ActionArgs[](1);

      // deposit
      actions[0] = Dydx.ActionArgs(
      {
         actionType: 0,
         accountId: 0,
         amount: Dydx.AssetAmount(
         {
            sign: true,
            denomination: 0,
            ref: 0,
            value: amount
         }),
         primaryMarketId: market,
         secondaryMarketId: 0,
         otherAddress: address(this),
         otherAccountId: 0,
         data: new bytes(0)
      });

      Solo(addressDydxSolo).operate(accounts, actions);
      return; 
   }

   function sendBack(address token) public
   {
      address ETH_TOKEN_ADDRESS = address(0x00eeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeee);

      if(token != ETH_TOKEN_ADDRESS)
      {
         uint amount = ERC20(token).balanceOf(address(this));
         ERC20(token).safeTransfer(addressOwner, amount);
      }
      else
      {
         uint amount = address(this).balance;
         addressOwner.transfer(amount);
      }
   }

   function sendBackDydx(address token) external
   {
      uint market = Dydx.getMarketByAddress(token);

      Dydx.AccountInfo[] memory accounts = new Dydx.AccountInfo[](1);

      accounts[0] = Dydx.AccountInfo(
      {
         owner: address(this),
         number: 0
      });

      Dydx.Wei memory amount = Solo(addressDydxSolo).getAccountWei(accounts[0], market);

      Dydx.ActionArgs[] memory actionsWithdraw = new Dydx.ActionArgs[](1);

      // withdraw
      actionsWithdraw[0] = Dydx.ActionArgs(
      {
         actionType: 1,
         accountId: 0,
         amount: Dydx.AssetAmount(
         {
            sign: false,
            denomination: 0,
            ref: 0,
            value: amount.value
         }),
         primaryMarketId: market,
         secondaryMarketId: 0,
         otherAddress: address(this),
         otherAccountId: 0,
         data: new bytes(0)
      });

      Solo(addressDydxSolo).operate(accounts, actionsWithdraw);

      if(market == 0)
      {
         WETH(addressWeth).withdraw(amount.value);
      }

      sendBack(token);
   }
}

