pragma solidity ^0.6.0;

library Dydx
{
   struct AccountInfo
   {
      address owner;
      uint256 number;
   }

   struct AssetAmount
   {
      bool sign;
      uint256 denomination;
      uint256 ref;
      uint256 value;
   }

   struct ActionArgs
   {
      uint256 actionType;
      uint256 accountId;
      AssetAmount amount;
      uint256 primaryMarketId;
      uint256 secondaryMarketId;
      address otherAddress;
      uint256 otherAccountId;
      bytes data;
   }

   struct Value
   {
      uint256 value;
   }

   struct Wei
   {
      bool sign;
      uint256 value;
   }

   function getMarketByAddress(address token) pure internal returns(uint)
   {
      uint market;

      if(token == address(0x00eeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeeee))
      {
         // WETH
         market = 0;
      }
      else if(token == address(0x0089d24a6b4ccb1b6faa2625fe562bdd9a23260359))
      {
         // SAI
         market = 1;
      }
      else if(token == address(0x00a0b86991c6218b36c1d19d4a2e9eb0ce3606eb48))
      {
         // USDC
         market = 2;
      }
      else if(token == address(0x006b175474e89094c44da98b954eedeac495271d0f))
      {
         // DAI
         market = 3;
      }

      return market;
   }
}

