    constructor(uint256 _initial_supply) {
        uint256 _max_total_supply = 80000;
        _name = "MyFinance";
        _symbol = "MYFI";
        _totalSupply = _initial_supply.mul(1000000000000000000);
        maxTotalSupply = _max_total_supply.mul(1000000000000000000);
