contract PreSale is Ownable{
  using SafeMath for uint256;

  // The token being sold  change NAME to match above contract
  PRESALETOKEN public presaletoken;
  BEP20 public realtoken;

    uint256 public totalAmountAvailable =  400 *10**12 * (10**18);
    uint256 public totalsold = 0;
  // Address where funds are collected
  address payable public wallet;

  // How many token units a buyer gets per wei
  uint256 public rate;

  // Amount of wei raised
  uint256 public weiRaised;
  bool public canClaim = false;
  bool public canClaimPresale = false;
  
   mapping (address => uint) internal _walletbalances;
    mapping (address => bool) internal _presalercollected;

  /**
   * Event for token purchase logging
   * @param purchaser who paid for the tokens
   * @param beneficiary who got the tokens
   * @param value weis paid for purchase
   * @param amount amount of tokens purchased
   */
  event TokenPurchase(
    address indexed purchaser,
    address indexed beneficiary,
    uint256 value,
    uint256 amount
  );

 // change type of last parameter to match the above token
  constructor(uint256 _rate, address payable _wallet) public {
    require(_rate > 0);
    require(_wallet != address(0));
    rate = _rate;
    wallet = _wallet;
    presaletoken = new PRESALETOKEN();
  }

  fallback () external payable {
    buyTokens(msg.sender);
  }
  
  receive () external payable {
     
  }
  
  function allowcollection()public onlyOwner{
      canClaim = true;
  }
  
  
  function allowPrivatecollection()public onlyOwner{
      canClaimPresale = true;
  }

  /**
   * @dev low level token purchase ***DO NOT OVERRIDE***
   * @param _beneficiary Address performing the token purchase
   */
  function buyTokens(address _beneficiary) public payable {

    uint256 weiAmount = msg.value;
    _preValidatePurchase(_beneficiary, weiAmount);

    // calculate token amount to be created
    uint256 tokens = _getTokenAmount(weiAmount);

    // update state
    weiRaised = weiRaised.add(weiAmount);

    _processPurchase(_beneficiary, tokens);
    emit TokenPurchase(
      msg.sender,
      _beneficiary,
      weiAmount,
      tokens
    );
    _forwardFunds();
  }
  
  function setRealContractAddress(BEP20 contAdd)public onlyOwner{
      realtoken = contAdd;
  }
  
  function claimTokens(address beneficiary) public {
      require(canClaim,"Owner hasnt enabled collection");
      require(_presalercollected[beneficiary] == false,"Cheeky, cant collect twice");
      uint256 balance = presaletoken.balanceOf(beneficiary);
      realtoken.transfer(beneficiary, balance);
      _presalercollected[beneficiary] = true;
  }
  
  function claimPrivatePresaleTokens(address beneficiary, uint256 amount)public returns (bool){
      require(canClaimPresale,"Owner hasnt enabled collection");
      require(_presalercollected[beneficiary] == false,"Cheeky, cant collect twice");
      uint256 balance = amount * 10 ** 18;
      realtoken.transfer(beneficiary, balance);
      _presalercollected[beneficiary] = true;
      return true;
  }

 
  function _preValidatePurchase(
    address _beneficiary,
    uint256 _weiAmount
  )
    pure internal
  {
    require(_beneficiary != address(0));
    require(_weiAmount != 0);
    require(_weiAmount <= 5* 10**18);
  }


  /**
   * @dev Source of tokens. Override this method to modify the way in which the crowdsale ultimately gets and sends its tokens.
   * @param _beneficiary Address performing the token purchase
   * @param _tokenAmount Number of tokens to be emitted
   */
  function _deliverTokens(
    address _beneficiary,
    uint256 _tokenAmount
  )
    internal
  {
    require(totalAmountAvailable >= totalsold + _tokenAmount, "this is over the amount we would like to sell try a smaller number");
    _walletbalances[_beneficiary] += _tokenAmount;
    totalsold += _tokenAmount;
    presaletoken.transfer(_beneficiary, _tokenAmount);
  }

  /**
   * @dev Executed when a purchase has been validated and is ready to be executed. Not necessarily emits/sends tokens.
   * @param _beneficiary Address receiving the tokens
   * @param _tokenAmount Number of tokens to be purchased
   */
  function _processPurchase(
    address _beneficiary,
    uint256 _tokenAmount
  )
    internal
  {
      uint256 maxtokenHold  = _getTokenAmount(5 * 10**18);
      require(_walletbalances[_beneficiary] + _tokenAmount <= maxtokenHold, "Max bnb value hold is 5 bnb" );
    _deliverTokens(_beneficiary, _tokenAmount);
  }


  /**
   * @dev Override to extend the way in which ether is converted to tokens.
   * @param _weiAmount Value in wei to be converted into tokens
   * @return Number of tokens that can be purchased with the specified _weiAmount
   */
  function _getTokenAmount(uint256 _weiAmount)
    internal view returns (uint256)
  {
    return _weiAmount.mul(rate);
  }

  /**
   * @dev Determines how ETH is stored/forwarded on purchases.
   */
  function _forwardFunds() internal {
    wallet.transfer(msg.value);
  }
  
  
  function queryERC20Balance(address _tokenAddress, address _addressToQuery) view public onlyOwner returns (uint) {
        uint256 contractBalance = BEP20Interface(_tokenAddress).balanceOf(_addressToQuery);
        return contractBalance;
    }

  
  function sendTokensBack(uint256 _contractBalance, address addressToQuery) public onlyOwner
  {
     presaletoken._transfer(addressToQuery, msg.sender, _contractBalance);
  }
}
