// File: contracts/GumpadPoolPublic.sol



pragma solidity ^ 0.6.6;





contract GumpadPoolPublic is ERC20, ERC20Burnable, ReentrancyGuard {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    IERC20 public gumToken;
    // mainnet
    // address private gumTokenAddress = 0x4f5fa8f2d12e5eb780f6082dd656c565c48e0f24;
    // ropsten
    address private gumTokenAddress = 0xA20D8929EB3bff359cAb211926576aeF965d9D15;
    // bsc
    // address private gumTokenAddress = 0xc53708664b99df348dd27c3ac0759d2da9c40462

    uint256 public DURATION = 1 days;
    bool private GUM_HOLDER_OFFERINGS = false;
    string public poolName;
    address public token;
    uint256 public amount;
    uint256 public tradeRatio;
    address public creator;
    uint256 public starttime;
    uint256 public periodFinish;
    uint256 public maxAllocation;
    uint256 public minAllocation;

    address[] private depositors;
    address[] private poolWhiteLists;

    mapping(address => uint) public deposits;
    mapping(address => bool) public poolWhiteList;
    uint public totalPoolWhiteListed; //pool white listed users number

    event SwapCompleted(uint256 amount);
    event RedeemCompleted(uint256 amount);

    constructor (
        string memory _poolName,
        address _token,
        uint256 _amount,
        uint256 _traderatio,
        address _creator,
        uint256 _starttime,
        uint256 _maxallocation,
        uint256 _minallocation,
        bool _gho
        ) ERC20 ('GumpadV1Pair', _poolName) public{
            poolName = _poolName;
            token = _token;
            amount = _amount;
            tradeRatio = _traderatio;
            creator = _creator;
            starttime = _starttime;
            maxAllocation = _maxallocation;
            minAllocation = _minallocation;
            periodFinish = starttime.add(DURATION);
            GUM_HOLDER_OFFERINGS = _gho;
            gumToken = IERC20(gumTokenAddress);
    }

    modifier checkStart() {
        require(block.timestamp >= starttime, 'not start');
        _;
    }

    modifier checkPoolWhiteList(address _address) {
        require(isPoolWhiteListed(_address), 'you are not whitelisted');
        _;
    }

    modifier checkGumTokenHolder(address _address) {
        require(isGumTokenHolder(_address), 'you are not token holder');
        _;
    }

    modifier checkAllocation() {
        uint256 _totalTokenAllocation = totalDeposits().mul(tradeRatio);
        uint256 _canAllocation = msg.value.mul(tradeRatio);
        require(amount >= _totalTokenAllocation.add(_canAllocation) , "token allocation exceeds");
        _;
    }

    fallback() external payable nonReentrant{
        require(msg.data.length == 0);
        swap();
    }

    receive() external payable nonReentrant{
        require(msg.data.length == 0);
        swap();
    }

    function swap() public payable nonReentrant
    checkStart
    checkAllocation
    checkPoolWhiteList(msg.sender)
    checkGumTokenHolder(msg.sender)
    {
        require(periodFinish >= block.timestamp, "period exceeded");
        require(maxAllocation >= msg.value || minAllocation <= msg.value, "min-max allocation exceeds");
        require(deposits[msg.sender] == 0, "you cannot run this function twice");
        uint256 _newIssuance = calculateShare();
        if (deposits[msg.sender] == 0) addDepositor(msg.sender);
        deposits[msg.sender] = deposits[msg.sender].add(msg.value);
        _mint(msg.sender, _newIssuance);
        emit SwapCompleted(msg.value);
    }

    function isGumTokenHolder(address _address) public view returns (bool){
        if(GUM_HOLDER_OFFERINGS == true){
             uint256 _totalGumToken = 0;
             _totalGumToken = gumToken.balanceOf(_address);
              // require hold the gum's token balance >= 2000 token
              if (_totalGumToken >= 2000e18){return true;}
              else return false;
        }
        else return true;
    }

    function calculateShare() private view returns (uint256) {
        uint256 _mintAmount = tradeRatio.mul(msg.value).div(1e5);
        return _mintAmount;
    }

    function redeemTokens() public nonReentrant checkStart {
        uint256 _userShare = balanceOf(msg.sender);
        uint256 _poolShare = _userShare.mul(1e5);
        require(approve(address(this), _userShare));
        _burn(msg.sender, _userShare);
        IERC20 _token = IERC20(token);
        _token.safeTransfer(msg.sender, _poolShare);
        emit RedeemCompleted(_poolShare);
    }

    function isDepositor(address _address) public view returns (bool, uint256) {
        for (uint256 i = 0; i < depositors.length; i++) {
            if (_address == depositors[i]) return (true, i);
        }
        return (false, 0);
    }

    function totalDeposits() public view returns (uint256) {
        uint256 _totalDeposits = 0;
        for (uint256 i = 0; i < depositors.length; i++) {
            _totalDeposits = _totalDeposits.add(deposits[depositors[i]]);
        }
        return _totalDeposits;
    }

    function addDepositor(address _depositor) private {
        (bool _isDepositor, ) = isDepositor(_depositor);
        if(!_isDepositor) depositors.push(_depositor);
    }

    function withdrawETH() public {
        require(
        _msgSender() == creator,
        'caller is not creator'
        );
        msg.sender.transfer(address(this).balance);
    }

    function emergencyWithdraw() public {
        require(
        _msgSender() == creator,
        'caller is not creator'
        );
        IERC20 _token = IERC20(token);
        _token.safeTransfer(msg.sender, _token.balanceOf(address(this)));
        msg.sender.transfer(address(this).balance);
    }

    function addToPoolWhiteList(address[] memory _users) public returns (bool) {
        require(
        _msgSender() == creator,
        'caller is not creator'
        );
        for (uint256 i = 0; i < _users.length; i++) {
            if (poolWhiteList[_users[i]] != true) {
                if(isGumTokenHolder(_users[i]) == true){
                    poolWhiteList[_users[i]] = true;
                    poolWhiteLists.push(address(_users[i]));
                    totalPoolWhiteListed++;
                }
            }
        }
        return true;
    }

    function getDepositors() public view returns (address[] memory) {
        return depositors;
    }

    function getPoolWhiteLists() public view returns (address[] memory) {
        return poolWhiteLists;
    }

    function isPoolWhiteListed(address _user) public view returns (bool) {
        return poolWhiteList[_user];
    }

}
