pragma solidity ^0.6.0;

import "./SafeMath.sol";
import "./ManagerRole.sol";
import "./Pausable.sol";
import "./IERC20.sol";
import "./EnumerableSet.sol";

contract Freezable is Pausable, ManagerRole {
    using SafeMath for uint256;
    using EnumerableSet for EnumerableSet.AddressSet;

    event Freeze(address indexed from, uint256 value);
    event Unfreeze(address indexed from, uint256 value);

    struct Holder {
        string tronAddress;
        uint256 amount;
    }

    // Tokens holdtime
    uint256 public _holdtime = 1 days;
    // Unit for rate calculation
    uint256 private UNIT = (10**uint256(18));
    // Address token ERC20 STARS
    address private _token;

    // Pool members
    EnumerableSet.AddressSet private _holders;
    // Members freeze timestamp
    mapping(address => uint256) private _timers;
    // Members freeze amount
    mapping(address => Holder) private _freezes;

    /**
     * @dev Initialize the contract
     * @param token - Address of the BountyHUB ERC20
     */
    constructor(address token) public {
        _token = token;
    }

    /**
     * @dev Freeze amount of tokens
     * @param value - The amount to be frozen
     * @return a boolean value indicating whether the operation succeeded
     *
     * Emits {Freeze} event.
     */
    function freeze(string memory tron, uint256 value)
        public
        virtual
        whenNotPaused
        returns (bool)
    {
        _holders.add(msg.sender);
        _freezes[msg.sender].tronAddress = tron;
        _freezes[msg.sender].amount = _freezes[msg.sender].amount.add(value);
        _timers[msg.sender] = now;

        IERC20(_token).transferFrom(msg.sender, address(this), value);

        emit Freeze(msg.sender, value);
        return true;
    }

    /**
     * @dev Unfreeze all frozen tokens
     * @return a boolean value indicating whether the operation succeeded
     *
     * Emits {Unreeze} event.
     */
    function unfreeze() public virtual returns (bool) {
        /* require(now > _timers[msg.sender] + _holdtime, "Freezable: frozen tokens are locked"); */
        require(
            now > _timers[msg.sender].add(_holdtime),
            "Freezable: frozen tokens are locked"
        );

        uint256 value = _freezes[msg.sender].amount;

        _freezes[msg.sender].amount = 0;
        _timers[msg.sender] = 0;

        IERC20(_token).transfer(msg.sender, value);

        emit Unfreeze(msg.sender, value);
        return true;
    }

    /**
     * @dev Set address token STARS
     * @return a boolean value indicating whether the operation succeeded
     */
    function setToken(address token) public virtual onlyManager returns (bool) {
        require(token != address(0), "Freezeble: address token zero");

        _token = token;
        return true;
    }

    /**
     * @dev Set holdtime
     * @return a boolean value indicating whether the operation succeeded
     */
    function setHoldtime(uint256 holdtime)
        public
        virtual
        onlyManager
        returns (bool)
    {
        _holdtime = holdtime;
        return true;
    }

    /**
     * @dev Set holdtime
     * @return a boolean value indicating whether the operation succeeded
     */
    function pause() public virtual onlyManager returns (bool) {
        _pause();
        return true;
    }

    /**
     * @dev Set holdtime
     * @return a boolean value indicating whether the operation succeeded
     */
    function unpause() public virtual onlyManager returns (bool) {
        _unpause();
        return true;
    }

    /**
     * @dev Update info holder
     * @return a boolean value indicating whether the operation succeeded
     */
    function updateHolder(string memory tronAddress) public virtual returns (bool) {
        _freezes[msg.sender].tronAddress = tronAddress;
        return true;
    }

    /**
     * @dev Get tron address holder
     */
    function getHolderInfo(address owner) public virtual view returns (string memory addressTron, uint256 amount) {
        return (_freezes[owner].tronAddress, _freezes[owner].amount);
    }

    /**
     * @dev Get total frozen tokens
     * @return The amount of frozen tokens
     */
    function totalFrozen() public virtual view returns (uint256) {
        uint256 total = 0;
        uint256 length = _holders.length();
        for (uint256 i = 0; i < length; i++) {
            total = total.add(_freezes[_holders.at(i)].amount);
        }
        return total;
    }

    /**
     * @dev Get balance available to payouts
     * @return The amount of TRX
     */
    function totalBank() public virtual view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @dev Get total frozen tokens rate
     * @return The rate of frozen tokens
     */
    function totalFrozenRate() public virtual view returns (uint256) {
        uint256 totalSupply = IERC20(_token).totalSupply();
        if (totalSupply > 0) {
            return (totalFrozen() * UNIT) / totalSupply;
        } else {
            return totalSupply;
        }
    }

    /**
     * @dev Gets the frozen tokens rate of the specified address
     * @param owner - The address to query the share of
     * @return The share of passed address
     */
    function shareOf(address owner) public virtual view returns (uint256) {
        uint256 frozen = totalFrozen();
        if (frozen > 0) {
            return (freezeOf(owner) * UNIT) / frozen;
        } else {
            return frozen;
        }
    }

    /**
     * @dev Gets the frozen tokens of the specified address
     * @param owner - The address to query the frozen of
     * @return The amount of frozen tokens for passed address
     */
    function freezeOf(address owner) public virtual view returns (uint256) {
        return _freezes[owner].amount;
    }

    /**
     * @dev Gets the timestamp for tokens freeze of the specified address
     * @param owner - The address to query the timestamp
     * @return The timestamp of tokens freeze for passed address
     */
    function freezeTimeOf(address owner) public virtual view returns (uint256) {
        return _timers[owner];
    }

    /**
     * @dev Gets the unfreeze tokens date of the specified address
     * @param owner - The address to query the date
     * @return The unfreeze tokens date for passed address
     */
    function unfreezeTimeOf(address owner)
        public
        virtual
        view
        returns (uint256)
    {
        return _timers[owner].add(_holdtime);
    }

    /**
     * @dev Returns the number of holders
     */
    function getHoldersCount() public virtual view returns (uint256) {
        return _holders.length();
    }

    /**
     * @dev Returns holder by position. `index` must be a
     * value between 0 and {getHoldersCount}, non-inclusive.
     */
    function getHolder(uint256 index) public virtual view returns (address) {
        return _holders.at(index);
    }

    /**
     * @dev Gets contract title
     */
    function title() public virtual pure returns (string memory) {
        return "Freezable";
    }

    /**
     * @return The token address
     */
    function getToken() public virtual view returns (address) {
        return _token;
    }

    /// DEBUG routines

    function isGap() public virtual view returns (bool) {
        return totalFrozen() != IERC20(_token).balanceOf(address(this));
    }

    function getBlock() public virtual view returns (uint256) {
        return block.number;
    }

    function getNow() public virtual view returns (uint256) {
        return now;
    }
}

