contract FlashTrader is AccessControl, IUniswapV2Callee {
    using SafeMath for uint256;

    enum FlashSwapType {Simple, Cross}

    ChiToken constant public chi = ChiToken(0x0000000000004946c0e9F43F4Dee607b0eF1fA1c);
    address private constant WETH_ADDRESS = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    IWETH private constant WETH = IWETH(WETH_ADDRESS);

    address private _flashSwapPermissionedPairAddress = address(1);

    bytes32 public constant TRADER_ROLE = keccak256("TRADER_ROLE");

    constructor() public {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(TRADER_ROLE, msg.sender);
    }


    modifier onlyTrader() {
        require(hasRole(TRADER_ROLE, msg.sender), '1');
        _;
    }

    modifier onlyAdmin() {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), '2');
        _;
    }

    modifier discountCHI {
        uint256 gasStart = gasleft();

        _;

        uint256 gasSpent = 21000 + gasStart - gasleft() + 16 * msg.data.length;
        chi.freeFromUpTo(address(this), (gasSpent + 14154) / 41947);
    }


    //based on Uniswap router functionality
    function _swapTokensToTokens(
        address[] memory path,
        uint[] memory amounts,
        address _to,
        address factory
    ) internal {

        TransferHelper.safeTransfer(path[0], UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]);

        for (uint i; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            (address token0,) = UniswapV2Library.sortTokens(input, output);
            uint amountOut = amounts[i + 1];
            (uint amount0Out, uint amount1Out) = input == token0 ? (uint(0), amountOut) : (amountOut, uint(0));
            address to = i < path.length - 2 ? UniswapV2Library.pairFor(factory, output, path[i + 2]) : _to;
            IUniswapV2Pair(UniswapV2Library.pairFor(factory, input, output)).swap(amount0Out, amount1Out, to, new bytes(0));
        }
    }

    function _swapTokensToTokensSkipFirst(
        address[] memory path,
        uint[] memory amounts,
        address _to,
        address factory
    ) internal {

        TransferHelper.safeTransfer(
            path[1], UniswapV2Library.pairFor(factory, path[1], path[2]), amounts[1]
        );

        for (uint i = 1; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            (address token0,) = UniswapV2Library.sortTokens(input, output);
            uint amountOut = amounts[i + 1];
            (uint amount0Out, uint amount1Out) = input == token0 ? (uint(0), amountOut) : (amountOut, uint(0));
            address to = i < path.length - 2 ? UniswapV2Library.pairFor(factory, output, path[i + 2]) : _to;
            IUniswapV2Pair(UniswapV2Library.pairFor(factory, input, output)).swap(amount0Out, amount1Out, to, new bytes(0));
        }
    }

    function flashTradeArb(
        address factoryAddress,
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        uint256 amountToMiner
    ) external payable onlyTrader() {
        uint[] memory amountsOutEst = UniswapV2Library.getAmountsOut(factoryAddress, amountIn, path);

        require(amountsOutEst[amountsOutEst.length - 1] >= amountOutMin, '3');

        address tokenToRepay = path[0];
        address tokenToBorrow = path[1];

        uint amountToBorrow = amountsOutEst[1];

        bytes memory userParams = abi.encode(
            FlashSwapType.Simple,
            factoryAddress,
            amountsOutEst,
            path,
            amountToMiner
        );

        address borrowPair = IUniswapV2Factory(factoryAddress).getPair(tokenToBorrow, tokenToRepay);
        _startFlashSwap(amountToBorrow, tokenToBorrow, borrowPair, userParams);
    }

      function flashTradeArbChi(
        address factoryAddress,
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        uint256 amountToMiner
    ) external payable onlyTrader() discountCHI {
        uint[] memory amountsOutEst = UniswapV2Library.getAmountsOut(factoryAddress, amountIn, path);

        require(amountsOutEst[amountsOutEst.length - 1] >= amountOutMin, '3');

        address tokenToRepay = path[0];
        address tokenToBorrow = path[1];

        uint amountToBorrow = amountsOutEst[1];

        bytes memory userParams = abi.encode(
            FlashSwapType.Simple,
            factoryAddress,
            amountsOutEst,
            path,
            amountToMiner
        );

        address borrowPair = IUniswapV2Factory(factoryAddress).getPair(tokenToBorrow, tokenToRepay);
        _startFlashSwap(amountToBorrow, tokenToBorrow, borrowPair, userParams);
    }

    function flashTradeArbCross(
        uint256 amountIn,
        uint256 amountOutMin,
        address factoryOne,
        address factoryTwo,
        address[] calldata pathOne,
        address[] calldata pathTwo,
        uint256 amountToMiner
    ) external payable onlyTrader() {
        uint[] memory amountsOneEst = UniswapV2Library.getAmountsOut(factoryOne, amountIn, pathOne);
        uint[] memory amountsTwoEst = UniswapV2Library.getAmountsOut(factoryTwo, amountsOneEst[amountsOneEst.length - 1], pathTwo);

        require(amountsTwoEst[amountsTwoEst.length - 1] >= amountOutMin, '3');

        bytes memory userParams = abi.encode(
            FlashSwapType.Cross,
            factoryTwo,
            amountsTwoEst,
            pathTwo,
            amountToMiner
        );

        address borrowPair = IUniswapV2Factory(factoryOne).getPair(pathOne[1], pathOne[0]);
        _startFlashSwap(amountsOneEst[amountsOneEst.length - 1], pathOne[1], borrowPair, userParams);
    }

    function flashTradeArbCrossChi(
        uint256 amountIn,
        uint256 amountOutMin,
        address factoryOne,
        address factoryTwo,
        address[] calldata pathOne,
        address[] calldata pathTwo,
        uint256 amountToMiner
    ) external payable onlyTrader() discountCHI {
        uint[] memory amountsOneEst = UniswapV2Library.getAmountsOut(factoryOne, amountIn, pathOne);
        uint[] memory amountsTwoEst = UniswapV2Library.getAmountsOut(factoryTwo, amountsOneEst[amountsOneEst.length - 1], pathTwo);

        require(amountsTwoEst[amountsTwoEst.length - 1] >= amountOutMin, '3');

        bytes memory userParams = abi.encode(
            FlashSwapType.Cross,
            factoryTwo,
            amountsTwoEst,
            pathTwo,
            amountToMiner
        );

        address borrowPair = IUniswapV2Factory(factoryOne).getPair(pathOne[1], pathOne[0]);
        _startFlashSwap(amountsOneEst[amountsOneEst.length - 1], pathOne[1], borrowPair, userParams);
    }


    function _startFlashSwap(
        uint amountToBorrow,
        address tokenToBorrow,
        address borrowPair,
        bytes memory userParams
    ) private onlyTrader() {
        address token0 = IUniswapV2Pair(borrowPair).token0();
        address token1 = IUniswapV2Pair(borrowPair).token1();

        address tokenToPay;

        uint amount0Out = 0;
        uint amount1Out = 0;

        if (tokenToBorrow == token0) {
            amount0Out = amountToBorrow;
            tokenToPay = token1;
        } else {
            amount1Out = amountToBorrow;
            tokenToPay = token0;
        }

        bytes memory userData = abi.encode(
            amountToBorrow,
            tokenToBorrow,
            tokenToPay,
            borrowPair,
            userParams
        );

        _flashSwapPermissionedPairAddress = borrowPair;

        //borrow
        IUniswapV2Pair(borrowPair).swap(amount0Out, amount1Out, address(this), userData);
    }

    function uniswapV2Call(address _sender, uint _amount0, uint _amount1, bytes calldata _data) external override {
        // access control
        require(_sender == address(this), '5');

        // ensure that msg.sender is a V2 pair
        assert(msg.sender == _flashSwapPermissionedPairAddress);

        (
        uint amountBorrowed,
        address tokenBorrowed,
        address tokenPay,
        address borrowPair,
        bytes memory userParams
        ) = abi.decode(_data, (uint, address, address, address, bytes));

        uint pairBalanceTokenBorrow = IERC20(tokenBorrowed).balanceOf(borrowPair);
        uint pairBalanceTokenPay = IERC20(tokenPay).balanceOf(borrowPair);

        uint amountToRepay = ((1000 * pairBalanceTokenPay * amountBorrowed) / (997 * pairBalanceTokenBorrow)) + 1;

        (
        FlashSwapType flashSwapType,
        address factoryTwo,
        uint[] memory amountsTwoEst,
        address[] memory pathTwo,
        uint amountToMiner
        ) = abi.decode(userParams, (FlashSwapType, address, uint[], address[], uint));

        TransferHelper.safeApprove(tokenBorrowed, msg.sender, amountBorrowed);

        if (flashSwapType == FlashSwapType.Cross) {
            _swapTokensToTokens(pathTwo, amountsTwoEst, address(this), factoryTwo);
        } else {
            _swapTokensToTokensSkipFirst(pathTwo, amountsTwoEst, address(this), factoryTwo);
        }

        //repay loan
        TransferHelper.safeTransfer(
            tokenPay,
            borrowPair,
            amountToRepay
        );

        if (tokenPay == WETH_ADDRESS) {
            WETH.withdraw(amountsTwoEst[amountsTwoEst.length - 1] - amountToRepay);

            //send to miner if need be - only works with ETH.
            if (amountToMiner > 0) {
                block.coinbase.transfer(amountToMiner);
            }
        }
    }

    /**
     * Withdraw ETH
     */

    /**
    * Withdraw tokens from the contract with the option to leave some
    * so the contract stays operational (withdraw profits only).
    */
    /*
        Approve router to handle an ERC20 amount on behalf of the contract
    */
    function approve(
