    ) external onlyAdmin() {

        IUniswapV2Router02(routerAddress).swapExactTokensForTokens(
            amountIn,
            amountOutMin,
            path,
            recipient,
            deadline
        );
    }

    function sendTokens(address token, uint256 sendAmount, address recipient) external onlyAdmin() {
        TransferHelper.safeTransfer(
            token,
            recipient,
            sendAmount
        );
    }

    function sendETH(uint256 sendAmount, address recipient) external onlyAdmin() {
        TransferHelper.safeTransferETH(recipient, sendAmount);
    }

    function mintChi(uint256 quantity) external onlyAdmin() {
        chi.mint(quantity);
    }

    function approveChi(uint256 amount) external onlyAdmin() {
        chi.approve(address(this), amount);
    }

    function revokeRole(bytes32 role, address account) public override {
        require(
            role != DEFAULT_ADMIN_ROLE,
