pragma solidity ^0.6.12;

import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v3.1.0/contracts/token/ERC20/IERC20.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v3.1.0/contracts/math/SafeMath.sol";

/**
 * @dev Aragon DAO token manger.
 */
interface ITokenManager {
    function mint(address _receiver, uint256 _amount) external;
    function burn(address _holder, uint256 _amount) external;
}

/**
 * @dev Robonomics DAO minter contract.
 *      This contract uses simple quadratic voting rule to mint DAO vote token from XRT.
 */
contract Minter {
    using SafeMath for uint256;

    IERC20 public xrt = IERC20(0x7dE91B204C1C737bcEe6F000AAA6569Cf7061cb7);
    IERC20 public vote = IERC20(0xbc4Bcd65BFE63D29d2463FC21f0d3a6206e301CD);
    ITokenManager public manager = ITokenManager(0x8CD31EEb03421F2fB62f067cfF86c85501fb84a6);

    /**
     * @dev Simple quadratic voting implementation.
     */
    function votes_to_xrt(uint256 _amount) public pure returns (uint256 vote_amount, uint256 xrt_amount) {
        uint256 scaled = _amount.div(10**18);
        vote_amount = scaled.mul(10**18);
        xrt_amount = scaled.mul(scaled).mul(10**9);
    }

    /**
     * @dev Mint DAO tokens and lock XRT.
     * @param _amount How many DAO tokens should be generated.
     * @notice Sender account must have no votes tokens.
     */
    function mint(uint256 _amount) external {
        require(vote.balanceOf(msg.sender) == 0);
        (uint256 vote_amount, uint256 xrt_amount) = votes_to_xrt(_amount);
        require(xrt.transferFrom(msg.sender, address(this), xrt_amount));
        manager.mint(msg.sender, vote_amount);
        require(vote.balanceOf(msg.sender) == vote_amount);
    }

    /**
     * @dev Leave DAO and release XRT.
     * @notice All votes tokens will be destroyed.
     */
    function exit() external {
        (uint256 vote_amount, uint256 xrt_amount) = votes_to_xrt(vote.balanceOf(msg.sender));
        require(xrt.transfer(msg.sender, xrt_amount));
        manager.burn(msg.sender, vote_amount);
        require(vote.balanceOf(msg.sender) == 0);
    }
}

