interface NtentArt {
   function mint(address _to, uint256 _projectId, address _by) external returns(uint256);
   function burn(address ownerAddress, uint256 _tokenId) external returns(bool);
   function getPricePerTokenInWei(uint256 _projectId) external returns(uint256);
   function projectTokenInfo(uint256 _projectId) external returns(address artistAddress, uint256 pricePerTokenInWei, uint256 invocations, uint256 maxInvocations, bool active, address purchaseContract, address dataContract, address tokenUriContract, bool acceptsMintPass, uint256 mintPassProjectId);
   function tokenIdToProjectId(uint256 _tokenId) external returns(uint256);
   function ownerOf(uint256 _tokenId) external returns (address);
   function ntentPercentage() external returns(uint256);
}

contract NtentPurchaseMinimal is Ownable {
    using SafeMath for uint256;
    
    NtentArt public ntentContract;
    address public ntentTokenContractAddress;
    address public ntentSustainabilityFundAddress;
    address public ntentCollectiveWalletAddress;
    
    uint256 collectivePercentage = 10;
    uint256 sustainabilityFundPercentage = 10;
    
    uint256 maxTeamMintsPerProject = 50;
    mapping(uint256 => uint256) public projectTeamMints;
    mapping(uint256 => mapping(address => bool)) public projectRainbowList;
    mapping(uint256 => bool) public isRainbowListedMinting;

    constructor (address _ntentTokenAddress, address _ntentFundAddress, address _ntentCollectiveWalletAddress) {
        ntentTokenContractAddress = _ntentTokenAddress;
        ntentSustainabilityFundAddress = _ntentFundAddress;
        ntentCollectiveWalletAddress = _ntentCollectiveWalletAddress;
    }
    
    function updateNtentTokenAddress(address _newAddress) public onlyOwner {
        ntentTokenContractAddress = _newAddress;
    }
        
    function updateNtentSustainabilityFundAddress(address _newAddress) public onlyOwner {
        ntentSustainabilityFundAddress = _newAddress;
    }
    
    function updateNtentCollectiveWalletAddress(address _newAddress) public onlyOwner {
        ntentCollectiveWalletAddress = _newAddress;
    }

    function withdraw() public onlyOwner {
        require(ntentSustainabilityFundAddress != address(0), "Sustainability Fund Address not set.");
        require(ntentCollectiveWalletAddress != address(0), "Collective Wallet Address not set.");
        
        uint256 balance = address (this).balance;
        
        require(balance > 0, "There's nothing to withdraw");
        
        ntentContract = NtentArt(ntentTokenContractAddress);
        
        uint256 fundAmount = balance / sustainabilityFundPercentage;
        uint256 collectiveAmount = balance / ntentContract.ntentPercentage();
        
        payable(ntentSustainabilityFundAddress).transfer(fundAmount);
        payable(ntentCollectiveWalletAddress).transfer(collectiveAmount);
        payable(msg.sender).transfer(balance - fundAmount - collectiveAmount);
    }
    
    function toggleRainbowListedMinting(uint256 _projectId, bool _usingRainbowList) public onlyOwner {
        isRainbowListedMinting[_projectId] = _usingRainbowList;
    }
    
    function addMembersToRainbowList(uint256 _projectId, address[] memory users)  public onlyOwner {
        for(uint256 i = 0;  i < users.length; i++){
            projectRainbowList[_projectId][users[i]] = true;
        }
    }
    
    function teamMint(uint256 _projectId, uint256 _tokenQuantity) public onlyOwner {
        require(_tokenQuantity > 0, "Quantity must be greater than zero");
        require(_tokenQuantity <= maxTeamMintsPerProject, "Exceeds max allowed");
        
        ntentContract = NtentArt(ntentTokenContractAddress);   
        
        for(uint256 i = 0; i < _tokenQuantity; i++){
            require(projectTeamMints[_projectId] < maxTeamMintsPerProject, "Team has hit max allowed mints");
            
            (uint256 tokenId) = ntentContract.mint(msg.sender, _projectId, msg.sender);
            require(tokenId > 0, "Mint failed.");
            
            projectTeamMints[_projectId] = projectTeamMints[_projectId] + 1;
        }
    }

    function purchase(
        address _purchasedForAddress, 
        uint256 _projectId, 
        uint _numberOfTokens) public payable {
            
        if(isRainbowListedMinting[_projectId] == true){
            require(projectRainbowList[_projectId][msg.sender] == true, "Address not rainbowlisted");
        } 
        
        ntentContract = NtentArt(ntentTokenContractAddress);
    
        uint256 tokenPrice = ntentContract.getPricePerTokenInWei(_projectId);
        require(tokenPrice.mul(_numberOfTokens) <= msg.value, "Ether value sent is not correct");
        
        for(uint256 i = 0; i < _numberOfTokens; i++) {
            (uint256 tokenId) = ntentContract.mint(_purchasedForAddress,  _projectId, msg.sender);
            require(tokenId > 0, "Mint failed.");
        }
    }
}
