        uint256 lpAccumulationFee = _originAmount.mul(LP_ACCUMULATION_FEE).div(
            1000
        );
        uint256 _amount = _originAmount.sub(lpAccumulationFee);

        if (_amount > 0) {
            pool.token.safeTransferFrom(
