// File: XChainTrade.sol
// contracts/XChainTrade.sol

contract XChainTrade { 
    mapping(address => uint256) public balances;
    ERC1155 token = ERC1155(0x930a9eb59E15Cd875532B26AEF73B006D4B82779); // This is the ERC 1155 NFT contract for XChain (you can find it also at xchainnft.eth)

   function currentPrice() public view returns(uint256) {
       uint256 price = 1 ether;
       if (block.timestamp < 1647302400) {price = 1 ether;} // if before start of sale period 15/03/2022
       else if (block.timestamp > 1695686400) {price = 49.56144107 ether;} // if after end of sale period 26/09/2023
       else { 
           for (uint i=0; i < ((block.timestamp - 1647302400) / uint256(604800)); i++) { // given 48384000 epoch difference from start to end, and / by 80 weeks = 604800
                price = price + ((price * 5) / 100); // price increase by 5 percent per week
           } 
       }
       return price;
   }

    function purchase() public payable {

        XChain c = XChain(0x930a9eb59E15Cd875532B26AEF73B006D4B82779); // This is the ERC 1155 NFT contract for XChain (you can find it also at xchainnft.eth)

        uint minted = c.totalSupply(0);
        uint256 incwei = msg.value;
        uint256 price = currentPrice();
        uint256 spent = 0;
        uint toBeMinted = 0;
        uint lockUntil = 0;
        bool done = false;

        // the contract will terminate if one of the following 3 checks fails
        require(minted < 50000, "Sold Out: Sorry all the XChain NFTs have been sold!"); // Maximum 5000 ID 0 NFTs will be minted
        require(block.timestamp >= 1642204800, "XChain: NFT presale not started yet!"); // If someone try to purchase a package before 15 January 2022
        require(incwei >= price, "XChain: Not enough funds to perform a purchase!"); // if somoone sends less ETH than the price of a single NFT

        // The loop below is the heart of the token sale. The amount sent to the contract is looped and used to get the best deals first.
        // For example, sending 362 ETH March 23th 20200 (when pre-sale is still open, 1 NFT price is ETH 1.05, and sale is open too) the loop will generate
        // the purchase of a ETH 200 packet, thus 400 NFTs,
        // the purchase of a ETH 100 packet, thus +150 NFTs,
        // the purchase of a ETH 50 packet, thus +65 NFTs,
        // the purchase of a ETH 10 packet, thus +11 NFTs,
        // the purchase of a single NFT for 1.05 ETH,
        // for a total of 626 NFTs, leaving a balance of 0.95 ETH to the address

        while (incwei >= price || done) { // keeps looping until one of the below conditions is met
            if (incwei >= 200000000000000000000 && minted + 400 <= 5000 && block.timestamp < 1656547200) { // if someone sends more than 200 ETH and the date is before June 30, 2022 
                spent = spent + 200000000000000000000; // adds 200 ETH to the bill
                incwei = incwei - 200000000000000000000; // removes 200 ETH from the budget
                toBeMinted = toBeMinted + 400; // adds 400 NFTs to the amount of NFTs to be minted
                lockUntil = block.timestamp + 3888000; // lock the account for 45 days since the current time
            } else if (incwei >= 100000000000000000000 && minted + 150 <= 5000 && block.timestamp < 1656547200) { // else if someone sends more than 100 ETH and the date is before June 30, 2022 
                spent = spent + 100000000000000000000; // adds 100 ETH to the bill
                incwei = incwei - 100000000000000000000; // removes 100 ETH from the budget
                toBeMinted = toBeMinted + 150; // adds 150 NFTs to the amount of NFTs to be minted
                if (lockUntil < block.timestamp + 2592000) {lockUntil = block.timestamp + 2592000;}
            } else if (incwei >= 50000000000000000000 && minted + 65 <= 5000 && block.timestamp < 1656547200) { // else if someone sends more than 50 ETH and the date is before June 30, 2022 
                spent = spent + 50000000000000000000; // adds 50 ETH to the bill
                incwei = incwei - 50000000000000000000; // removes 50 ETH from the budget
                toBeMinted = toBeMinted + 65; // adds 65 NFTs to the amount of NFTs to be minted
                if (lockUntil < block.timestamp + 1728000) {lockUntil = block.timestamp + 1728000;}
            } else if (incwei >= 10000000000000000000 && minted + 11 <= 5000 && block.timestamp < 1656547200) { // else if someone sends more than 10 ETH and the date is before June 30, 2022 
                spent = spent + 10000000000000000000; // adds 10 ETH to the bill
                incwei = incwei - 10000000000000000000; // removes 10 ETH from the budget
                toBeMinted = toBeMinted + 11; // adds 11 NFTs to the amount of NFTs to be minted
                if (lockUntil < block.timestamp + 864000) {lockUntil = block.timestamp + 864000;}
            } else if (incwei >= price && minted + 1 <= 5000 && block.timestamp > 1647302400) { // else if someone sends more than current NFT price and the date is after March 15, 2022 
                spent = spent + price; // adds the current price of 1 NFT to the bill
                incwei = incwei - price; // removes the current price of 1 NFT from the budget
                toBeMinted = toBeMinted + 1; // adds 1 NFT to the amount of NFTs to be minted
                if (lockUntil < 1643068800) {lockUntil = 1643068800;} // if account lock time is before Jan 25th 2022, set it at Jan 25th 2022
            } else {
                done = true; // if none of the previous conditions is met, exit the loop
            }
        }

        // the contract will terminate if one of the following 4 checks fails
        require(toBeMinted > 0, "XChain: Sorry you didn't manage to purchase any NFT!"); // if somehow someone has managed to go through the loop and come out with 0 NFTs to be minted
        require(toBeMinted + minted <= 5000, "XChain: Sorry your ourchase went above the total 5000 NFTs limit!"); // if someone managed to go through the loop and purchase more than the available NFTs
        require(msg.value == spent + incwei, "XChain: PANIC something wrong with money math. Exiting"); // if somehow the amount spent plus the change is different than the amount sent
        require(lockUntil > 0, "XChain: PANIC something wrong with lock time math. Exiting"); // if the date the account is locked is not being set

        balances[0x8b8E1624814975aD4D52BFFA7c38C05101675bB7] += spent; // XChainTech.eth gets the amount spent in its balance
        balances[msg.sender] += incwei; // the account that made the purchase gets the change in its balance

        c.mint(msg.sender, 0, toBeMinted, lockUntil); // the NFTs are minted and the account locked until the longest date.
    }

    // This is the function to withdraw funds from an account balance
    function withdraw(uint256 amount, address payable destAddr) public { 
        require(amount <= balances[msg.sender], "XChain: Not enough balance. Withdraw a lower amount.");
        balances[msg.sender] -= amount;
        destAddr.transfer(amount);
    }
}    

contract XChain {
    function mint(address to, uint256 id, uint256 amount, uint lockedUntilTimestamp) public virtual {}
    function totalSupply(uint256 id) public view virtual returns (uint256) {}
}
