contract DefiVesting is Ownable {
    using SafeMath for uint256;

    IERC20 public token;

    uint256 decimals = 18;

    bool mulsig1Signed = false;
    bool mulsig2Signed = false;
    bool mulsig3Signed = false;

    address[] public holders;

    mapping (address => Holding) public holdings;

    struct Holding {
        bool exists;
        uint256 monthlyTokens;
        uint256 totalMonths;
        uint256 availableFrom;
        uint256 monthlyWithdrawalsDone;
    }

    // Events
    event TokensReleased(address _to, uint256 _amount);

    // Wallets
    address privateSaleWallet = 0xF6c69A7b00f1C26C27D2Fdf91Bbe9C2e7806b9E4;
    address seedWallet = 0x08648056d95A2c216d52dc9eED85bF7AD5dCdd5A;
    address teamWallet = 0x837Af17c981553e82d6D887c11e79b9bb3127E1f;
    address marketingWallet = 0x04aFcfb83abBe0654550B1527073C391C1773CaF;
    address reserveWallet = 0x97b46703224926aF369bA3ee6F59e237fCCdde6c;
    address ecosystemWallet = 0xdfA445761a6c1cfBaa04874DE65B5b06edf7796A;

    address multisig1Wallet = 0x0DA23CEAce709e50d6Fcf712C7C99021109e2FDE;
    address multisig2Wallet = 0x01073C0150fdc46daC527BEFBfE10D27D3238e0E;
    address multisig3Wallet = 0x27252680D481DDCEaE9e44DD3E20490D985cfef2;

    constructor(address _token) public {
        token = IERC20(_token);

        holders.push(privateSaleWallet);
        holders.push(seedWallet);
        holders.push(teamWallet);
        holders.push(marketingWallet);
        holders.push(reserveWallet);

        _createVesting(privateSaleWallet, 80000 * 10 ** decimals, 5, now);
        _createVesting(seedWallet, 50000 * 10 ** decimals, 10, now);
        _createVesting(teamWallet, 75000 * 10 ** decimals, 20, now + 365 days);
        _createVesting(marketingWallet, 100000 * 10 ** decimals, 5, now);
        _createVesting(reserveWallet, 50000 * 10 ** decimals, 40, now + 180 days);
    }

    function getVestingByBeneficiary(address _beneficiary) external view returns (uint256 monthlyTokens, uint256 totalMonths, uint256 availableFrom, uint256 monthlyWithdrawalsDone) {
        require(holdings[_beneficiary].exists, "The holding doesn't exist");
        Holding memory holding = holdings[_beneficiary];
        monthlyTokens = holding.monthlyTokens;
        totalMonths = holding.totalMonths;
        availableFrom = holding.availableFrom;
        monthlyWithdrawalsDone = holding.monthlyWithdrawalsDone;
    }

    function setItIsLive() public {
        if (msg.sender == multisig1Wallet && !mulsig1Signed) {
            mulsig1Signed = true;
        }
        else if (msg.sender == multisig2Wallet && !mulsig2Signed) {
            mulsig2Signed = true;
        }
        else if (msg.sender == multisig3Wallet && !mulsig3Signed) {
            mulsig3Signed = true;
        }
        else {
            revert();
        }

        if (mulsig1Signed && mulsig2Signed && mulsig3Signed) {
            holders.push(ecosystemWallet);
            _createVesting(ecosystemWallet, 41666666666666666666666, 120, now);
        }
    }

    function getTotalMonthsToClaimThenByBeneficiary(address _beneficiary, uint256 _newDate) public view returns(uint256) {
        Holding memory holding = holdings[_beneficiary];

        uint256 today_year = Date.getYear(_newDate);
        uint256 today_month = Date.getMonth(_newDate);
        uint256 today_day = Date.getDay(_newDate);

        uint256 from_year = Date.getYear(holding.availableFrom);
        uint256 from_month = Date.getMonth(holding.availableFrom);
        uint256 from_day = Date.getDay(holding.availableFrom);

        uint256 yearsDifference = today_year.sub(from_year);

        uint256 totalMonths = yearsDifference.mul(12);

        if (today_month > from_month) {
            totalMonths = totalMonths.add(today_month).sub(from_month);
        }
        else {
            totalMonths = totalMonths.add(from_month).sub(today_month);
        }

        if (today_day < from_day) {
            totalMonths = totalMonths.sub(1);
        }

        if (totalMonths < holding.totalMonths) {
            return totalMonths.sub(holding.monthlyWithdrawalsDone);
        }

        return holding.totalMonths.sub(holding.monthlyWithdrawalsDone);
    }

    function getTotalMonthsToClaimNowByBeneficiary(address _beneficiary) public view returns(uint256) {
        return getTotalMonthsToClaimThenByBeneficiary(_beneficiary, now);
    }

    function getTotalToClaimThenByBeneficiary(address _beneficiary, uint256 _newDate) public view returns(uint256) {
        Holding memory holding = holdings[_beneficiary];
        uint256 months = getTotalMonthsToClaimThenByBeneficiary(_beneficiary, _newDate);
        return months.mul(holding.monthlyTokens);
    }

    function getTotalToClaimNowByBeneficiary(address _beneficiary) public view returns(uint256) {
        return getTotalToClaimThenByBeneficiary(_beneficiary, now);
    }

    function getTotalVested() public view returns(uint256) {
        uint256 total = 0;

        for (uint256 i = 0; i < holders.length; i++) {
            Holding memory holding = holdings[holders[i]];
            total = total.add(holding.monthlyTokens.mul(holding.totalMonths));
        }

        return total;
    }

    function getTotalClaimed() public view returns(uint256) {
        uint256 total = 0;

        for (uint256 i = 0; i < holders.length; i++) {
            Holding memory holding = holdings[holders[i]];
            total = total.add(holding.monthlyWithdrawalsDone.mul(holding.monthlyTokens));
        }

        return total;
    }

    function claimTokens() external
    {
        uint256 months = getTotalMonthsToClaimNowByBeneficiary(msg.sender);

        require(months > 0, "Nothing to claim");

        Holding memory holding = holdings[msg.sender];

        uint256 totalToClaim = months.mul(holding.monthlyTokens);

        holding.monthlyWithdrawalsDone = holding.monthlyWithdrawalsDone.add(months);

        holdings[msg.sender] = holding;

        require(token.transfer(msg.sender, totalToClaim), "Insufficient balance in vesting contract");
        emit TokensReleased(msg.sender, totalToClaim);
    }

    function _createVesting(address _beneficiary, uint256 _tokens, uint256 _totalMonths, uint256 _startFrom) internal {
        Holding memory holding = Holding(true, _tokens, _totalMonths, _startFrom, 0);
        holdings[_beneficiary] = holding;
    }
}
