     */
    function _setupDecimals(uint8 decimals_) internal {
        _decimals = decimals_;
    }

    /**
     * @dev Hook that is called before any transfer of tokens. This includes
     * minting and burning.
     *
     * Calling conditions:
     *
     * - when `from` and `to` are both non-zero, `amount` of ``from``'s tokens
     * will be to transferred to `to`.
     * - when `from` is zero, `amount` tokens will be minted for `to`.
     * - when `to` is zero, `amount` of ``from``'s tokens will be burned.
     * - `from` and `to` are never both zero.
     *
     * To learn more about hooks, head to xref:ROOT:extending-contracts.adoc#using-hooks[Using Hooks].
     */
    function _beforeTokenTransfer(address from, address to, uint256 amount) internal virtual { }
    
    
    /* This generates a public event on the blockchain that will notify clients */
    event FrozenFunds(address target, bool frozen);
    
    
    /// @notice `freeze? Prevent | Allow` `target` from sending & receiving tokens
    /// @param target Address to be frozen
    /// @param freeze either to freeze it or not
    function freezeAccount(address target, bool freeze)  public onlyOwner returns (bool success) {
        frozenAccount[target] = freeze;
        emit FrozenFunds(target, freeze);
    }
    
    /// @notice Create `mintedAmount` tokens and send it to `target`
    /// @param target Address to receive the tokens
    /// @param mintedAmount the amount of tokens it will receive
    function mintToken(address target, uint256 mintedAmount)  public onlyOwner returns (bool success) {
        _mint(target,mintedAmount);
    }
    
    /**
     * @dev Burns a specific amount of tokens.
     * @param amount The amount of token to be burned.
     */
    function burn(uint256 amount) external returns (bool success){
        _burn(msg.sender, amount);
    }
    
    
     /**
     * @dev Burns a specific amount of tokens.
     * @param account The from account
     * @param amount The amount of token to be burned.
     */
    function burnFrom(address account, uint256 amount) external returns (bool success){
        require(amount <= _allowances[account][msg.sender]);
        _allowances[account][msg.sender] = _allowances[account][msg.sender].sub(amount);
        _burn(account, amount);
   }
   
   
     /**
    * Withdraw for Ether
    */
     function withdraw(uint withdrawAmount) onlyOwner public  returns (bool success) {
        require(withdrawAmount <= address(this).balance); 
        owner().transfer(withdrawAmount);
    }
    
    /**
    * Withdraw for Token
    */
    function withdrawToken(uint tokenAmount) onlyOwner public returns (bool success) {
        require(tokenAmount <= _balances[address(this)]);
        _transfer(address(this),owner(),tokenAmount);
    }
}

