contracts/RealEstate.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Burnable.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Supply.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract RealEstate is ERC1155, ERC1155Burnable, ERC1155Supply {

    address private CONTRACT_OWNER;
    address private CONTRACT_LAWYER;
    using Counters for Counters.Counter;
    Counters.Counter public tokenIdCounter;
    using SafeMath for uint256;
    mapping(address => mapping(uint256 => bool)) private acceptedForBurn;

    uint256 private FEE = 20;

    struct Property {
        uint256 tokenId;
        uint256 amount;
        uint256 price;
        address owner;
        uint256 amountOnSale;
        bool isOnSale;
    }

    mapping(address => mapping(uint256 => Property)) properties;
    mapping(address => mapping(uint256 => bool)) tokenOwnerExists;

    event tokenCreated(address indexed minter, uint256 id, uint256 amount, string tokenUri, bool isUnlockable, string unlockable);
    event tradeOpened(address indexed seller, uint256 id, uint256 amount, uint256 price);
    event tradeClosed(address indexed seller, uint256 id, uint256 amount);
    event tradeSold(address indexed seller, address indexed buyer, uint256 id, uint256 amount, uint256 price);

    constructor(address _lawyer) ERC1155("https://apartchain.io/tokens/{contractAddress}/{id}") {
        CONTRACT_OWNER = msg.sender;
        CONTRACT_LAWYER = _lawyer;
        tokenIdCounter.increment();
    }

    modifier onlyContractLawyer {
        require(msg.sender == CONTRACT_LAWYER);
        _;
    }

    function changeFee(uint256 _fee) public {
        require(msg.sender == CONTRACT_OWNER);
        require(_fee > 0);
        FEE = _fee;
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override(ERC1155, ERC1155Supply) {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);

        for (uint i = 0; i < ids.length; i++) {
            Property storage property = properties[from][ids[i]];

            if (tokenOwnerExists[to][ids[i]]) {
                Property storage propertyTo = properties[to][ids[i]];
                propertyTo.amount += amounts[i];
            } else {
                properties[to][ids[i]] = Property(ids[i], amounts[i], properties[from][ids[i]].price, msg.sender, 0, false);
                tokenOwnerExists[to][ids[i]] = true;
            }

            if (property.amount == amounts[i]) {
                delete properties[from][ids[i]];
            } else {
                if(property.amount != 0) {
                    property.amount -= amounts[i];
                    property.amountOnSale -= amounts[i];
                }
            }
        }
    }

    function createToken(
        uint256 price,
        string memory uri,
        uint256 amount,
        bool isForSale,
        bool isUnlockable,
        string memory unlockable
    ) public {
        uint256 tokenId = tokenIdCounter.current();
        _mint(msg.sender, tokenId, amount, "");
        uint256 amountOnSale = isForSale ? amount : 0;
        properties[msg.sender][tokenId] = Property(tokenId, amount, price, msg.sender, amountOnSale, isForSale);
        tokenIdCounter.increment();
        emit tokenCreated(msg.sender, tokenId, amount, uri, isUnlockable, unlockable);
        if (isForSale) {
            emit tradeOpened(msg.sender, tokenId, amount, price);
        }
    }

    function buyTokens(address owner, uint256 tokenId, uint256 amount) public payable {
        require(owner != msg.sender, "You can't buy your own tokens");
        require(tokenId < tokenIdCounter.current(), "Token ID is not valid");
        require(balanceOf(owner, tokenId) >= amount, "Not enough tokens");
        require(properties[owner][tokenId].isOnSale, "Token is not for sale");
        require(properties[owner][tokenId].amountOnSale >= amount, "Not enough tokens for sale");
        uint256 value = amount * properties[owner][tokenId].price;
        uint256 fee = value.div(FEE); 
        uint256 valueWithFee = value.add(fee); // add 0.5% fee to value

        require(valueWithFee <= msg.value, "Not enough ETH");
        address payable tokenOwner = payable(owner);

        (bool sent, ) = tokenOwner.call{
            value: value
        }("");

        (sent, ) = payable(CONTRACT_OWNER).call{
            value: fee
        }("");

        require(sent);
        super._safeTransferFrom(owner, msg.sender, tokenId, amount, "");
        emit tradeSold(owner, msg.sender, tokenId, amount, properties[owner][tokenId].price);

    }


    function putOnSale(uint256 tokenId, uint256 amount, uint256 price) public {
        require(tokenId < tokenIdCounter.current(), "tokenid");
        require(amount > 0, "amount");
        require(price > 0, "price");
        require(balanceOf(msg.sender, tokenId) >= amount, "balance");
        Property storage property = properties[msg.sender][tokenId];
        uint256 availableForSale = property.amount - property.amountOnSale;
        require(availableForSale >= amount, "available");

        property.price = price;

        if (property.isOnSale){
            property.amountOnSale += amount;
        } else {
            property.amountOnSale = amount;
            property.isOnSale = true;
        }
        emit tradeOpened(msg.sender, tokenId, amount, price);
    }

    function removeFromSale(uint256 tokenId, uint256 amount) public {
        require(tokenId < tokenIdCounter.current(), "tokenid");
        require(amount > 0, "amount");
        require(balanceOf(msg.sender, tokenId) >= amount, "balance");
        Property storage property = properties[msg.sender][tokenId];
        require(property.amountOnSale >= amount, "available");
        property.amountOnSale -= amount;

        if (property.amountOnSale == 0) {
            property.isOnSale = false;
        }

        emit tradeClosed(msg.sender, tokenId, amount);
    }

    function sendDividends(uint256 id, address[] memory owners) public payable {
        require(id < tokenIdCounter.current(), "tokenid");
        require(msg.value > 0, "value");
        require(owners.length > 0, "owners");
        uint256 totalAmount = totalSupply(id);
        uint256 unit = msg.value.div(totalAmount);
        bool sent;

        for(uint i = 0; i < owners.length; i++) {
            require(owners[i] != msg.sender, "Your address");
            require(balanceOf(owners[i], id) > 0, "Token owner doesn't have tokens");
            require(owners[i] != address(0)); 

            (sent, ) = payable(owners[i]).call{
                value: balanceOf(owners[i], id).mul(unit)
            }("");

            require(sent);
        }
    }

    function burn(
        address account,
        uint256 id,
        uint256 value
    ) public virtual override {
        require(acceptedForBurn[account][id], "no access");
        super.burn(account, id, value);
    }

    function burnBatch(
        address account,
        uint256[] memory ids,
        uint256[] memory values
    ) public virtual override {
        for (uint i = 0; i < ids.length; i++) {
            require(acceptedForBurn[account][ids[i]], "no access");
        }
        super.burnBatch(account, ids, values);
    }

    function acceptBurn(address operator,  uint256[] memory ids) public onlyContractLawyer {
        for (uint i = 0; i < ids.length; i++) {
            acceptedForBurn[operator][ids[i]] = true;
        }
    }
}
