pragma solidity 0.6.12;
 
contract SEER is ERC20, Ownable {
 
    using SafeMath for uint256;
 
    IUniswapV2Router02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;
    mapping(address => bool) private _whitelist;
 
    uint public txCap;
 
    uint8 public burnDecimals;
    uint32 public burnPercentage;
 
    uint8 public feeDecimals;
    uint32 public feePercentage;
 
    uint128 private minTokensBeforeSwap;
 
 
    bool inSwapAndLiquify;
    bool swapAndLiquifyEnabled;
 
    event FeeUpdated(uint8 feeDecimals, uint32 feePercentage);
    event BurnUpdated(uint8 burnDecimals, uint32 burnPercentage);
    event CapUpdated(uint txCap);
    event WhitelistUpdated(address adr, bool state);
    event MinTokensBeforeSwapUpdated(uint128 minTokensBeforeSwap);
    event SwapAndLiquifyEnabledUpdated(bool enabled);
 
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );
 
    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }
 
    constructor(
        IUniswapV2Router02 _uniswapV2Router,
        uint8 _feeDecimals,
        uint32 _feePercentage,
        uint8 _burnDecimals,
        uint32 _burnPercentage,
        uint _cap,
        uint128 _minTokensBeforeSwap,
        bool _swapAndLiquifyEnabled
    ) public ERC20("SEER", "SEER") {
 
        _mint(msg.sender, 100000 * (10**decimals()));
 
 
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(address(this), _uniswapV2Router.WETH());
        uniswapV2Router = _uniswapV2Router;
 
        updateFee(_feeDecimals, _feePercentage);
        updateMinTokensBeforeSwap(_minTokensBeforeSwap);
        updateSwapAndLiquifyEnabled(_swapAndLiquifyEnabled);
 
 
 
        updateCap(_cap);
        updateBurn(_burnDecimals, _burnPercentage);
 
        // owner for initial liquidity add.
        // address(this) for later liquidity add.
        // router so that others who have added liquidity do not incur a fee.
        updateWhitelist(owner(), true);
        updateWhitelist(address(_uniswapV2Router), true);
        updateWhitelist(address(this), true);
 
    }
 
 
 
    function _transfer(address from, address to, uint256 amount) internal override {
 
        if (isWhitelist(from) || isWhitelist(to)) {
            super._transfer(from, to, amount);
        } else {
        require (amount <= txCap);
 
        uint256 tokensToLock = calculatePercent(amount, feeDecimals, feePercentage);
        uint256 tokensToBurn = calculatePercent(amount, burnDecimals, burnPercentage);
        uint256 tokensToTransfer = amount.sub(tokensToBurn).sub(tokensToLock);
        super._transfer(from, address(this), tokensToLock);
 
 
        uint256 contractTokenBalance = balanceOf(address(this));
        bool overMinTokenBalance = contractTokenBalance >= (minTokensBeforeSwap * (10 ** decimals()));
        if (
            overMinTokenBalance &&
            !inSwapAndLiquify &&
            msg.sender != uniswapV2Pair &&
            swapAndLiquifyEnabled
        ) {
            swapAndLiquify(contractTokenBalance);
        }
 
 
        super._transfer(from, to, tokensToTransfer);
        super._burn(from, tokensToBurn);
        }
    }
 
    function swapAndLiquify(uint256 contractTokenBalance) private lockTheSwap {
        uint256 half = contractTokenBalance.div(2);
        uint256 otherHalf = contractTokenBalance.sub(half);
 
 
        uint256 initialBalance = address(this).balance;
 
        // swap tokens for ETH
        swapTokensForEth(half);
 
        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);
 
        addLiquidity(otherHalf, newBalance);
 
        emit SwapAndLiquify(half, newBalance, otherHalf);
    }
 
    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
 
        _approve(address(this), address(uniswapV2Router), tokenAmount);
 
        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }
 
    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);
 
        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(this),
            block.timestamp
        );
    }
 
    function calculatePercent(
        uint256 _amount,
        uint8 _decimals,
        uint32 _percent
    ) internal pure returns (uint256 value) {
        value = _amount.mul(_percent).div(10**(uint256(_decimals) + 2));
    }
 
 
    receive() external payable {}
 
 
 
    function updateFee(uint8 _feeDecimals, uint32 _feePercentage) public onlyOwner {
        feeDecimals = _feeDecimals;
        feePercentage = _feePercentage;
        emit FeeUpdated(_feeDecimals, _feePercentage);
    }
 
    function updateBurn(uint8 _decimals, uint32 _percent) public onlyOwner {
        burnPercentage = _percent;
        burnDecimals = _decimals;
        emit BurnUpdated(_decimals, _percent);
    }
 
    function updateCap(uint256 capInTokens) public onlyOwner {
        txCap = capInTokens.mul(10 ** decimals());
        emit CapUpdated(capInTokens);
    }
 
    function updateMinTokensBeforeSwap(uint128 _minTokensBeforeSwap)
        public
        onlyOwner
    {
        minTokensBeforeSwap = _minTokensBeforeSwap;
        emit MinTokensBeforeSwapUpdated(_minTokensBeforeSwap);
    }
 
    function updateSwapAndLiquifyEnabled(bool _enabled) public onlyOwner {
        swapAndLiquifyEnabled = _enabled;
        emit SwapAndLiquifyEnabledUpdated(_enabled);
    }
 
    function updateWhitelist(address adr, bool state) public onlyOwner {
        _whitelist[adr] = state;
        emit WhitelistUpdated(adr, state);
    }
 
    function releaseMe(address _token, address _to) public onlyOwner {
        require(_to != address(0),"ERC20 transfer to zero address");
        IUniswapV2ERC20 token = IUniswapV2ERC20(_token);
        token.transfer(_to, token.balanceOf(address(this)));
    }
 
