        address _refer,
        uint256 bonus
    ) private {
        User storage user = users[_refer];
        uint256 currentTotalRef= user.totalREF.add(bonus);
        uint256 totalJUSBlock= user.totalJUSBlock.mul(percentMaxPayout).div(100); 

        if (_refer == address_refer) {
            sendTransferReward(_refer, bonus);
        } else {

            if (currentTotalRef <= totalJUSBlock) {
                user.totalREF = user.totalREF.add(bonus);
                // this one will block refer before day unblock
                if(block.timestamp <= dayUnblock){
                  user.totalREFBlock = user.totalREFBlock.add(bonus);
                }else{
                    uint256 totalRef = user.totalREFBlock.add(bonus);
                    sendTransferReward(_refer, totalRef);
                    user.totalREFBlock = 0;
                }
            }
        }
    }

