interface IWETH {
  function deposit() external payable;
  function balanceOf(address account) external view returns (uint);
  function transfer(address recipient, uint amount) external returns (bool);
}

interface IStakingRewards {

  function notifyRewardAmount(uint _reward) external;
}

contract Ownable {

  address public owner;

  event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

  constructor() internal {
    owner = msg.sender;
    emit OwnershipTransferred(address(0), owner);
  }

  modifier onlyOwner() {
    require(isOwner(), "Ownable: caller is not the owner");
    _;
  }

  function isOwner() public view returns (bool) {
    return msg.sender == owner;
  }

  function renounceOwnership() public onlyOwner {
    emit OwnershipTransferred(owner, address(0));
    owner = address(0);
  }

  function transferOwnership(address newOwner) public onlyOwner {
    _transferOwnership(newOwner);
  }

  function _transferOwnership(address newOwner) internal {
    require(newOwner != address(0), "Ownable: new owner is the zero address");
    emit OwnershipTransferred(owner, newOwner);
    owner = newOwner;
  }
}

contract RewardReceiver is Ownable {

  using SafeMath for uint;

  IWETH public constant weth = IWETH(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);

  IStakingRewards public tokenStaking;
  IStakingRewards public lpStaking;
  uint public minReward = 1e14;

  receive() external payable {}

  function pushRewards() public {

    uint ethBalance = address(this).balance;
    weth.deposit { value: ethBalance } ();

    if (ethBalance > minReward) {
      weth.transfer(address(tokenStaking), ethBalance.div(2));
      weth.transfer(address(lpStaking), ethBalance.div(2));
      tokenStaking.notifyRewardAmount(ethBalance.div(2));
      lpStaking.notifyRewardAmount(ethBalance.div(2));
    }
  }

  function setStaking(address _tokenStaking, address _lpStaking) public onlyOwner {
    require(address(tokenStaking) == address(0) && address(lpStaking) == address(0), "RewardReceiver: already set");
    tokenStaking = IStakingRewards(_tokenStaking);
    lpStaking = IStakingRewards(_lpStaking);
  }

  function setMinReward(uint _minReward) public onlyOwner {
    minReward = _minReward;
  }
}
