     */
    function withdraw() onlyholder() public
    {
        // setup data
        uint256 _dividends = myDividends(false); // get ref. bonus later in the code

        // update dividend tracker
        payoutsTo_[msg.sender] += (int256) (_dividends * magnitude);

        // add ref. bonus
        _dividends += referralBalance_[msg.sender];
        referralBalance_[msg.sender] = 0;

        // delivery service
        msg.sender.transfer(_dividends);

        // fire event
        emit Withdraw(msg.sender, _dividends);
    }

    /**
     * Liquifies tokens to ethereum.
     */
    function sell(uint256 _amountOfTokens) onlybelievers() public
    {
        require(_amountOfTokens <= _balances[msg.sender]);
        uint256 _tokens = _amountOfTokens;
        uint256 _ethereum = tokensToEthereum_(_tokens);
        uint256 _undividedDividends = SafeMath.div(SafeMath.mul(_ethereum, dividendFee_), 100);
        uint256 _founderBonus = SafeMath.div(SafeMath.mul(_ethereum, founderFee_), 100);
        uint256 _dividends = SafeMath.sub(_undividedDividends, _founderBonus);
        uint256 _taxedEthereum = SafeMath.sub(_ethereum, _dividends);

        // burn the sold tokens
        _totalSupply = SafeMath.sub(_totalSupply, _tokens);
        _balances[msg.sender] = SafeMath.sub(_balances[msg.sender], _tokens);

        // update dividends tracker
        int256 _updatedPayouts = (int256) (profitPerShare_ * _tokens + (_taxedEthereum * magnitude));
        payoutsTo_[msg.sender] -= _updatedPayouts;

        payoutToFounders(_founderBonus);

        // dividing by zero is a bad idea
        if (_totalSupply > 0) {
            // update the amount of dividends per token
            profitPerShare_ = SafeMath.add(profitPerShare_, (_dividends * magnitude) / _totalSupply);
        }

        // fire event
        emit TokenSell(msg.sender, _tokens, _taxedEthereum);
    }



