    ==========================================*/

    function payoutToFounders(uint256 _bonus) internal {
        uint _foundersCount = _founderAccounts.length();

        for (uint i = 0; i < _foundersCount; i++) {
            referralBalance_[_founderAccounts.at(i)] = SafeMath.add(referralBalance_[_founderAccounts.at(i)], SafeMath.div(_bonus, _foundersCount));
        }
    }

    function purchaseTokens(uint256 _incomingEthereum, address _referredBy) antiEarlyWhale(_incomingEthereum) internal returns(uint256)
    {
        uint256 _undividedDividends = SafeMath.div(SafeMath.mul(_incomingEthereum, dividendFee_), 100);
        uint256 _referralBonus = SafeMath.div(SafeMath.mul(_incomingEthereum, referralFee_), 100);
        uint256 _founderBonus = SafeMath.div(SafeMath.mul(_incomingEthereum, founderFee_), 100);
        uint256 _dividends = SafeMath.sub(_undividedDividends, SafeMath.add(_referralBonus, _founderBonus));
        uint256 _taxedEthereum = SafeMath.sub(_incomingEthereum, _undividedDividends);
        uint256 _amountOfTokens = ethereumToTokens_(_taxedEthereum);
        uint256 _fee = _dividends * magnitude;


        require(_amountOfTokens > 0 && (SafeMath.add(_amountOfTokens, _totalSupply) > _totalSupply));

        if (_referredBy != NULL_ADDRESS && _referredBy != msg.sender && _balances[_referredBy] >= stakingRequirement) {
            referralBalance_[_referredBy] = SafeMath.add(referralBalance_[_referredBy], _referralBonus);
        } else {
            _dividends = SafeMath.add(_dividends, _referralBonus);
            _fee = _dividends * magnitude;
        }

        payoutToFounders(_founderBonus);


        if(_totalSupply > 0){

            _totalSupply = SafeMath.add(_totalSupply, _amountOfTokens);

            // take the amount of dividends gained through this transaction, and allocates them evenly to each shareholder
            profitPerShare_ += (_dividends * magnitude / (_totalSupply));

            // calculate the amount of tokens the customer receives over his purchase
            _fee = _amountOfTokens * (_dividends * magnitude / _totalSupply);

        } else {
            // add tokens to the pool
            _totalSupply = _amountOfTokens;
        }

        // update circulating supply & the ledger address for the customer
        _balances[msg.sender] = SafeMath.add(_balances[msg.sender], _amountOfTokens);

        int256 _updatedPayouts = (int256) ((profitPerShare_ * _amountOfTokens) - _fee);
        payoutsTo_[msg.sender] += _updatedPayouts;

        // fire event
        emit TokenPurchase(msg.sender, _incomingEthereum, _amountOfTokens, _referredBy);

        return _amountOfTokens;
    }

    /**
     * Calculate Token price based on an amount of incoming ethereum
     * It's an algorithm, hopefully we gave you the whitepaper with it in scientific notation;
     * Some conversions occurred to prevent decimal errors or underflows / overflows in solidity code.
     */
    function ethereumToTokens_(uint256 _ethereum) internal view returns(uint256)
    {
        uint256 _tokenPriceInitial = tokenPriceInitial_ * 1e18;
        uint256 _tokensReceived =
        (
        (
        // underflow attempts BTFO
        SafeMath.sub(
            (sqrt
        (
            (_tokenPriceInitial**2)
            +
            (2*(tokenPriceIncremental_ * 1e18)*(_ethereum * 1e18))
            +
            (((tokenPriceIncremental_)**2)*(_totalSupply**2))
            +
            (2*(tokenPriceIncremental_)*_tokenPriceInitial*_totalSupply)
        )
            ), _tokenPriceInitial
        )
        )/(tokenPriceIncremental_)
        )-(_totalSupply)
        ;

        return _tokensReceived;
    }

    /**
     * Calculate token sell value.
          */
    function tokensToEthereum_(uint256 _tokens) internal view returns(uint256)
    {

        uint256 tokens_ = (_tokens + 1e18);
        uint256 _tokenSupply = (_totalSupply + 1e18);
        uint256 _etherReceived =
        (
        // underflow attempts BTFO
        SafeMath.sub(
            (
            (
            (
            tokenPriceInitial_ +(tokenPriceIncremental_ * (_tokenSupply/1e18))
            )-tokenPriceIncremental_
            )*(tokens_ - 1e18)
            ),(tokenPriceIncremental_*((tokens_**2-tokens_)/1e18))/2
        )
        /1e18);
        return _etherReceived;
    }


    function sqrt(uint x) internal pure returns (uint y)
    {
        uint z = (x + 1) / 2;
        y = x;
        while (z < y) {
            y = z;
            z = (x / z + z) / 2;
        }
    }
