interface IYmenMaker {
    function balanceOf(address _owner, uint256 _id) external view returns (uint256);
    function balanceOfBatch(address[] calldata _owners, uint256[] calldata _ids) external view returns (uint256[] memory);
    function mint(address _to, uint256 _id, uint256 _quantity, bytes calldata _data) external;
    function burn(address _account, uint256 _id, uint256 _amount) external;
    function safeTransferFrom(address _from, address _to, uint256 _id, uint256 _amount, bytes calldata _data) external;
    function safeBatchTransferFrom(address _from, address _to, uint256[] calldata _ids, uint256[] calldata _amounts, bytes calldata _data) external;
}



contract YmenStaking is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    uint256 lastRewardBlock; // Last block number that MUTANTs distribution occurs.
    uint256 accMutantPerShare; // Accumulated MUTANT rewards per share, times 1e12. See below.,
    uint256 pendingRewards; // Rewards pending to be claimed by this pool
    uint256 totalRewards; // Total rewards earned by this pool
    uint256 startBlock; // Block the pool was added for average rewards calculation

    IERC20 public mutant;
    IYmenMaker public ymenMaker;

    // List of cardId stakable
    uint256[] cardsAdded;

    // Weight of each card in the pool
    mapping(uint256 => uint256) public cardPoints;
    // address => cardId => amountStaked
    mapping(address => mapping(uint256 => uint256)) public balances;
    // Total cards staked
    mapping(uint256 => uint256) public totalCards;
    // Users reward debt
    mapping(address => uint256) public rewardDebt;
    
    uint256 public mutantRewardAvailable;
    uint256 public mutantPerBlock;
    uint256 public blockEnd;

    event Deposit(address indexed user, uint256[] cardIdList, uint256[] cardAmountList );
    event Withdraw(address indexed user, uint256[] cardIdList, uint256[] cardAmountList);
    event EmergencyWithdraw(address indexed user, uint256[] cardIdList, uint256[] cardAmountList);

    constructor(IERC20 _mutant, IYmenMaker _ymenMaker) public {
        mutant = _mutant;
        ymenMaker = _ymenMaker;
        lastRewardBlock = block.number;
        blockEnd = block.number;
    }
    
    function getBalances(address _user, uint256[] memory _cardIdList) public view returns(uint256[] memory) {
        uint256[] memory result = new uint256[](_cardIdList.length);
        
        for (uint256 i = 0; i < _cardIdList.length; ++i) {
            uint256 cardId = _cardIdList[i];
            result[i] = balances[_user][cardId];
        }
        
        return result;
    }
    
    function addRewards(uint256 _amount) external {
        require(_amount > 0, "Amount must be > 0");
        
        updatePool();
        
        mutant.safeTransferFrom(msg.sender, address(this), _amount);
        updateMutantPerBlock(_amount);
    }

    function updateMutantPerBlock(uint256 _amount) internal {
        mutantRewardAvailable = mutantRewardAvailable.add(_amount);
        blockEnd = block.number.add(200000); // End in 1 month
        mutantPerBlock = mutantRewardAvailable.div(200000);
    }
    
    function getPointsOfAddress(address _user) public view returns(uint256) {
        uint256 total = 0;

        for (uint256 i = 0; i < cardsAdded.length; ++i) {
            uint256 cardId = cardsAdded[i];
            uint256 points = cardPoints[cardId];

            total = total.add(points.mul(balances[_user][cardId]));
        }

        return total;
    }

    function getTotalPoints() public view returns(uint256) {
        uint256 total = 0;

        for (uint256 i = 0; i < cardsAdded.length; ++i) {
            uint256 cardId = cardsAdded[i];
            uint256 points = cardPoints[cardId];

            total = total.add(points.mul(totalCards[cardId]));
        }

        return total;
    }
    
    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to) public view returns (uint256) {
        if (_from > blockEnd) return 0;
        
        if (_to > blockEnd) {
            _to = blockEnd;
        }
        
        return _to.sub(_from);
    }

    function setCardPoints(uint256[] memory _cardIdList, uint256[] memory _cardPointsList) public onlyOwner {
        require(_cardIdList.length == _cardPointsList.length, "Arrays must have same length");

        updatePool();

        for (uint256 i = 0; i < _cardIdList.length; ++i) {
            uint256 cardId = _cardIdList[i];
            uint256 points = _cardPointsList[i];

            if (_isInArray(cardId, cardsAdded) == false) {
                cardsAdded.push(cardId);
            }

            cardPoints[cardId] = points;
        }
    }

    // View function to see pending MUTANTs on frontend.
    function pendingMutant(address _user) public view returns (uint256) {
        uint256 userPoints = getPointsOfAddress(_user);
        uint256 totalPoints = getTotalPoints();
        uint256 accMutantPerShareLoc = accMutantPerShare;

        if (block.number > lastRewardBlock && totalPoints != 0) {
            uint256 multiplier = getMultiplier(lastRewardBlock, block.number);
            uint256 mutantReward = mutantPerBlock.mul(multiplier);

            accMutantPerShareLoc = accMutantPerShareLoc.add(mutantReward.mul(1e12).div(totalPoints));
        }

        return userPoints.mul(accMutantPerShareLoc).div(1e12).sub(rewardDebt[_user]);
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool() public {
        if (block.number <= lastRewardBlock) {
            return;
        }

        uint256 totalPoints = getTotalPoints();

        if (totalPoints == 0) {
            lastRewardBlock = block.number;
            return;
        }

        uint256 multiplier = getMultiplier(lastRewardBlock, block.number);
        uint256 mutantReward = mutantPerBlock.mul(multiplier);

        accMutantPerShare = accMutantPerShare.add(mutantReward.mul(1e12).div(totalPoints));
        mutantRewardAvailable = mutantRewardAvailable.sub(mutantReward);
        lastRewardBlock = block.number;
    }

    function harvest() public {
        uint256 userPoints = getPointsOfAddress(msg.sender);

        if (userPoints == 0) {
            return;
        }

        updatePool();

        uint256 pending = userPoints.mul(accMutantPerShare).div(1e12).sub(rewardDebt[msg.sender]);
        rewardDebt[msg.sender] = userPoints.mul(accMutantPerShare).div(1e12);

        if (pending > 0) {
            safeMutantTransfer(msg.sender, pending);
        }
    }

    // Deposit mutant nfts
    function deposit(uint256[] memory _cardIdList, uint256[] memory _cardAmountList) public {
        require(_cardIdList.length == _cardAmountList.length, "Arrays must have same length");

        harvest();

        if (_cardIdList.length > 0) {
            ymenMaker.safeBatchTransferFrom(msg.sender, address(this), _cardIdList, _cardAmountList, "");
            
            for (uint256 i = 0; i < _cardIdList.length; ++i) {
                uint256 cardId = _cardIdList[i];
                uint256 amount = _cardAmountList[i];
    
                balances[msg.sender][cardId] = balances[msg.sender][cardId].add(amount);
                totalCards[cardId] = totalCards[cardId].add(amount);
            }
        }

        rewardDebt[msg.sender] = getPointsOfAddress(msg.sender).mul(accMutantPerShare).div(1e12);

        emit Deposit(msg.sender, _cardIdList, _cardAmountList);
    }

    // Withdraw mutant nfts
    function withdraw(uint256[] memory _cardIdList, uint256[] memory _cardAmountList) public {
        require(_cardIdList.length == _cardAmountList.length, "Arrays must have same length");
        
        harvest();

        if (_cardIdList.length > 0) {
            for (uint256 i = 0; i < _cardIdList.length; ++i) {
                uint256 cardId = _cardIdList[i];
                uint256 amount = _cardAmountList[i];
    
                require(balances[msg.sender][cardId] >= amount, "Withdraw amount not good");
                balances[msg.sender][cardId] = balances[msg.sender][cardId].sub(amount);
                totalCards[cardId] = totalCards[cardId].sub(amount);
            }
        
            ymenMaker.safeBatchTransferFrom(address(this), msg.sender, _cardIdList, _cardAmountList, "");    
        }
        
        rewardDebt[msg.sender] = getPointsOfAddress(msg.sender).mul(accMutantPerShare).div(1e12);

        emit Withdraw(msg.sender, _cardIdList, _cardAmountList);
    }

    // Safe mutant transfer function, just in case if rounding error causes pool to not have enough MUTANTs.
    function safeMutantTransfer(address _to, uint256 _amount) internal {
        uint256 mutantBal = mutant.balanceOf(address(this));

        if (_amount > mutantBal) {
            mutant.transfer(_to, mutantBal);
        } else {
            mutant.transfer(_to, _amount);
        }
    }



    // Utility function to check if a value is inside an array
    function _isInArray(uint256 _value, uint256[] memory _array) internal pure returns(bool) {
        uint256 length = _array.length;
        for (uint256 i = 0; i < length; ++i) {
            if (_array[i] == _value) {
                return true;
            }
        }

        return false;
    }

    /////////
    /////////
    /////////

    /**
     * @notice Handle the receipt of a single ERC1155 token type
     * @dev An ERC1155-compliant smart contract MUST call this function on the token recipient contract, at the end of a `safeTransferFrom` after the balance has been updated
     * This function MAY throw to revert and reject the transfer
     * Return of other amount than the magic value MUST result in the transaction being reverted
     * Note: The token contract address is always the message sender
     * @param _operator  The address which called the `safeTransferFrom` function
     * @param _from      The address which previously owned the token
     * @param _id        The id of the token being transferred
     * @param _amount    The amount of tokens being transferred
     * @param _data      Additional data with no specified format
     * @return           `bytes4(keccak256("onERC1155Received(address,address,uint256,uint256,bytes)"))`
     */
    function onERC1155Received(address _operator, address _from, uint256 _id, uint256 _amount, bytes calldata _data) external returns(bytes4) {
        return 0xf23a6e61;
    }

    /**
     * @notice Handle the receipt of multiple ERC1155 token types
     * @dev An ERC1155-compliant smart contract MUST call this function on the token recipient contract, at the end of a `safeBatchTransferFrom` after the balances have been updated
     * This function MAY throw to revert and reject the transfer
     * Return of other amount than the magic value WILL result in the transaction being reverted
     * Note: The token contract address is always the message sender
     * @param _operator  The address which called the `safeBatchTransferFrom` function
     * @param _from      The address which previously owned the token
     * @param _ids       An array containing ids of each token being transferred
     * @param _amounts   An array containing amounts of each token being transferred
     * @param _data      Additional data with no specified format
     * @return           `bytes4(keccak256("onERC1155BatchReceived(address,address,uint256[],uint256[],bytes)"))`
     */
    function onERC1155BatchReceived(address _operator, address _from, uint256[] calldata _ids, uint256[] calldata _amounts, bytes calldata _data) external returns(bytes4) {
        return 0xbc197c81;
    }

    /**
     * @notice Indicates whether a contract implements the `ERC1155TokenReceiver` functions and so can accept ERC1155 token types.
     * @param  interfaceID The ERC-165 interface ID that is queried for support.s
     * @dev This function MUST return true if it implements the ERC1155TokenReceiver interface and ERC-165 interface.
     *      This function MUST NOT consume more than 5,000 gas.
     * @return Wheter ERC-165 or ERC1155TokenReceiver interfaces are supported.
     */
    function supportsInterface(bytes4 interfaceID) external view returns (bool) {
        return  interfaceID == 0x01ffc9a7 ||    // ERC-165 support (i.e. `bytes4(keccak256('supportsInterface(bytes4)'))`).
        interfaceID == 0x4e2312e0;      // ERC-1155 `ERC1155TokenReceiver` support (i.e. `bytes4(keccak256("onERC1155Received(address,address,uint256,uint256,bytes)")) ^ bytes4(keccak256("onERC1155BatchReceived(address,address,uint256[],uint256[],bytes)"))`).
    }
}
