pragma solidity ^0.6.0;

import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/math/SafeMath.sol";
import "./Crowdsale.sol";

/**
 * @title CappedCrowdsale
 * @dev Crowdsale with a limit for total contributions.
 */
contract CappedCrowdsale is Crowdsale {
    using SafeMath for uint256;

    uint256 private _cap; // Maximum amount of token to sell
    uint256 private _tokenSold;

    /**
     * @dev Constructor, takes maximum amount of wei accepted in the crowdsale.
     * @param cap Max amount of wei to be contributed
     */
    constructor (uint256 rate, address payable wallet, address token, address aggregator, uint256 cap)
        public Crowdsale(rate, wallet, token, aggregator)
    {
        require(cap > 0, "CappedCrowdsale: cap is 0");
        _cap = cap;
    }

    /**
     * @return the cap of the crowdsale.
     */
    function cap() public view returns (uint256) {
        return _cap;
    }

    /**
     * @return the tokenSold of the crowdsale.
     */
    function tokenSold() public view returns (uint256) {
        return _tokenSold;
    }

    /**
     * @dev Checks whether the cap has been reached.
     * @return Whether the cap was reached
     */
    function capReached() public view returns (bool) {
        return tokenSold() >= _cap;
    }

    /**
     * @dev Extend parent behavior requiring purchase to respect the funding cap.
     * @param beneficiary Token purchaser
     * @param weiAmount Amount of wei contributed
     */
    function _postValidatePurchase(address beneficiary, uint256 weiAmount, uint256 tokens) internal view override {
        super._postValidatePurchase(beneficiary, weiAmount, tokens);
        require(tokenSold() <= _cap, "CappedCrowdsale: cap exceeded");
    }

    function _updatePurchasingState(address beneficiary, uint256 weiAmount, uint256 tokens) internal override {
        _tokenSold = _tokenSold.add(tokens);
    }
}

