contract ArtSNSwap is ReentrancyGuard, Simple3Role, RejectDirectETH, SimpleSealSCSignature {
    using SafeMath for uint256;
    using Calculation for uint256;
    using Address for address payable;
    using SafeERC20 for IERC20;
    using SealUtils for address;

    address public approver;
    IArtNFT public artNFT;

    mapping(bytes32=>uint256) public artList;
    mapping(bytes=>bool) public deprecatedSN;

    event ArtListed(bytes32 artID, bytes32 fingerprint);
    event ArtSwapOut(bytes32 artID, bytes32 fingerprint, address to);
    event ArtPullOff(bytes32 artID, bytes32 fingerprint, address to);
    event SNDeprecated(bytes sn);

    constructor(address _owner, address _artNFT, address _approver) public Simple3Role(_owner) {
        artNFT = IArtNFT(_artNFT);
        approver = _approver;
    }

    function snSwapForArt(bytes32 _fingerprint, bytes calldata _sn) external {
        uint256 artID = artList[_fingerprint];

        require(artID != 0, "art not exist");
        require(!deprecatedSN[_sn], "deprecated serial number");

        approver.verifySignature(abi.encodePacked(_fingerprint), _sn, "sn not match the art");

        artNFT.safeTransferFrom(address (this), msg.sender, artID);

        emit ArtSwapOut(bytes32(artID), _fingerprint, msg.sender);

        artList[_fingerprint] = 0;
    }

    function setArtNFT(address _newArtNFT) external onlyAdmin {
        artNFT = IArtNFT(_newArtNFT);
    }

    function setApprover(address _newApprover) external onlyAdmin {
        approver = _newApprover;
    }

    function listArt(string calldata _name, string calldata _author, bytes32 _fingerprint) external onlyAdmin {
        uint256 artID = artNFT.createArtDirectly(_name, _author, _fingerprint, address(this));
        artList[_fingerprint] = artID;
        emit ArtListed(bytes32(artID), _fingerprint);
    }

    function pullOffArt(bytes32 _fingerprint, address _receiver) external onlyAdmin {
        uint256 artID = artList[_fingerprint];
        require(artID != 0, "not list");

        artNFT.safeTransferFrom(address(this), _receiver, artID);
        artList[_fingerprint] = 0;

        emit ArtPullOff(bytes32(artID), _fingerprint, _receiver);
    }

    function deprecateSerialNumber(bytes calldata _sn) external onlyAdmin {
        deprecatedSN[_sn] = true;

        emit SNDeprecated(_sn);
    }
}
