interface SpaceD  {
    function isExcludedFromFee(address account) external view returns(bool);
}



contract SaleMarket is Ownable, ReentrancyGuard {
    event SetPeriodIsStart(uint256 _period, bool _isStart);
    event BuySpaceD(address indexed user, uint256 _period, uint256 _cost, uint256 _buy, uint256 _get);

    using SafeERC20 for IERC20;
    using SafeMath for uint256;
    IERC20 public spaceD;
    IERC20 public sDog;
    uint256 public totalPeriod;
    uint256 public less = 1e17;

    struct PeriodInfo {
        uint256 number;
        uint256 total;
        uint256 haveSell;
        uint256 price;
        bool isStart;
    }

    struct UserInfo {
        uint256 cost;
        uint256 amount;
        uint256 num;
        bool isIn;
    }

    mapping(uint256 => PeriodInfo) public periodInfo;
    mapping(address => bool) public isOperator;
    mapping(address => mapping(uint256 => UserInfo)) public userInfo;
    mapping(address => uint256[]) public userPeriods;

    constructor(
        IERC20 _spaceD,
        IERC20 _sDog
    ) public {
        spaceD = _spaceD;
        sDog = _sDog;
    }

    modifier isPeriodExist(uint256 _period) {
        require(_period > 0 && _period <= totalPeriod, "_period not exist");
        _;
    }

    modifier isPermission() {
        require(isOperator[msg.sender] || msg.sender == owner(), "not operator");
        _;
    }

    modifier isMarketExcluded() {
        require(
            SpaceD(address(spaceD)).isExcludedFromFee(address(this)), 
            "contract should have _isExcludedFromFee"
        );
        _;
    }

    function setLessAmount(uint256 _newLess) public isPermission {
        less = _newLess;
    }

    function addOpreator(address newOperator) public onlyOwner {
        isOperator[newOperator] = true;
    }

    function removeOpreator(address operator) public onlyOwner {
        isOperator[operator] = false;
    }

    function setPeriodInfo(uint256 _num, uint256 _price) 
        public 
        isPermission 
        isMarketExcluded
        nonReentrant
        returns(uint256) 
    {
        require(_num > less, "_num should > less");
        require(_price > 0, "_price should > 0");

        uint256 _period = ++totalPeriod;
        periodInfo[_period].number = _num; 
        periodInfo[_period].price = _price;
        uint256 _before = spaceD.balanceOf(address(this));
        spaceD.safeTransferFrom(msg.sender, address(this), _num);
        uint256 _after = spaceD.balanceOf(address(this));
        periodInfo[_period].total = _after.sub(_before);

        return _period;
    }

    function setPeriodIsStart(uint256 _period, bool _isStart) 
        public 
        isPermission 
        isPeriodExist(_period)
    {
        periodInfo[_period].isStart = _isStart;
        emit SetPeriodIsStart(_period, _isStart);
    }

    function transferTo(IERC20 token, address _to, uint256 _value) 
        public 
        onlyOwner 
        isMarketExcluded
    {
        require(_value > 0, "_value should > 0");
        require(token.balanceOf(address(this)) >= _value, "not enough balance");
        token.safeTransfer(_to, _value);
    }


    function buySpaceD(uint256 _period, uint256 _num) 
        public 
        isPeriodExist(_period)
        isMarketExcluded
        nonReentrant 
    {
        UserInfo storage user = userInfo[msg.sender][_period];
        require(periodInfo[_period].isStart, "_period over");
        require(_num >= less, "_num out of range");
        require(
            periodInfo[_period].number.sub(periodInfo[_period].haveSell) >= _num, 
            "remaining not enough"
        );

        uint256 _cost = periodInfo[_period].price.mul(_num).div(1e18);
        require(sDog.balanceOf(msg.sender) >= _cost, "user sDog not enough");
        uint256 _buy = _num;
        require(spaceD.balanceOf(address(this)) >= _buy);
        periodInfo[_period].haveSell = periodInfo[_period].haveSell.add(_num);
        user.cost = user.cost.add(_cost);
        uint256 before = spaceD.balanceOf(msg.sender);
        sDog.safeTransferFrom(msg.sender, address(this), _cost);
        spaceD.safeTransfer(msg.sender, _buy);
        uint256 _after = spaceD.balanceOf(msg.sender);
        uint256 _get = _after.sub(before);
        if(!user.isIn) {
            user.isIn = true;
            userPeriods[msg.sender].push(_period);
        }
        user.amount = user.amount.add(_get);
        user.num = user.num.add(_num);

        emit BuySpaceD(msg.sender, _period, _cost, _buy, _get);
    }

    function getUserPeriods(address account) public view returns(uint256[] memory) {
        return userPeriods[account];
    }

    function getUserPeriodLength(address account) public view returns(uint256) {
        return userPeriods[account].length;
    }

}
