contract TDmining1x {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    struct UserInfo {
        uint256 amount;     // How many TD tokens the user has provided.
        uint256 rewardDebt; // Reward debt.
    }
    mapping (uint256 => mapping (address => UserInfo)) public userInfo;

    struct PoolInfo {
        IERC20 tdToken;           // Address of TD token contract.
        uint256 allocPoint;       // How many allocation points assigned to this pool. SUASHI to distribute per block.
        uint256 lastRewardBlock;  // Last block number that SUASHI distribution occurs.
        uint256 accSuancaiPerShare; // Accumulated SUASHI per share, times 1e12. See below.
    }
    PoolInfo[] public poolInfo;
    address private admin;
    IERC20 public suancai; 
    uint256 public suancaiPerBlock; 
    uint256 public constant BONUS_MULTIPLIER = 10;  
    uint256 public totalAllocPoint = 0; 
    uint256 public startBlock; 
    uint256 public fullBlock; 
    uint256 public endBlock; 
    address public suancaiPool; 

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);

    constructor() public {
        admin = 0xaa2ab4801D5F664212B8f5171C7d5bb51cc41a23;
        suancai = IERC20(0x9F0cDA7CFbf1824C56193a4911774e0822893625);
        suancaiPerBlock = 4*1e14;
        fullBlock = 1898992;
        suancaiPool = 0xaa2ab4801D5F664212B8f5171C7d5bb51cc41a23;
        startBlock = 1899999;
        endBlock = 19999999;
    }

    function owner() public view returns (address) {
        return admin;
    }

    modifier onlyOwner() {
        require(admin == msg.sender, "Ownable: caller is not the owner");
        _;
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    function add(uint256 _allocPoint, IERC20 _tdToken, bool _withUpdate) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 lastRewardBlock = block.number > startBlock ? block.number : startBlock;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfo.push(PoolInfo({
            tdToken: _tdToken,
            allocPoint: _allocPoint,
            lastRewardBlock: lastRewardBlock,
            accSuancaiPerShare: 0
        }));
    }

    function massUpdatePools() internal {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    function updatePool(uint256 _pid) internal {
        PoolInfo storage pool = poolInfo[_pid];
        uint256 avaibleBlock;
        avaibleBlock = block.number > endBlock? endBlock: block.number;
        if (avaibleBlock <= pool.lastRewardBlock) {
            return;
        }
        uint256 tdSupply = pool.tdToken.balanceOf(address(this));
        if (tdSupply == 0) {
            pool.lastRewardBlock = avaibleBlock;
            return;
        }
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, avaibleBlock);
        uint256 sucancaiReward = multiplier.mul(suancaiPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
        pool.accSuancaiPerShare = pool.accSuancaiPerShare.add(sucancaiReward.mul(1e12).div(tdSupply));  //?SUASHI的单位是多少位？
        pool.lastRewardBlock = avaibleBlock;
    }

    function getMultiplier(uint256 _from, uint256 _to) internal pure returns (uint256) {
        return _to.sub(_from);
    }
    
    function getPoolBalance (uint256 _pid) external view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        uint256 tdSupply = pool.tdToken.balanceOf(address(this));
        return tdSupply;
    }

    function pendingSuancai(uint256 _pid, address _user) external view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];

        uint256 avaibleBlock;
        avaibleBlock = block.number > endBlock? endBlock: block.number;
        
        if (avaibleBlock <= pool.lastRewardBlock) {
            return 0;
        }
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accSuancaiPerShare = pool.accSuancaiPerShare;
        uint256 tdSupply = pool.tdToken.balanceOf(address(this));
        if (avaibleBlock > pool.lastRewardBlock && tdSupply != 0) {
            uint256 multiplier = getMultiplier(pool.lastRewardBlock, avaibleBlock);
            uint256 suancaiReward = multiplier.mul(suancaiPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
            accSuancaiPerShare = accSuancaiPerShare.add(suancaiReward.mul(1e12).div(tdSupply));
        }
        uint256 bonus = user.amount.mul(accSuancaiPerShare).div(1e12).sub(user.rewardDebt);
        if (block.number < fullBlock) {
            return  bonus.mul(90).div(100);
        } 
        else {
            return bonus;
        }
    }

    // Deposit td tokens to MasterChef for suancai allocation.
    function deposit(uint256 _pid, uint256 _amount) public {
        require (block.number <= endBlock, "out of time.");
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);
        if (user.amount > 0) {
            uint256 bonus = user.amount.mul(pool.accSuancaiPerShare).div(1e12).sub(user.rewardDebt);
            uint256 pending;
            if (block.number > startBlock && block.number < fullBlock) {
                pending = bonus.mul(90).div(100);
                safeSuancaiTransfer(suancaiPool, bonus.sub(pending));
            }
            else {
                pending = bonus;
            }
            safeSuancaiTransfer(msg.sender, pending);   
        }
        pool.tdToken.safeTransferFrom(address(msg.sender), address(this), _amount); 
        user.amount = user.amount.add(_amount);
        user.rewardDebt = user.amount.mul(pool.accSuancaiPerShare).div(1e12);
        emit Deposit(msg.sender, _pid, _amount);
    }

    // Withdraw TD tokens from MasterChef.
    function withdraw(uint256 _pid, uint256 _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");
        updatePool(_pid);
        uint256 bonus = user.amount.mul(pool.accSuancaiPerShare).div(1e12).sub(user.rewardDebt);
        uint256 pending;
        if (block.number > startBlock && block.number < fullBlock) {
            pending = bonus.mul(90).div(100);
            safeSuancaiTransfer(suancaiPool, bonus.sub(pending));
        }
        else {
            pending = bonus;
        }
        safeSuancaiTransfer(msg.sender, pending);  
        user.amount = user.amount.sub(_amount);
        user.rewardDebt = user.amount.mul(pool.accSuancaiPerShare).div(1e12);
        pool.tdToken.safeTransfer(address(msg.sender), _amount);  
        emit Withdraw(msg.sender, _pid, _amount);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        pool.tdToken.safeTransfer(address(msg.sender), user.amount);
        emit EmergencyWithdraw(msg.sender, _pid, user.amount);
        user.amount = 0;
        user.rewardDebt = 0;
    }

    // Safe suancai transfer function, just in case if rounding error causes pool to not have enough SUANCAIs.
    function safeSuancaiTransfer(address _to, uint256 _amount) internal {
        uint256 suancaiBal = suancai.balanceOf(address(this));
        if (_amount > suancaiBal) {
            suancai.transfer(_to, suancaiBal);
        } else {
            suancai.transfer(_to, _amount);
        }
    }
}
