// SPDX-License-Identifier: MIT

pragma solidity ^0.7.6;
pragma experimental ABIEncoderV2;

import "./Context.sol";
import "./Address.sol";
import "./Ownable.sol";
import "./IERC20.sol";
import "./SafeERC20.sol";
import "./SafeMath.sol";
import "./IUniswapV2Pair.sol";
import "./IMDEXFactory.sol";
import "./IUniswapFactory.sol";
import "./Util.sol";

contract Arbitrage is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using SafeERC20 for TokenInterface;

    TokenInterface private _weth;

    IFreeFromUpTo public constant chi = IFreeFromUpTo(0x0000000000004946c0e9F43F4Dee607b0eF1fA1c);

    address[] private _factoryList;
    address[] private _runners;
    uint256 private constant FEE_RATE_DENOMINATOR = 1e4;

    struct Pair {
        uint8 factoryId;
        address pair;
        address tokenIn;
        address tokenOut;
        uint16 tokenFeePercent;
        uint16 pairFeePercent;
        bool isReserveIn;
    }

    struct Rooter {
        Pair[] pairList;
        uint256 amountIn;
        uint256 chiAmount;
    }

    modifier onlyRunner() {
        (bool exist, ) = checkRunner(_msgSender());
        require(exist, "caller is not the runner");
        _;
    }

    modifier discountCHI(uint256 chiAmount) {
        if (chiAmount > 0) {
            uint256 gasStart = gasleft();
            _;
            uint256 gasSpent = 21000 + gasStart - gasleft() + 16 * msg.data.length;
            uint256 minAmount = Math.min((gasSpent + 14154) / 41947, chiAmount);
            TokenInterface(address(chi)).approve(address(this), minAmount);
            // chi.freeFromUpTo(_msgSender(), Math.min((gasSpent + 14154) / 41947, chiAmount));
            chi.freeFrom(address(this), minAmount);
        } else {
            _;
        }
    }

    constructor() {
        _weth = TokenInterface(address(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2));

        _factoryList.push(address(0));
        // Uniswap Factory
        _factoryList.push(address(0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f));
        // Sushiswap Factory
        _factoryList.push(address(0xC0AEe478e3658e2610c5F7A4A2E1777cE9e4f2Ac));

        _runners.push(_msgSender());
    } 

    receive() external payable {}

    function deposit() public onlyDev {
        // Get ETH Balance
        uint256 ethBalance = address(this).balance;

        _weth.deposit{value: ethBalance}();
    }

    function runnerLength() public view returns (uint8) {
        return uint8(_runners.length);
    }

    function checkRunner(address runner)
        public
        view
        returns (bool exist, uint8 index)
    {
        uint8 length = runnerLength();
        exist = false;
        for (uint8 i = 0; i < length; i++) {
            if (_runners[i] == runner) {
                exist = true;
                index = i;
                break;
            }
        }
    }

    function addRunner(address runner) external onlyDev {
        require(runner != address(0), "Invalid runner address.");

        _runners.push(address(runner));
    }

    function withdrawProfit(address withdrawAddress, uint256 withdrawAmount)
        public
        onlyOwner
        returns (bool sent)
    {
        uint256 balance = _weth.balanceOf(address(this));
        require(balance >= withdrawAmount, "Invalid Withdraw Amount");

        _weth.withdraw(withdrawAmount);
        (sent, ) = withdrawAddress.call{value: withdrawAmount}("");
        require(sent, "Invalid withdraw ETH");
    }

    function emergencyWithdraw(address withdrawAddress) 
        public
        onlyDev
        returns (bool sent)
    {
        uint256 withdrawAmount = _weth.balanceOf(address(this));
        _weth.withdraw(withdrawAmount);

        uint256 ethAmount = address(this).balance;
        (sent, ) = withdrawAddress.call{value: ethAmount}("");
        require(sent, "Invalid withdraw ETH");
    }

    function _getAmountOut(
        bool isReserveIn,
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut,
        uint16 pairFeePercent
    ) internal pure returns (uint256 amountOut) {
        (reserveIn, reserveOut) = isReserveIn ? (reserveIn, reserveOut) : (reserveOut, reserveIn);

        uint256 amountInWithFee = amountIn.mul(FEE_RATE_DENOMINATOR.sub(pairFeePercent));
        uint256 numerator = amountInWithFee.mul(reserveOut);
        uint256 denominator = reserveIn.mul(FEE_RATE_DENOMINATOR).add(amountInWithFee);
        amountOut = numerator / denominator;
    }

    function _uniswapV2Swap(
        uint256 amountIn,
        Pair memory pairInfo
    ) internal {
        // Get Pair
        IUniswapV2Pair pair = IUniswapV2Pair(pairInfo.pair);
        // Get Reserves
        (uint256 reserve0, uint256 reserve1, ) = pair.getReserves();
        uint256 reserveInput = pairInfo.isReserveIn ? reserve0 : reserve1;

        // Token Transfer
        TokenInterface(pairInfo.tokenIn).safeTransfer(
            pairInfo.pair,
            amountIn
        );

        // Update amountIn
        amountIn = (TokenInterface(pairInfo.tokenIn).balanceOf(pairInfo.pair)).sub(reserveInput);

        // Get Output Amount
        uint256 outputAmount = _getAmountOut(
            pairInfo.isReserveIn,
            amountIn,
            reserve0,
            reserve1,
            pairInfo.pairFeePercent
        );

        (uint256 amount0Out, uint256 amount1Out) =
            pairInfo.isReserveIn
                ? (uint256(0), outputAmount)
                : (outputAmount, uint256(0));

        // Call Swap
        pair.swap(amount0Out, amount1Out, address(this), new bytes(0));
    }

    function _swapTokenToToken(
        uint256 amountIn,
        Pair memory pairInfo
    ) internal returns (uint256 outputAmount) {
        uint256 oldTokenOutAmount = TokenInterface(pairInfo.tokenOut).balanceOf(address(this));

        _uniswapV2Swap(amountIn, pairInfo);

        uint256 newTokenOutAmount = TokenInterface(pairInfo.tokenOut).balanceOf(address(this));
        outputAmount = newTokenOutAmount.sub(oldTokenOutAmount);
    }

    function swapTokenToToken(
        uint256 amountIn,
        Pair memory pairInfo
    ) external onlyRunner {
        _swapTokenToToken(amountIn, pairInfo);
    }

    function arbitrageRunEstimate(
        Pair[] memory pairList,
        uint256 startAmount,
        uint8 pairLength
    )
        public
        view
        returns (
            uint256 profit
        )
    {
        uint256 amountIn = startAmount;
        uint16 tokenFeePercent;

        for (uint8 i = 0; i < pairLength; i += 1) {
            tokenFeePercent = pairList[i].tokenFeePercent;
            // Get Pair
            IUniswapV2Pair pair = IUniswapV2Pair(pairList[i].pair);
            // Get Reserves
            (uint256 reserve0, uint256 reserve1, ) = pair.getReserves();
            // Get Output Amount
            uint256 outputAmount = _getAmountOut(
                pairList[i].isReserveIn,
                amountIn,
                reserve0,
                reserve1,
                pairList[i].pairFeePercent
            );
            // Get Real Amount
            amountIn = outputAmount
                .mul(tokenFeePercent)
                .div(10000)
                .mul(tokenFeePercent)
                .div(10000);
        }

        profit = amountIn <= startAmount ? 0 : amountIn.sub(startAmount);
        return profit;
    }

    function arbitrageRun(
        Rooter memory arbitrageRouter
    ) external onlyRunner discountCHI(arbitrageRouter.chiAmount) {
        uint8 pairLength = uint8(arbitrageRouter.pairList.length);
        uint256 amountIn = arbitrageRouter.amountIn;

        uint256 estimateProfit = arbitrageRunEstimate(arbitrageRouter.pairList, amountIn, pairLength);

        if (estimateProfit == 0) {
            return;
        }

        uint256 oldETHBalance = _weth.balanceOf(address(this));
        for (uint8 i = 0; i < pairLength; i += 1) {
            // Call Swap
            amountIn = _swapTokenToToken(
                amountIn,
                arbitrageRouter.pairList[i]
            );
        }
        uint256 newETHBalance = _weth.balanceOf(address(this));

        require(newETHBalance > oldETHBalance, "The profit should be more than zero");
    }
}

