    uint256 private _supply = 10000;
    uint256 private _whitelistPrice = 0.03 ether;
    uint256 private _whitelistDate;
    uint256 private _publicSaleDate;
    string private _uri;
    address private _authorizer = 0x7ACa8b1e3D01F52DF81bDD154319DB3D445cbD35;
    mapping (address => EnumerableSet.UintSet) private _ownerBabies;
