        _uri = uri_;
        _whitelistDate = 1639771200;
        _publicSaleDate = 1639771200 + 24 hours;
        _babiesId.increment();
    }
    
    /**
        @dev Buy and mint a new token
    */    
    function mintOnWhitelist(bytes memory signature) external payable {
        require(recov(keccak256(abi.encodePacked(msg.sender)), signature) == _authorizer, "Bad Babies: Invalid whitelist singature");
        require(msg.value >= _whitelistPrice, "Bad Babies: msg.value less than price");
        require(_whitelistDate <= block.timestamp, "Bad Babies: Mintin not allowed");
        require(_publicSaleDate > block.timestamp, "Bad Babies: Only public sale");
        mint_(msg.sender);
    }

    function recov(bytes32 hash, bytes memory signature) internal pure returns (address) {
        return ECDSA.recover(hash, signature);
    }

    function mint() external payable {
        require(_publicSaleDate <= block.timestamp, "Bad Babies: Only whitelist");
        require(msg.value >= _price, "Bad Babies: msg.value less than price");
        mint_(msg.sender);
    }

