pragma solidity 0.8.7;

// File: contracts/ShiryoinuChef.sol

contract MetaGochiChef is Ownable, ReentrancyGuard {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    IERC20 public rewardToken;

    IERC20 public stakedToken;

    IERC20 public stakedLP;
    uint256 public quantumStakedTokens = 400000000000000000000000; // number of staked tokens needed to earn the reward, 20 trillion x 9 decimals
    uint256 public quantumLPTokens = 20000000000000000000; // number of LP tokens needed to earn the reward (20) x 18 dec
    //uint256 public quantumLPTokens = 20000000000000000; // 0.002 for testing
    uint256 public rewardForTokenPerDuration = 1;
    uint256 public rewardForLPPerDuration = 2;

    // uint256 rewardDuration = 7 days;
    uint256 public rewardDuration = 10 minutes;
    uint256 public totalClaimed;

    bool public farming = true;

    struct userPosition {
        uint256 totalClaimed;
        uint256 amountToken;
        uint256 amountTokenReceived;
        uint256 amountLP; // quantized
        uint256 amountLPReceived; // amount received due to transfer tax
        uint256 lastRewardTimestamp;
        uint256 grossTotalClaimed;
    }
    mapping(address => userPosition) public addressToUserPosition;
    address[] public tokenStakers;

    constructor() {
        rewardToken = IERC20(0x90749BcAE7bDeE78fD7b8829aeAc855c32A56376);
        stakedToken = IERC20(0xC1a85Faa09c7f7247899F155439c5488B43E8429);
        stakedLP = IERC20(0xBA470432de1f7cC406137379DEbB0192284A3031);
    }

    function _checkAndClaimPacks(address _user) internal {
        uint256 claimable = claimablePacks(_user);
        if (claimable > 0) {
            addressToUserPosition[_user].lastRewardTimestamp = block.timestamp;
            addressToUserPosition[_user].totalClaimed = claimable;
            totalClaimed = totalClaimed + claimable;
            addressToUserPosition[_user].grossTotalClaimed = totalClaimed;
            rewardToken.safeTransfer(_user, claimable);
        }
    }

    // user functions
    function deposit(IERC20 _target, uint256 _amount) public nonReentrant {
        require(_target == stakedToken || _target == stakedLP, "Uknown token.");

        _checkAndClaimPacks(msg.sender);

        if (_target == stakedToken) {
            uint256 current = addressToUserPosition[msg.sender].amountToken;
            require(
                current + _amount >= quantumStakedTokens,
                "Not enough tokens staked."
            );
            uint256 tokenBalanceBefore = stakedToken.balanceOf(address(this));
            stakedToken.safeTransferFrom(msg.sender, address(this), _amount);
            uint256 tokenReceived = stakedToken.balanceOf(address(this)).sub(
                tokenBalanceBefore
            );
            addressToUserPosition[msg.sender].amountTokenReceived =
                addressToUserPosition[msg.sender].amountTokenReceived +
                tokenReceived;
            addressToUserPosition[msg.sender].amountToken = current + _amount;
            addressToUserPosition[msg.sender].lastRewardTimestamp = block
                .timestamp;

            tokenStakers.push(msg.sender);
        }
        if (_target == stakedLP) {
            uint256 current = addressToUserPosition[msg.sender].amountLP;
            require(
                current + _amount >= quantumLPTokens,
                "Not enough LP staked."
            );
            uint256 lpBalanceBefore = stakedLP.balanceOf(address(this));
            stakedLP.safeTransferFrom(msg.sender, address(this), _amount);
            uint256 lpReceived = stakedLP.balanceOf(address(this)).sub(
                lpBalanceBefore
            );
            addressToUserPosition[msg.sender].amountLPReceived =
                addressToUserPosition[msg.sender].amountLPReceived +
                lpReceived;
            addressToUserPosition[msg.sender].amountLP = current + _amount;
            addressToUserPosition[msg.sender].lastRewardTimestamp = block
                .timestamp;
        }
    }

    function withdraw(IERC20 _target) public nonReentrant {
        _checkAndClaimPacks(msg.sender);

        if (
            _target == stakedToken &&
            addressToUserPosition[msg.sender].amountToken > 0
        ) {
            uint256 current = addressToUserPosition[msg.sender]
                .amountTokenReceived;
            addressToUserPosition[msg.sender].amountToken = 0;
            addressToUserPosition[msg.sender].amountTokenReceived = 0;
            stakedToken.safeTransfer(msg.sender, current);
        }
        if (
            _target == stakedLP &&
            addressToUserPosition[msg.sender].amountLP > 0
        ) {
            uint256 current = addressToUserPosition[msg.sender]
                .amountLPReceived;
            addressToUserPosition[msg.sender].amountLP = 0;
            addressToUserPosition[msg.sender].amountLPReceived = 0;
            stakedLP.safeTransfer(msg.sender, current);
        }
        addressToUserPosition[msg.sender].lastRewardTimestamp = block.timestamp;
    }

    function nextRewardTime(address _user) public view returns (uint256) {
        uint256 elapsedTime = block.timestamp -
            addressToUserPosition[_user].lastRewardTimestamp;
        uint256 next = addressToUserPosition[_user].lastRewardTimestamp +
            rewardDuration;
        if (elapsedTime >= rewardDuration) {
            uint256 durationUnits = elapsedTime.div(rewardDuration).add(1);
            next = addressToUserPosition[_user].lastRewardTimestamp.add(
                durationUnits.mul(rewardDuration)
            );
        }
        return next;
    }

    function claimablePacks(address _user) public view returns (uint256) {
        uint256 claimable = 0;
        uint256 elapsedTime = block.timestamp -
            addressToUserPosition[_user].lastRewardTimestamp;
        if (elapsedTime >= rewardDuration && farming == true) {
            uint256 durationUnits = elapsedTime.div(rewardDuration);
            uint256 multipleOfTokenQuantums = 0;
            if (addressToUserPosition[_user].amountToken > 0) {
                multipleOfTokenQuantums = addressToUserPosition[_user]
                    .amountToken
                    .div(quantumStakedTokens);
            }
            uint256 multipleOfLPQuantums = 0;
            if (addressToUserPosition[_user].amountLP > 0) {
                multipleOfLPQuantums = addressToUserPosition[_user]
                    .amountLP
                    .div(quantumLPTokens);
            }
            claimable = durationUnits.mul(
                rewardForTokenPerDuration.mul(multipleOfTokenQuantums) +
                    rewardForLPPerDuration.mul(multipleOfLPQuantums)
            );
        }
        return claimable;
    }

    function claim() public {
        _checkAndClaimPacks(msg.sender);
    }

    // admin functions
    function setStakedToken(IERC20 _target) public onlyOwner {
        require(address(_target) != address(0x0), "Null address not allowed");
        stakedToken = _target;
    }

    function setStakedLPT(IERC20 _target) public onlyOwner {
        require(address(_target) != address(0x0), "Null address not allowed");
        stakedLP = _target;
    }

    // sets the reward amount for the target token
    function setReward(IERC20 _target, uint256 _amount) public onlyOwner {
        require(_target == stakedToken || _target == stakedLP, "Uknown token.");

        if (_target == stakedToken) {
            rewardForTokenPerDuration = _amount;
        }
        if (_target == stakedLP) {
            rewardForLPPerDuration = _amount;
        }
    }

    function setRewardDuration(uint256 _durationSeconds) public onlyOwner {
        rewardDuration = _durationSeconds;
    }

    function setQuantumLPTokens(uint256 _amount) public onlyOwner {
        quantumLPTokens = _amount;
    }

    function setQuantumStakedTokens(uint256 _amount) public onlyOwner {
        quantumStakedTokens = _amount;
    }

    function withdrawRewardToken(uint256 _amount) public onlyOwner {
        rewardToken.safeTransfer(msg.sender, _amount);
    }

    function setFarming(bool _farming) public onlyOwner {
        farming = _farming;
    }
}
