// File: contracts\Comptroller\ElenaVaultLiquidation.sol
// License: MIT

contract ElenaVaultLiquidation is AccessControl{ 
    using SafeMath for uint256; 
    using SafeERC20 for IERC20; 
    uint256 public constant PRICE_PRECISION = 1e6;
    address public useVaultPool;
    address public comptrollerPool;   
    bool public exitWithElenaPaused = true;
    bool public exitWithUSEPaused   = true;
    uint256 public constant MAX_ELENA_PRICE = 40e6;
    uint256 public constant MAX_USE_PRICE   = 36e4;
    uint256 public elenaLiquidationPrice = MAX_ELENA_PRICE;
    uint256 public useLiquidationPrice   = MAX_USE_PRICE;
    bytes32 public constant VAULT_LIQUIDATION_MGR    = keccak256("VAULT_LIQUIDATION_MGR");
    bytes32 public constant VAULT_EMERGENCY_WITHDRAW = keccak256("VAULT_EMERGENCY_WITHDRAW");
    constructor() public { 
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        grantRole(VAULT_LIQUIDATION_MGR, _msgSender());      
    }
    modifier onlyAuthorized{
        require(hasRole(VAULT_LIQUIDATION_MGR, msg.sender),"not mgr");
        _;
    }  
    function collatDollarBalance() external pure  returns (uint256){
       return 0;
    }
    function init(address _usePool,address _elenaPool)  public onlyAuthorized{ 
        useVaultPool = _usePool; 
        comptrollerPool = _elenaPool;
        address _use = IUSEComptrollerPool(comptrollerPool).use();
        IERC20(_use).safeApprove(useVaultPool,0);
        IERC20(_use).safeApprove(useVaultPool,uint256(-1));
    } 
    function burnUseOrShares(uint256 _sharesAmount,uint256 _useAmount)  public onlyAuthorized{ 
        if(_sharesAmount > 0){
             address _shares = IUSEComptrollerPool(comptrollerPool).shares();
            IShareToken(_shares).burn(_sharesAmount);
        }
        if(_useAmount > 0){
             address _use = IUSEComptrollerPool(comptrollerPool).use();
            IUSEStablecoin(_use).burn(_useAmount);
        }
    }
    function redeem1t1USE(uint256 _use_amount) public onlyAuthorized{
        require(IUSEPool(useVaultPool).redeemPaused() == true,"not emergency mode");
        address _use = IUSEComptrollerPool(comptrollerPool).use();
        IUSEStablecoin(_use).pool_mint(address(this),_use_amount);
        IUSEPool(useVaultPool).toggleRedeeming(); 
        IUSEPool(useVaultPool).redeem1t1USE(_use_amount,0);
        IUSEPool(useVaultPool).toggleRedeeming(); 
        require(IUSEPool(useVaultPool).redeemPaused() == true,"double check failed");
    }
    function collectRedemption() public onlyAuthorized{
        IUSEPool(useVaultPool).collectRedemption();
    } 
    function exitWithElena(uint256 _amount) public{
        require(exitWithElenaPaused == false,"!paused");
        require(elenaLiquidationPrice <= MAX_ELENA_PRICE,"!elena price");
        //elena
        address _shares = IUSEComptrollerPool(comptrollerPool).shares();
        IERC20(_shares).safeTransferFrom(msg.sender,address(this),_amount);
         //collateral
        address _collateral_address = IUSEPool(useVaultPool).collateral_address();
        uint256 _collateral_amount = elenaLiquidationPrice.mul(_amount).div(PRICE_PRECISION);
        IERC20(_collateral_address).safeTransfer(msg.sender,_collateral_amount);
    }
    function exitWithUSE(uint256 _amount) public{
        require(exitWithUSEPaused == false,"!paused");
        require(useLiquidationPrice <= MAX_USE_PRICE,"!use price");
        //use
        address _use = IUSEComptrollerPool(comptrollerPool).use();
        IERC20(_use).safeTransferFrom(msg.sender,address(this),_amount);
        //collateral
        address _collateral_address = IUSEPool(useVaultPool).collateral_address();
        uint256 _collateral_amount = useLiquidationPrice.mul(_amount).div(PRICE_PRECISION);
        IERC20(_collateral_address).safeTransfer(msg.sender,_collateral_amount);
    }
    function emergencyWithdraw(address _token,uint256 _amount) public onlyAuthorized{ 
       require(hasRole(VAULT_EMERGENCY_WITHDRAW, msg.sender),"emergency?");
       IERC20(_token).safeTransfer(msg.sender, _amount); 
    }
    function toggleExitWithElenaPaused() public onlyAuthorized{ 
        exitWithElenaPaused = !exitWithElenaPaused;
    }
    function toggleExitWithUSEPaused() public onlyAuthorized{ 
        exitWithUSEPaused = !exitWithUSEPaused;
    } 
    function updateLiqudationPrice(uint256 _elenaPrice,uint256 _usePrice) public onlyAuthorized{
        require(_elenaPrice <= MAX_ELENA_PRICE,"!elena price");
        require(_usePrice   <= MAX_USE_PRICE  ,"!use price");
        elenaLiquidationPrice = _elenaPrice;
        useLiquidationPrice = _usePrice;
    } 
}
