// File: contracts/mining/LPMining.sol

pragma solidity ^0.6.12;




contract LPMining is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    struct UserInfo {
        uint256 amount;     // 用户抵押的 LP 数量
        uint256 rewardDebt; // 用户奖励的债务
        uint256 lastBlock; // 用户抵押 提现 领取后更新的操作

        // 用户应得挖矿奖励计算:
        //
        //   pending reward = (user.amount * pool.accTokenPerShare) - user.rewardDebt
        //
        // 用户抵押，提现时进行的操作:
        //   1. The pool's `accTokenPerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
    }

    // 池子的信息
    struct PoolInfo {
        IERC20 lpToken;           // LP 地址
        uint256 allocPoint;       // 池子的权重
        uint256 lastRewardBlock;  // 最新的奖励区块
        uint256 accTokenPerShare; // 根据区块计算平均每个块挖到的 token
        uint256 totalDeposit;     // 有效抵押数
    }

    // 奖励代币
    STSToken public stsToken;
    // 分红截止区块
    uint256 public bonusEndBlock;
    // 每个块产的奖励代币
    uint256 public stsTokenPerBlock;
    // 每个周期多少个块
    uint256 public blockPerPeriod;
    // 分红奖励倍数 1 是 1倍 即不奖励 2 是奖励2倍 以此类推
    uint256 public constant BONUS_MULTIPLIER = 1; // no bonus

    // 池子数组信息
    PoolInfo[] public poolInfo;
    // 用户信息映射
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;
    // 所有池子的总权重
    uint256 public totalAllocPoint = 0;
    // 挖矿开始区块
    uint256 public startBlock;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);

    // 构造函数
    // stsToken: 奖励代币
    // startBlock: 挖矿起始区块
    // stsTokenPerBlock: 每个区块产矿数
    // blockPerPeriod: 每个周期多少个区块
    constructor(
        STSToken _stsToken,
        uint256 _startBlock,
        uint256 _initSTSTokenBase,
        uint256 _blockPerPeriod
    ) public {
        stsToken = _stsToken;
        stsTokenPerBlock = _initSTSTokenBase;
        blockPerPeriod = _blockPerPeriod;
        bonusEndBlock = _startBlock.add(_blockPerPeriod);
        startBlock = _startBlock;
    }

    function setBonusEndBlock(uint256 _startBlock) public onlyOwner {
        bonusEndBlock = _startBlock.add(blockPerPeriod);
    }

    function setStartBlock(uint256 _startBlock) public onlyOwner {
        startBlock = _startBlock;
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    // 新增 LP 池子
    // 不要添加相同 LP token 的池子，会引起计算混乱
    function add(uint256 _allocPoint, IERC20 _lpToken, bool _withUpdate) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        checkDuplicateLp(_lpToken);
        uint256 lastRewardBlock = block.number > startBlock ? block.number : startBlock;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfo.push(PoolInfo({
            lpToken : _lpToken,
            allocPoint : _allocPoint,
            lastRewardBlock : lastRewardBlock,
            accTokenPerShare : 0,
            totalDeposit: 0
            }));
    }

    // 检查是否存在重复LP
    function checkDuplicateLp(IERC20 _lpToken) internal view {
        PoolInfo storage pool;
        for(uint256 i = 0; i < poolInfo.length; i++) {
            pool = poolInfo[i];
            require(pool.lpToken != _lpToken, "lptoken is exist");
        }
    }

    // 更新池子的权重
    function set(uint256 _pid, uint256 _allocPoint, bool _withUpdate) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(_allocPoint);
        poolInfo[_pid].allocPoint = _allocPoint;
    }

    // 返回区块奖励因数
    // 避免 衰减后 uint div 取整的问题，故传入了一个 baseRatio
    function getMultiplier(uint256 _from, uint256 _to, uint baseRatio) public view returns (uint256) {
        uint base = baseRatio;
        uint canAwardBlockNum;
        if (_to <= bonusEndBlock) {
            return _to.sub(_from).mul(BONUS_MULTIPLIER).mul(base);
        } else if (_from >= bonusEndBlock) {
            {
                for (uint i = bonusEndBlock; i < _to; i = i + blockPerPeriod) {
                    // 超过12个周期不再进行挖矿奖励
                    if (i >= bonusEndBlock.add(blockPerPeriod.mul(11))) {
                        break;
                    }
                    // 12个周期内才进行奖励衰减
                    if (i < bonusEndBlock.add(blockPerPeriod.mul(11))) {
                        base = base.mul(4).div(5);
                    }
                    // 循环进行衰减计算
                    if (i.add(blockPerPeriod) < _from) {
                        continue;
                    }

                    if (i < _from && i.add(blockPerPeriod) >= _from) {
                        // _from _to 同周期
                        if (i.add(blockPerPeriod) > _to) {
                            canAwardBlockNum = canAwardBlockNum.add(base.mul(_to.sub(_from)));
                        } else {
                            //  _from _to 跨周期
                            canAwardBlockNum = canAwardBlockNum.add(base.mul(i.add(blockPerPeriod).sub(_from)));
                        }
                        continue;
                    }
                    //
                    if (i.add(blockPerPeriod) > _to) {
                        // 同周期 is (_to - i)
                        canAwardBlockNum = canAwardBlockNum.add(_to.sub(i).mul(base));
                        continue;
                    }
                    // 用户区块 逻辑可领取区块数
                    canAwardBlockNum = canAwardBlockNum.add(blockPerPeriod.mul(base));
                }
            }
            return canAwardBlockNum;

        } else {
            uint first = bonusEndBlock.sub(_from).mul(BONUS_MULTIPLIER).mul(base);
            {
                for (uint j = bonusEndBlock; j < _to; j = j + blockPerPeriod) {
                    // after 24 period do not mint award
                    if (j >= bonusEndBlock.add(blockPerPeriod.mul(11))) {
                        break;
                    }
                    // after 13 period do not decay
                    if (j < bonusEndBlock.add(blockPerPeriod.mul(11))) {
                        base = base.mul(4).div(5);
                    }
                    if (j.add(blockPerPeriod) > _to) {
                        // cross the blocks is (_to - j)
                        canAwardBlockNum = canAwardBlockNum.add(_to.sub(j).mul(base));
                        continue;
                    }
                    // 用户区块 逻辑可领取区块数
                    canAwardBlockNum = canAwardBlockNum.add(blockPerPeriod.mul(base));
                }
            }
            return first.add(canAwardBlockNum);
        }
    }

    // 前端渲染挖矿奖励数值
    function pendingToken(uint256 _pid, address _user) external view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accTokenPerShare = pool.accTokenPerShare;
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint baseRatio = 1e12;
            uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number, baseRatio);
            uint256 stsTokenReward = multiplier.mul(stsTokenPerBlock).mul(pool.allocPoint).div(totalAllocPoint).div(baseRatio);
            accTokenPerShare = accTokenPerShare.add(stsTokenReward.mul(1e12).div(lpSupply));
        }
        return user.amount.mul(accTokenPerShare).div(1e12).sub(user.rewardDebt);
    }

    // 强制更新池子信息
    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    // 更新池子信息
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (lpSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint baseRatio = 1e12;
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number, baseRatio);
        uint256 stsTokenReward = multiplier.mul(stsTokenPerBlock).mul(pool.allocPoint).div(totalAllocPoint).div(baseRatio);

        pool.accTokenPerShare = pool.accTokenPerShare.add(stsTokenReward.mul(1e12).div(lpSupply));
        pool.lastRewardBlock = block.number;
    }

    // 抵押 LP token 当 amount 为 0 时、领取挖矿奖励
    function deposit(uint256 _pid, uint256 _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        // 如果是抵押令牌
        if (_amount > 0) {
            // 如果是初次新增抵押，则有效抵押数量 +1
            if (user.amount == 0) {
                pool.totalDeposit = pool.totalDeposit.add(1);
            }
        }
        updatePool(_pid);
        if (user.amount > 0) {
            uint256 pending = user.amount.mul(pool.accTokenPerShare).div(1e12).sub(user.rewardDebt);
            safeTokenTransfer(msg.sender, pending);
        }
        if (_amount > 0) {
            pool.lpToken.safeTransferFrom(address(msg.sender), address(this), _amount);
        }
        user.amount = user.amount.add(_amount);
        user.rewardDebt = user.amount.mul(pool.accTokenPerShare).div(1e12);
        emit Deposit(msg.sender, _pid, _amount);
    }

    // 提取 LP token
    function withdraw(uint256 _pid, uint256 _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");
        updatePool(_pid);
        uint256 pending = user.amount.mul(pool.accTokenPerShare).div(1e12).sub(user.rewardDebt);
        safeTokenTransfer(msg.sender, pending);
        user.amount = user.amount.sub(_amount);
        if (user.amount == 0) {
            pool.totalDeposit = pool.totalDeposit.sub(1);
        }
        user.rewardDebt = user.amount.mul(pool.accTokenPerShare).div(1e12);
        pool.lpToken.safeTransfer(address(msg.sender), _amount);
        emit Withdraw(msg.sender, _pid, _amount);
    }

    // 不要奖励，直接提取 LP token
    function emergencyWithdraw(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        pool.lpToken.safeTransfer(address(msg.sender), user.amount);
        emit EmergencyWithdraw(msg.sender, _pid, user.amount);
        user.amount = 0;
        user.rewardDebt = 0;
    }

    // 从合约发送奖励代币 如果合约账户代币不足，则直接 return
    function safeTokenTransfer(address _to, uint256 _amount) internal {
        uint256 stsTokenBal = stsToken.balanceOf(address(this));
        if (_amount == 0 || stsTokenBal == 0) {
            return;
        }
        if (_amount > stsTokenBal) {
            stsToken.transfer(_to, stsTokenBal);
        } else {
            stsToken.transfer(_to, _amount);
        }
    }

    // 转移合约上的代币到指定地址，可由部署者操作
    function forceTransfer(address _addr, uint256 _amount) public onlyOwner {
        require(_addr != address(0), "address can not be 0");
        safeTokenTransfer(_addr, _amount);
    }

    function getTotalDeposit(uint256 _pid) public view returns(uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        return pool.totalDeposit;
    }

}
