// 
contract MasterPhoenix is Ownable {
    using SafeMath for uint;
    using SafeERC20 for IERC20;

    struct UserInfo {
        uint amount;
        uint rewardDebt;
        uint lastClaimBlock;
        bool privateUser;
    }

    struct PoolInfo {
        IERC20 lpToken;
        uint allocPoint;
        uint lastRewardBlock;
        uint accDephPerShare; 
        bool privatePool;
    }

    IDefiance defiance;
    uint public dephPerBlock = 1e18;
    PoolInfo[] public poolInfo;
    mapping(uint => mapping(address => UserInfo)) public userInfo;
    uint public totalAllocPoint = 0;
    uint public startBlock;
    address public defianceDeployer = 0xa1d3A9FFC46D3b4c19A939e6C37876927aab1620;

    // Events
    event Recovered(address token, uint amount);
    event Deposit(address indexed user, uint indexed pid, uint amount);
    event Withdraw(address indexed user, uint indexed pid, uint amount);
    event EmergencyWithdraw(
        address indexed user,
        uint indexed pid,
        uint amount
    );

    constructor(
        address _defiance,
        uint _startBlock
    ) public {
        defiance =  IDefiance(_defiance);
        startBlock = _startBlock;
    }

    function poolLength() external view returns (uint) {
        return poolInfo.length;
    }

    function add(
        uint _allocPoint,
        IERC20 _lpToken,
        bool _withUpdate,
        bool _private
    ) public onlyOwner {

        if (_withUpdate) {
            massUpdatePools();
        }

        uint lastRewardBlock = block.number > startBlock
            ? block.number
            : startBlock;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfo.push(
            PoolInfo({
                lpToken: _lpToken,
                allocPoint: _allocPoint,
                lastRewardBlock: lastRewardBlock,
                accDephPerShare: 0,
                privatePool: _private
            })
        );
    }

    function set(
        uint _pid,
        uint _allocPoint,
        bool _withUpdate
    ) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }

        totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(
            _allocPoint
        );
        poolInfo[_pid].allocPoint = _allocPoint;
    }

    function getMultiplier(uint _from, uint _to) private view returns (uint) {
        uint _blockCount = _to.sub(_from);

        return dephPerBlock.mul(_blockCount);
    }

    function pendingDeph(uint _pid, address _user)
        external
        view
        returns (uint)
    {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint accDephPerShare = pool.accDephPerShare;
        uint lpSupply = pool.lpToken.balanceOf(address(this));
        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint multiplier = getMultiplier(
                pool.lastRewardBlock,
                block.number
            );
            uint dephReward = multiplier
                .mul(pool.allocPoint)
                .div(totalAllocPoint);
            accDephPerShare = accDephPerShare.add(
                dephReward
                .mul(1e12)
                .div(lpSupply)
            );
        }
        return user.amount.mul(accDephPerShare)
        .div(1e12)
        .sub(user.rewardDebt);
    }

    function massUpdatePools() public {
        uint length = poolInfo.length;
        for (uint pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    function updatePool(uint _pid) public {
        if(startBlock == 0) {
            return;
        }

        PoolInfo storage pool = poolInfo[_pid];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint lpSupply = pool.lpToken.balanceOf(address(this));
        if (lpSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        //require(defiance.balanceOf(address(this)) >= dephPerBlock,"farming ended.!");
        
        if(defiance.balanceOf(address(this)) < dephPerBlock ){
           return;
        }

        uint multiplier = getMultiplier(pool.lastRewardBlock, block.number);

        uint dephReward = multiplier
            .mul(pool.allocPoint)
            .div(totalAllocPoint);

        pool.accDephPerShare = pool.accDephPerShare.add(
            dephReward
            .mul(1e12)
            .div(lpSupply)
        );
        pool.lastRewardBlock = block.number;
    }
    function getClaimPenalty(uint _lastClaimBlock) internal view returns(uint)
    {
        uint _day = block.number.sub(_lastClaimBlock).div(6700).add(1);
        if(_day == 1)
            return 60;
        else if(_day == 2)
            return 50;
        else if(_day == 3)
            return 40;
        else if(_day == 4)
            return 30;
        else if(_day == 5)
            return 20;
        else if(_day == 6)
            return 10;
        else 
            return 0;
    }
    function deposit(uint _pid, uint _amount) public {
        require(startBlock > 0, "phoenix not activated yet.!");
        
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        
        require((!pool.privatePool && !user.privateUser) || (pool.privatePool && user.privateUser), "private users only.!");        
        
        updatePool(_pid);
        if (user.amount > 0) {
            uint pending = user
                .amount
                .mul(pool.accDephPerShare)
                .div(1e12)
                .sub(user.rewardDebt);
                
            if (pending > 0) {

                uint _claimPenalty = getClaimPenalty(user.lastClaimBlock);
                if(_claimPenalty > 0)
                {
                    uint _burnAmount = pending.mul(_claimPenalty).div(100);
                    defiance.distribute(_burnAmount);
                    pending = pending.sub(_burnAmount);
                }
                safeDEPHTransfer(msg.sender, pending);
            }
        }
        if (_amount > 0) {
            pool.lpToken.safeTransferFrom(
                address(msg.sender),
                address(this),
                _amount
            );
            user.amount = user.amount.add(_amount);
        }
        user.lastClaimBlock = block.number;
        user.rewardDebt = user.amount.mul(pool.accDephPerShare).div(1e12);

        emit Deposit(msg.sender, _pid, _amount);
    }

    function withdraw(uint _pid, uint _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");

        updatePool(_pid);
        uint pending = user.amount.mul(pool.accDephPerShare)
        .div(1e12)
        .sub(user.rewardDebt);

        if (pending > 0) {
            uint _claimPenalty = getClaimPenalty(user.lastClaimBlock);
            if(_claimPenalty > 0)
            {
                uint _burnAmount = pending.mul(_claimPenalty).div(100);
                defiance.distribute(_burnAmount);
                pending = pending.sub(_burnAmount);
            }
            safeDEPHTransfer(msg.sender, pending);
        }
        if (_amount > 0) {
            user.amount = user.amount.sub(_amount);
            pool.lpToken.safeTransfer(address(msg.sender), _amount);
        }
        user.rewardDebt = user.amount.mul(pool.accDephPerShare).div(1e12);

        user.lastClaimBlock = block.number;

        emit Withdraw(msg.sender, _pid, _amount);
    }

    function emergencyWithdraw(uint _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        pool.lpToken.safeTransfer(address(msg.sender), user.amount);
        emit EmergencyWithdraw(msg.sender, _pid, user.amount);
        user.amount = 0;
        user.rewardDebt = 0;
        user.lastClaimBlock = block.number;
    }
    function emergencyFallback() external onlyOwner {
        uint dephBalance = defiance.balanceOf(address(this));
        defiance.transfer(defianceDeployer, dephBalance);
    }
    function safeDEPHTransfer(address _to, uint _amount) private {
        uint dephBalance = defiance.balanceOf(address(this));
        if (_amount > dephBalance) {
            defiance.transfer(_to, dephBalance);
        } else {
            defiance.transfer(_to, _amount);
        }
    }

    function setPrivateUsers(uint _pid, address[] calldata _users, bool _status) public onlyOwner{
        require(_users.length > 0);
        
        for (uint256 i = 0; i < _users.length; i++) {
            UserInfo storage user = userInfo[_pid][_users[i]];
            user.privateUser = _status;
		}
    }
  
    function setStartBlock(uint _startBlock) external onlyOwner {
        require(startBlock == 0 && _startBlock > 0, " startBlock > 0 ?");
        startBlock = _startBlock;
    }
}
