 *  https://rope.lol
 */

pragma solidity >=0.6.0;
pragma experimental ABIEncoderV2;

interface ToshimonMinter {
  function safeTransferFrom(
    address _from,
    address _to,
    uint256 _id,
    uint256 _amount,
    bytes calldata _data
  ) external;

  function safeBatchTransferFrom(
    address _from,
    address _to,
    uint256[] calldata _ids,
    uint256[] calldata _amounts,
    bytes calldata _data
  ) external;

  function setApprovalForAll(address _operator, bool _approved) external;

  function isApprovedForAll(address _owner, address _operator)
    external
    view
    returns (bool isOperator);

  function balanceOf(address _owner, uint256 _id)
    external
    view
    returns (uint256);

  function totalSupply(uint256 _id) external view returns (uint256);

  function maxSupply(uint256 _id) external view returns (uint256);
  
  function burn(address _account, uint256 _id, uint256 _amount) external;

  function mint(
    address _to,
    uint256 _id,
    uint256 _quantity,
    bytes memory _data
  ) external;
}

interface ToshiCoin {
  function totalSupply() external view returns (uint256);

  function totalClaimed() external view returns (uint256);

  function addClaimed(uint256 _amount) external;

  function setClaimed(uint256 _amount) external;

  function transfer(address receiver, uint256 numTokens)
    external
    returns (bool);

  function transferFrom(
    address owner,
    address buyer,
    uint256 numTokens
  ) external returns (bool);

  function balanceOf(address owner) external view returns (uint256);

  function mint(address _to, uint256 _amount) external;

  function burn(address _account, uint256 value) external;
}

contract ToshiDojo is Ownable {
  using SafeMath for uint256;

  ToshimonMinter public toshimonMinter;
  ToshiCoin public toshiCoin;

  struct Pack {
    uint256 minterId; // ToshimonMinter id for this pack
    uint256[] cardsPerTier; // Number of guaranteed cards from each tier for each pack
    mapping(uint256 => uint256[]) cardIdList; // mapping of tier -> cardIds for each tier in the pack
    uint256 priceInToshiCoin; // Amount of ToshiCoin required to purchase
    uint256 purchased;
    uint256 redeemed;
  }

  struct Card {
    uint256 maxSupply;
    uint256 supply;
  }

  struct PackCardData {
    uint256 cardId;
    uint256 cardsLeft;
  }

  uint256[] public packIdList;
  mapping(uint256 => Pack) public packs;
  mapping(uint256 => Card) public cards;

  event Redeemed(
    address indexed _user,
    uint256 indexed _packId,
    uint256 indexed _cardId
  );

  constructor(ToshimonMinter _toshimonMinter, ToshiCoin _toshiCoin) public {
    toshimonMinter = _toshimonMinter;
    toshiCoin = _toshiCoin;
  }

  modifier onlyEOA() {
    require(msg.sender == tx.origin, 'Not eoa');
    _;
  }

  // Returns the list of cardIds which are part of a tier
  function getCardIdListOfTier(uint256 _packId, uint256 _tier)
    external
    view
    returns (uint256[] memory)
  {
    return packs[_packId].cardIdList[_tier];
  }

  function _getPackCardData(uint256 _packId, uint256 _tier)
    internal
    view
    returns (PackCardData[] memory)
  {
    uint256[] memory _cardIdList = packs[_packId].cardIdList[_tier];
    PackCardData[] memory result = new PackCardData[](_cardIdList.length);

    for (uint256 i = 0; i < _cardIdList.length; ++i) {
      uint256 _cardId = _cardIdList[i];
      uint256 maxSupply = cards[_cardId].maxSupply;
      uint256 supply = cards[_cardId].supply;
      uint256 cardsLeft = maxSupply.sub(supply);
      result[i] = PackCardData(_cardId, cardsLeft);
    }

    return result;
  }

  // Returns amount of packs left
  function _getPacksLeft(PackCardData[] memory _data)
    internal
    pure
    returns (uint256)
  {
    uint256 total = 0;
    for (uint256 i = 0; i < _data.length; ++i) {
      total = total.add(_data[i].cardsLeft);
    }

    return total;
  }

  // Returns amount of packs left
  function getPacksLeft(uint256 _packId) public view returns (uint256) {
    uint256 _tierCount = packs[_packId].cardsPerTier.length;

    uint256 total = 0;
    for (uint256 i = 0; i < _tierCount; ++i) {
      uint256[] memory _cardIdList = packs[_packId].cardIdList[i];

      for (uint256 j = 0; j < _cardIdList.length; ++j) {
        uint256 _cardId = _cardIdList[j];
        uint256 cardsLeft = toshimonMinter.maxSupply(_cardId).sub(
          toshimonMinter.totalSupply(_cardId)
        );

        total = total.add(cardsLeft);
      }
    }

    return total;
  }

  // Returns probability to get each card (Value must be divided by 1e5)
  function getTierProbabilities(uint256 _packId, uint256 _tier)
    public
    view
    returns (uint256[] memory)
  {
    uint256 packsLeft = getPacksLeft(_packId);
    uint256[] memory _cardIdList = packs[_packId].cardIdList[_tier];
    uint256 _totalCardsLeft = packsLeft.mul(packs[_packId].cardsPerTier[_tier]);

    uint256[] memory proba = new uint256[](_cardIdList.length);
    for (uint256 i = 0; i < _cardIdList.length; ++i) {
      uint256 _cardId = _cardIdList[i];
      uint256 cardsLeft = toshimonMinter.maxSupply(_cardId).sub(
        toshimonMinter.totalSupply(_cardId)
      );

      proba[i] = cardsLeft.mul(1e5).div(_totalCardsLeft);
    }

    return proba;
  }

  function getTotalRedeemed() public view returns (uint256) {
    uint256 totalRedeemed = 0;

    for (uint256 i = 0; i < packIdList.length; ++i) {
      totalRedeemed = totalRedeemed.add(packs[packIdList[i]].redeemed);
    }

    return totalRedeemed;
  }

  function getTotalPurchased() public view returns (uint256) {
    uint256 totalPurchased = 0;

    for (uint256 i = 0; i < packIdList.length; ++i) {
      totalPurchased = totalPurchased.add(packs[packIdList[i]].purchased);
    }

    return totalPurchased;
  }

  function getCardsInPack(uint256 _packId) public view returns (uint256) {
    uint256 _tierCount = packs[_packId].cardsPerTier.length;

    uint256 _cardCount = 0;
    for (uint256 i = 0; i < _tierCount; ++i) {
      _cardCount = _cardCount.add(packs[_packId].cardIdList[i].length);
    }

    return _cardCount;
  }

  function getCardsLeft(uint256 _packId)
    external
    view
    returns (uint256[] memory)
  {
    uint256 _tierCount = packs[_packId].cardsPerTier.length;
    uint256 _cardCount = getCardsInPack(_packId);
    uint256[] memory result = new uint256[](_cardCount);

    for (uint256 i = 0; i < _tierCount; ++i) {
      uint256[] memory _cardIdList = packs[_packId].cardIdList[i];

      for (uint256 j = 0; j < _cardIdList.length; ++j) {
        uint256 _cardId = _cardIdList[j];
        uint256 cardsLeft = toshimonMinter.maxSupply(_cardId).sub(
          toshimonMinter.totalSupply(_cardId)
        );

        result[i] = cardsLeft;
      }
    }

    return result;
  }

  function addPack(
    uint256 _packId,
    uint256[] memory _cardsPerTier,
    uint256[][] memory _cardIdList,
    uint256 _priceInToshiCoin
  ) public onlyOwner {
    require(packs[_packId].priceInToshiCoin > 0, 'Pack already exists');
    require(_cardsPerTier.length > 0, 'TierProbabilities cannot be empty');
    require(_cardIdList.length > 0, 'CardIdList cannot be empty');
    require(_priceInToshiCoin > 0, 'Price cannot be 0');

    if (_isInArray(_packId, packIdList) == false) {
      packIdList.push(_packId);
    }

    packs[_packId] = Pack(_packId, _cardsPerTier, _priceInToshiCoin, 0, 0);

    updateCardsData(_cardIdList, _packId);
  }

  // We need to call this function, if we ever mint manually some cards included in a pack
  function updateCardsData(uint256[][] memory _cardIdList, uint256 _packId)
    public
    onlyOwner
  {
    for (uint256 i = 0; i < _cardIdList.length; ++i) {
      packs[_packId].cardIdList[i] = new uint256[](_cardIdList[i].length);

      for (uint256 j = 0; j < _cardIdList[i].length; ++j) {
        uint256 _cardId = _cardIdList[i][j];

        packs[_packId].cardIdList[i][j] = _cardId;
        cards[_cardId] = Card(
          toshimonMinter.maxSupply(_cardId),
          toshimonMinter.totalSupply(_cardId)
        );
      }
    }
  }

  function removePack(uint256 _packId) public onlyOwner {
    uint256 _tiers = packs[_packId].cardsPerTier.length;

    delete packs[_packId].cardsPerTier;

    for (uint256 i = 0; i < _tiers; ++i) {
      delete packs[_packId].cardIdList[i];
    }

    packs[_packId].priceInToshiCoin = 0;
  }

  // Purchase one or more card packs for the price in ToshiCoin
  function purchasePack(uint256 _packId, uint256 amount) public {
    Pack storage pack = packs[_packId];

    require(pack.priceInToshiCoin > 0, 'Pack does not exist');
    require(
      toshiCoin.balanceOf(msg.sender) >= pack.priceInToshiCoin.mul(amount),
      'Not enough toshiCoin for pack'
    );

    toshiCoin.burn(msg.sender, pack.priceInToshiCoin);
    pack.purchased = pack.purchased.add(1);
    toshimonMinter.mint(msg.sender, pack.minterId, amount, '');
  }

  // Redeem a random card pack (Not callable by contract, to prevent exploits on RNG)
  function redeemPack(uint256 _packId, uint256 _packsToRedeem) public onlyEOA {
    Pack storage pack = packs[_packId];

    require(
      toshimonMinter.balanceOf(msg.sender, pack.minterId) >= _packsToRedeem,
      'Not enough pack tokens'
    );

    toshimonMinter.burn(msg.sender, pack.minterId, _packsToRedeem);
    pack.redeemed = pack.redeemed.add(1);

    uint256 baseRng = _rng(getTotalRedeemed());

    for (uint256 _pack = 0; _pack < _packsToRedeem; ++_pack) {
      for (uint256 _tier = 0; _tier < pack.cardsPerTier.length; ++_tier) {
        for (uint256 _card = 0; _card < pack.cardsPerTier[_tier]; ++_card) {
          PackCardData[] memory data = _getPackCardData(_packId, _tier);

          uint256 packsLeft = _getPacksLeft(data);

          require(packsLeft > 0, 'Pack sold out');

          uint256 rng = _simpleRng(baseRng) % packsLeft;

          uint256 cardIdWon = 0;
          uint256 cumul = 0;
          for (uint256 j = 0; j < data.length; ++j) {
            uint256 cardId = data[j].cardId;

            cumul = cumul.add(data[j].cardsLeft);
            if (rng < cumul) {
              cardIdWon = cardId;
              cards[cardId].supply = cards[cardId].supply.add(1);
              break;
            }
          }

          require(cardIdWon != 0, 'Error during card redeeming RNG');

          toshimonMinter.mint(msg.sender, cardIdWon, 1, '');
          emit Redeemed(msg.sender, _packId, cardIdWon);
        }
      }
    }
  }

  // Utility function to check if a value is inside an array
  function _isInArray(uint256 _value, uint256[] memory _array)
    internal
    pure
    returns (bool)
  {
    uint256 length = _array.length;
    for (uint256 i = 0; i < length; ++i) {
      if (_array[i] == _value) {
        return true;
      }
    }

    return false;
  }

  // This is a pseudo random function, but considering the fact that redeem function is not callable by contract,
  // and the fact that ToshiCoin is not transferable, this should be enough to protect us from an attack
  // I would only expect a miner to be able to exploit this, and the attack cost would not be worth it in our case
  function _rng(uint256 _seed) internal view returns (uint256) {
    return
      uint256(
        keccak256(
          abi.encodePacked(
            (block.timestamp)
              .add(_seed)
              .add(block.difficulty)
              .add(
              (uint256(keccak256(abi.encodePacked(block.coinbase)))) /
                block.timestamp
            )
              .add(block.gaslimit)
              .add(
              (uint256(keccak256(abi.encodePacked(msg.sender)))) /
                block.timestamp
            )
              .add(block.number)
          )
        )
      );
  }

  // This is a pseudo random function, but considering the fact that redeem function is not callable by contract,
  // and the fact that ToshiCoin is not transferable, this should be enough to protect us from an attack
  // I would only expect a miner to be able to exploit this, and the attack cost would not be worth it in our case
  function _simpleRng(uint256 _seed) internal view returns (uint256) {
    return
      uint256(
        keccak256(
          abi.encodePacked(
            _seed.add(block.timestamp).add(
              (uint256(keccak256(abi.encodePacked(msg.sender)))) /
                block.timestamp
            )
          )
        )
      );
  }
}
