// BlockchainMuseum NFT token

pragma solidity ^0.8.0;




contract BlockchainMuseumNFT is ERC1155, AccessControl {
    using SafeMath for uint256;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    uint256 public constant PERCENTS_DIVIDER = 1000;
    uint256 public constant FEE_MAX_PERCENT = 300;

    string public name;
    bool public isPublic;
    uint256 public currentID;
    address public factory;
    address public owner;

    event ItemAdded(uint256 id, string uri, uint256 supply, address creator);

    mapping(uint256 => address) private _creators;
    mapping(uint256 => uint256) private _creatorFee;
    mapping(uint256 => uint256) public tokenSupply;
    mapping(uint256 => string) customUri;

    constructor() ERC1155("") {
        factory = msg.sender;
    }

    /**
		Initialize from Swap contract
	 */
    function initialize(
        string memory _name,
        string memory _uri,
        address creator,
        bool bPublic
    ) external {
        require(msg.sender == factory, "Only for factory");
        _setURI(_uri);
        name = _name;
        owner = creator;
        isPublic = bPublic;

        _setupRole(DEFAULT_ADMIN_ROLE, owner);
        _setupRole(MINTER_ROLE, owner);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC1155, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    /**
		Change Collection URI
	 */
    function setURI(string memory newuri) public onlyOwner {
        _setURI(newuri);
    }

    /**
		Change Collection Name
	 */
    function setName(string memory newname) public onlyOwner {
        name = newname;
    }

    /**
		Make collection as Public
	 */
    function setPublic(bool bPublic) public onlyOwner {
        isPublic = bPublic;
    }

    function uri(uint256 _id) public view override returns (string memory) {
        require(_exists(_id), "ERC1155Tradable#uri: NONEXISTENT_TOKEN");
        // We have to convert string to bytes to check for existence
        bytes memory customUriBytes = bytes(customUri[_id]);
        if (customUriBytes.length > 0) {
            return customUri[_id];
        } else {
            return super.uri(_id);
        }
    }

    /**
     * @dev Returns the total quantity for a token ID
     * @param _id uint256 ID of the token to query
     * @return amount of token in existence
     */
    function totalSupply(uint256 _id) public view returns (uint256) {
        return tokenSupply[_id];
    }

    /**
     * @dev Will update the base URI for the token
     * @param _tokenId The token to update. _msgSender() must be its creator.
     * @param _newURI New URI for the token.
     */
    function setCustomURI(uint256 _tokenId, string memory _newURI)
        public
        creatorOnly(_tokenId)
    {
        customUri[_tokenId] = _newURI;
        emit URI(_newURI, _tokenId);
    }

    /**
		Create Card - Only Minters
	 */
    function addItem(
        uint256 supply,
        uint256 _fee,
        string memory _uri
    ) public returns (uint256) {
        require(
            hasRole(MINTER_ROLE, msg.sender) || isPublic,
            "Only minter can add item"
        );
        require(supply > 0, "supply can not be 0");
        require(_fee < FEE_MAX_PERCENT, "Too big creator fee");

        currentID = currentID.add(1);
        tokenSupply[currentID] = supply;

        _creators[currentID] = msg.sender;
        _creatorFee[currentID] = _fee;
        if (bytes(_uri).length > 0) {
            customUri[currentID] = _uri;
            emit URI(_uri, currentID);
        }

        if (supply > 0) {
            _mint(msg.sender, currentID, supply, "Mint");
        }

        emit ItemAdded(currentID, _uri, supply, msg.sender);
        return currentID;
    }

    function creatorOf(uint256 id) public view returns (address) {
        return _creators[id];
    }

    function creatorFee(uint256 id) public view returns (uint256) {
        return _creatorFee[id];
    }

    modifier onlyOwner() {
        require(owner == _msgSender(), "caller is not the owner");
        _;
    }

    function _exists(uint256 _id) internal view returns (bool) {
        return _creators[_id] != address(0);
    }

    /**
     * @dev Require _msgSender() to be the creator of the token id
     */
    modifier creatorOnly(uint256 _id) {
        require(
            _creators[_id] == _msgSender(),
            "ERC1155Tradable#creatorOnly: ONLY_CREATOR_ALLOWED"
        );
        _;
    }
}

// File: contracts\BlockchainMuseumBid.sol

// BlockchainMuseum - NFT Swap contract
 
pragma solidity ^0.8.0;







interface IBlockchainMuseumNFT {
	function initialize(string memory _name, string memory _uri, address creator, bool bPublic) external;
	function safeTransferFrom(address from,
			address to,
			uint256 id,
			uint256 amount,
			bytes calldata data) external;
	function balanceOf(address account, uint256 id) external view returns (uint256);
	function creatorOf(uint256 id) external view returns (address);
	function creatorFee(uint256 id) external view returns (uint256);
	function totalSupply(uint256 _id) external view returns (uint256);
}

contract BlockchainMuseumNFTBid is Ownable, ERC1155Holder {
    using SafeMath for uint256;
	using EnumerableSet for EnumerableSet.AddressSet;

	uint256 constant public PERCENTS_DIVIDER = 1000;
	uint256 constant public FEE_MAX_PERCENT = 300;
	
	IERC20 public blockchainMuseumToken;
    uint256 public swapFee;  // swap fee as percent - percent divider = 1000
	address public feeAddress; 

    /* Bid Item Structure */
	struct Order {
		uint256 order_id;
		address collection;
		uint256 token_id;
		address orderer;
		uint256 amount;
		uint256 price;
		uint256 creatorFee;		
		bool bValid;
	}

	// order_id => Order mapping
    mapping(uint256 => Order) public orders;
	uint256 public currentOrderId;


	/** Events */
    event OrderAdded(uint256 id, address collection, uint256 token_id, address orderer, uint256 amount, uint256 price, uint256 creatorFee);
	event OrderCanceled(uint256 id);	

    event Sold(address seller, Order order, uint256 id, uint256 amount);

	constructor (address _tokenAddress, uint256 _fee, address _feeAddress) {	
		blockchainMuseumToken = IERC20(_tokenAddress);	
		feeAddress = _feeAddress;
		swapFee = _fee;
	}
	
    function setblockchainMuseumToken(address _address) external onlyOwner {
        require(_address != address(0x0), "invalid address");
		blockchainMuseumToken = IERC20(_address);
    }   

	function setFeeAddress(address _address) external onlyOwner {
		require(_address != address(0x0), "invalid address");
        feeAddress = _address;
    }

	function setSwapFeePercent(uint256 _percent) external onlyOwner {
		require(_percent < FEE_MAX_PERCENT, "too big swap fee");
		swapFee = _percent;
	}

    function bidding(address _collection, uint256 _token_id, uint256 _amount, uint256 _price) public {
		require(_price > 0, "invalid price");
		require(_amount > 0, "invalid amount : amount have to be bigger than zero");		

		IBlockchainMuseumNFT nft = IBlockchainMuseumNFT(_collection);
		uint256 totalSupply = nft.totalSupply(_token_id);
		require(totalSupply >= _amount, "invalid amount : amount have to be smaller than totalSupply");	

		currentOrderId = currentOrderId.add(1);
		orders[currentOrderId].order_id = currentOrderId;
		orders[currentOrderId].collection = _collection;
		orders[currentOrderId].token_id = _token_id;
		orders[currentOrderId].orderer = msg.sender;
		orders[currentOrderId].amount = _amount;
		orders[currentOrderId].price = _price;
		orders[currentOrderId].creatorFee = nft.creatorFee(_token_id);
		orders[currentOrderId].bValid = true;

        emit OrderAdded(currentOrderId, 
			_collection,
			_token_id, 
			msg.sender,
			_amount, 
			_price, 
			orders[currentOrderId].creatorFee
		);
    }

	function calcelBid(uint256 _id) external {
		require(orders[_id].bValid, "invalid Bid id");
		require(orders[_id].orderer == msg.sender, "only bidder can cancel");
		orders[_id].amount = 0;
		orders[_id].bValid = false;
		emit OrderCanceled(_id);
	}

    function sell(uint256 _id, uint256 _amount) external {
        require(orders[_id].bValid, "invalid Bid id");
		require(orders[_id].amount >= _amount, "insufficient selling amount");

		Order memory order = orders[_id];
		address orderer = order.orderer;
		uint256 blockchainMuseumAmount = order.price.mul(_amount);
        uint256 orderer_balance = blockchainMuseumToken.balanceOf(orderer);
		require(orderer_balance >= blockchainMuseumAmount, "insufficient token balance : orderer don't have enough tokens");

        IBlockchainMuseumNFT nft = IBlockchainMuseumNFT(orders[_id].collection);
		uint256 token_id = order.token_id;

		uint256 nft_own_balance = nft.balanceOf(msg.sender, token_id);
        require(nft_own_balance >= _amount, "insufficient amount : don't have enough nft tokens to sell");
		
		
		address creator = nft.creatorOf(token_id);

		// transfer BlockchainMuseum token to admin
		if(swapFee > 0) {
			require(blockchainMuseumToken.transferFrom(orderer, feeAddress, blockchainMuseumAmount.mul(swapFee).div(PERCENTS_DIVIDER)), "failed to transfer admin fee");
		}
		// transfer BlockchainMuseum token to creator
		if(order.creatorFee > 0) {
			require(blockchainMuseumToken.transferFrom(orderer, creator, blockchainMuseumAmount.mul(order.creatorFee).div(PERCENTS_DIVIDER)), "failed to transfer creator fee");
		}
		// transfer BlockchainMuseum token to owner
		uint256 ownerPercent = PERCENTS_DIVIDER.sub(swapFee).sub(order.creatorFee);
		require(blockchainMuseumToken.transferFrom(orderer, msg.sender, blockchainMuseumAmount.mul(ownerPercent).div(PERCENTS_DIVIDER)), "failed to transfer to owner");

		// transfer NFT token to bidder
		nft.safeTransferFrom(msg.sender, orderer, token_id, _amount, "sell to bidder");

		orders[_id].amount = orders[_id].amount.sub(_amount);
		if (orders[_id].amount == 0) {
			orders[_id].bValid = false;
		}
        emit Sold(msg.sender, orders[_id], _id, _amount);
    }
}
